import React, { useEffect, useState, useRef } from "react";
import { MapContainer, TileLayer, Marker, Popup, Polyline } from "react-leaflet";
import "leaflet/dist/leaflet.css";
import L from "leaflet";
import { useNavigate, useLocation } from "react-router-dom";

export default function RouteDetails() {
  const location = useLocation();
  const navigate = useNavigate();
  const routeSpots = location.state?.itinerary || [];
  const itineraryIdFromState = location.state?.id || null;

  const [segments, setSegments] = useState([]);
  const [loading, setLoading] = useState(true);
  const [poiDetails, setPoiDetails] = useState([]);
  const [saving, setSaving] = useState(false);
  const [saveMessage, setSaveMessage] = useState("");
  const [status, setStatus] = useState("pending");
  const [userLocation, setUserLocation] = useState(null);
  const [itineraryId, setItineraryId] = useState(itineraryIdFromState);

  // üÜï For navigation simulation
  const [tourActive, setTourActive] = useState(false);
  const [movingMarker, setMovingMarker] = useState(null);
  const [currentStopIndex, setCurrentStopIndex] = useState(0);
  const moveIntervalRef = useRef(null);

  const mapRef = useRef(null);

  const ORS_API_KEY = "YOUR_ORS_API_KEY";

  const numberedIcon = (number) =>
    L.divIcon({
      html: `<div style="background:#1976d2;color:#fff;width:28px;height:28px;border-radius:50%;text-align:center;line-height:28px;font-weight:600;border:2px solid #fff;box-shadow:0 2px 6px rgba(0,0,0,0.3);">${number}</div>`,
      className: "",
      iconSize: [28, 28],
    });

  const userIcon = L.divIcon({
    html: `<div style="background:#4caf50;color:#fff;width:30px;height:30px;border-radius:50%;text-align:center;line-height:30px;font-weight:700;border:2px solid #fff;box-shadow:0 2px 8px rgba(0,0,0,0.3);">üßç</div>`,
    className: "",
    iconSize: [30, 30],
  });

  // --- Get user's location ---
  useEffect(() => {
    if (!navigator.geolocation) return;
    navigator.geolocation.getCurrentPosition(
      (pos) => setUserLocation({ lat: pos.coords.latitude, lng: pos.coords.longitude }),
      (err) => console.warn("Location error:", err),
      { enableHighAccuracy: true }
    );
  }, []);

  // --- Fetch POI details ---
  const fetchPoiDetails = async () => {
    try {
      const res = await fetch("http://localhost:5000/api/pois");
      const data = await res.json();
      const matched = routeSpots.map((spot) =>
        data.find((poi) => poi.name === spot.name || poi.id === spot.id)
      );
      setPoiDetails(matched);
    } catch (err) {
      console.error("Error fetching POI details:", err);
    }
  };

  // --- Snap to road ---
  const snapToRoad = async (poi) => {
    try {
      const res = await fetch("https://api.openrouteservice.org/v2/snap/driving-car", {
        method: "POST",
        headers: { Authorization: ORS_API_KEY, "Content-Type": "application/json" },
        body: JSON.stringify({ coordinates: [[poi.lng, poi.lat]] }),
      });
      const data = await res.json();
      if (data.snappedLocations?.length) {
        const [lng, lat] = data.snappedLocations[0].location;
        return { lat, lng };
      }
      return poi;
    } catch {
      return poi;
    }
  };

  // --- Fetch route segment ---
  const fetchRouteSegment = async (from, to) => {
    if (!from || !to) return null;
    const snappedFrom = await snapToRoad(from);
    const snappedTo = await snapToRoad(to);
    try {
      const res = await fetch("https://api.openrouteservice.org/v2/directions/driving-car/geojson", {
        method: "POST",
        headers: { Authorization: ORS_API_KEY, "Content-Type": "application/json" },
        body: JSON.stringify({
          coordinates: [
            [snappedFrom.lng, snappedFrom.lat],
            [snappedTo.lng, snappedTo.lat],
          ],
          preference: "fastest",
          instructions: false,
        }),
      });
      const data = await res.json();
      if (data.features?.length) {
        const coords = data.features[0].geometry.coordinates.map(([lng, lat]) => [lat, lng]);
        const summary = data.features[0].properties.summary;
        return { coords, summary };
      }
    } catch {
      return { coords: [[from.lat, from.lng], [to.lat, to.lng]], summary: { distance: 0, duration: 0 } };
    }
  };

  // --- Load all segments ---
  useEffect(() => {
    const loadAllSegments = async () => {
      setLoading(true);
      await fetchPoiDetails();
      const segs = [];
      for (let i = 0; i < routeSpots.length - 1; i++) {
        const seg = await fetchRouteSegment(routeSpots[i], routeSpots[i + 1]);
        if (seg) segs.push(seg);
      }
      setSegments(segs);
      setLoading(false);
    };
    if (routeSpots.length > 0) loadAllSegments();
  }, [routeSpots]);

  // --- Start Tour Simulation ---
  const startTour = async () => {
    if (!userLocation || segments.length === 0) return;
    setTourActive(true);
    setCurrentStopIndex(0);

    const marker = L.marker([userLocation.lat, userLocation.lng], {
      icon: L.divIcon({
        html: "üöó",
        className: "",
        iconSize: [32, 32],
      }),
    }).addTo(mapRef.current);

    setMovingMarker(marker);

    // function to move marker step by step
    const moveAlongRoute = (coords, onFinish) => {
      let idx = 0;
      moveIntervalRef.current = setInterval(() => {
        if (idx >= coords.length) {
          clearInterval(moveIntervalRef.current);
          onFinish();
          return;
        }
        const [lat, lng] = coords[idx];
        marker.setLatLng([lat, lng]);
        if (mapRef.current) mapRef.current.setView([lat, lng]);
        idx++;
      }, 1000); // move every second
    };

    const moveToNextStop = (index) => {
      if (index >= segments.length) {
        setTourActive(false);
        alert("üéâ Tour finished!");
        marker.remove();
        return;
      }
      moveAlongRoute(segments[index].coords, () => moveToNextStop(index + 1));
    };

    moveToNextStop(0);
  };

  const cancelTour = () => {
    if (moveIntervalRef.current) clearInterval(moveIntervalRef.current);
    if (movingMarker) movingMarker.remove();
    setTourActive(false);
    alert("üö´ Tour cancelled.");
  };

  if (!routeSpots.length || !userLocation) return <p>Loading route...</p>;

  return (
    <div style={{ display: "grid", gridTemplateColumns: "1.3fr 0.7fr", gap: "24px", padding: "24px", minHeight: "100vh", fontFamily: "'Poppins', sans-serif", background: "linear-gradient(to bottom right, #e3f2fd, #e8f5e9)" }}>
      {/* Map */}
      <div style={{ height: "650px", borderRadius: "18px", overflow: "hidden", boxShadow: "0 10px 25px rgba(0,0,0,0.15)", border: "4px solid #fff" }}>
        <MapContainer ref={mapRef} center={[userLocation.lat, userLocation.lng]} zoom={13} style={{ height: "100%", width: "100%" }}>
          <TileLayer url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png" />
          <Marker position={[userLocation.lat, userLocation.lng]} icon={userIcon}>
            <Popup>You are here</Popup>
          </Marker>
          {segments.map((seg, i) => <Polyline key={i} positions={seg.coords} color="#1976d2" weight={5} opacity={0.9} />)}
          {routeSpots.map((poi, i) => (
            <Marker key={i} position={[poi.lat, poi.lng]} icon={numberedIcon(i + 1)}>
              <Popup><strong>{poi.name}</strong></Popup>
            </Marker>
          ))}
        </MapContainer>
      </div>

      {/* Sidebar */}
      <div style={{ background: "#fff", borderRadius: "18px", padding: "24px", boxShadow: "0 8px 24px rgba(0,0,0,0.1)", overflowY: "auto", maxHeight: "650px" }}>
        <h2 style={{ color: "#1565c0", fontWeight: "700" }}>Route Overview</h2>
        <p>Status: {status.toUpperCase()}</p>

        {!tourActive ? (
          <button
            onClick={startTour}
            style={{ width: "100%", padding: "12px", background: "#4caf50", color: "#fff", border: "none", borderRadius: "8px", fontWeight: "600" }}
          >
            üöó Start Tour
          </button>
        ) : (
          <button
            onClick={cancelTour}
            style={{ width: "100%", padding: "12px", background: "#f44336", color: "#fff", border: "none", borderRadius: "8px", fontWeight: "600" }}
          >
            ‚ùå Cancel Tour
          </button>
        )}

        {saveMessage && <p style={{ marginTop: "10px", color: "#1565c0" }}>{saveMessage}</p>}
      </div>
    </div>
  );
}
