import React, { useEffect, useState } from "react";

export default function AdminUsers() {
  const [users, setUsers] = useState([]);
  const [editingUser, setEditingUser] = useState(null);
  const [role, setRole] = useState("");

  useEffect(() => {
    fetch("http://localhost:5000/api/users") // you may need to add GET /api/users in your server
      .then((res) => res.json())
      .then((data) => setUsers(data))
      .catch((err) => console.error("Failed to fetch users:", err));
  }, []);

  const saveRole = () => {
    fetch(`http://localhost:5000/api/users/${editingUser.id}/role`, {
      method: "PUT",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ role }),
    })
      .then((res) => res.json())
      .then(() => {
        setUsers(users.map(u => u.id === editingUser.id ? { ...u, role } : u));
        setEditingUser(null);
      })
      .catch(err => console.error("Failed to update role:", err));
  };

  const deleteUser = (id) => {
    if (!window.confirm("Are you sure you want to delete this user?")) return;
    fetch(`http://localhost:5000/api/users/${id}`, { method: "DELETE" })
      .then(() => setUsers(users.filter(u => u.id !== id)))
      .catch(err => console.error("Failed to delete user:", err));
  };

  return (
    <div>
      <h2>Users</h2>
      <table border={1} cellPadding={8}>
        <thead>
          <tr>
            <th>ID</th>
            <th>Username</th>
            <th>Email</th>
            <th>Role</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {users.map(u => (
            <tr key={u.id}>
              <td>{u.id}</td>
              <td>{u.username}</td>
              <td>{u.email}</td>
              <td>{u.role}</td>
              <td>
                <button onClick={() => { setEditingUser(u); setRole(u.role); }}>Edit Role</button>
                <button onClick={() => deleteUser(u.id)} style={{ marginLeft: 8, color: "red" }}>Delete</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      {editingUser && (
        <div style={{ marginTop: 20, padding: 10, border: "1px solid #ccc" }}>
          <h3>Edit Role for {editingUser.username}</h3>
          <select value={role} onChange={(e) => setRole(e.target.value)}>
            <option value="user">User</option>
            <option value="admin">Admin</option>
          </select>
          <button onClick={saveRole} style={{ marginLeft: 8 }}>Save</button>
          <button onClick={() => setEditingUser(null)} style={{ marginLeft: 8 }}>Cancel</button>
        </div>
      )}
    </div>
  );
}
