import React, { useEffect, useState } from "react";
import { MapContainer, TileLayer, Marker, Popup, Polyline } from "react-leaflet";
import "leaflet/dist/leaflet.css";
import { useNavigate, useLocation } from "react-router-dom";

export default function RouteDetails() {
  const location = useLocation();
  const navigate = useNavigate();
  const routeSpots = location.state?.itinerary || [];

  const [currentIndex, setCurrentIndex] = useState(0);
  const [segmentData, setSegmentData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [weather, setWeather] = useState(null);

  const ORS_API_KEY =
    AIzaSyA6D72kCqt2yln8Yhfg0Y0iE25F0ndZtO4;
  const WEATHER_API_KEY = "13d7213054a676ccdd935415b00fac6f";

  const currentPOI = routeSpots[currentIndex];
  const nextPOI = routeSpots[currentIndex + 1];

  const snapToRoad = async (poi) => {
    try {
      const res = await fetch("https://api.openrouteservice.org/v2/snap/driving-car", {
        method: "POST",
        headers: {
          Authorization: ORS_API_KEY,
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ coordinates: [[poi.lng, poi.lat]] }),
      });
      const data = await res.json();
      if (data.snappedLocations && data.snappedLocations.length) {
        const snapped = data.snappedLocations[0].location;
        return { lat: snapped[1], lng: snapped[0] };
      }
      return poi;
    } catch {
      return poi;
    }
  };

  const fetchRouteSegment = async (from, to) => {
    if (!from || !to) return null;

    const snappedFrom = await snapToRoad(from);
    const snappedTo = await snapToRoad(to);

    try {
      const res = await fetch("https://api.openrouteservice.org/v2/directions/driving-car/geojson", {
        method: "POST",
        headers: {
          Authorization: ORS_API_KEY,
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          coordinates: [
            [snappedFrom.lng, snappedFrom.lat],
            [snappedTo.lng, snappedTo.lat],
          ],
          preference: "fastest",
          instructions: false,
        }),
      });

      const data = await res.json();
      if (data.features && data.features.length) {
        const coords = data.features[0].geometry.coordinates.map(([lng, lat]) => [lat, lng]);
        const summary = data.features[0].properties.summary;
        return { coords, summary, profile: "driving" };
      }
    } catch {}

    try {
      const res = await fetch("https://api.openrouteservice.org/v2/directions/foot-walking/geojson", {
        method: "POST",
        headers: {
          Authorization: ORS_API_KEY,
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          coordinates: [
            [snappedFrom.lng, snappedFrom.lat],
            [snappedTo.lng, snappedTo.lat],
          ],
          instructions: false,
        }),
      });

      const data = await res.json();
      if (data.features && data.features.length) {
        const coords = data.features[0].geometry.coordinates.map(([lng, lat]) => [lat, lng]);
        const summary = data.features[0].properties.summary;
        return { coords, summary, profile: "non-drivable" };
      }
    } catch {}

    return {
      coords: [
        [from.lat, from.lng],
        [to.lat, to.lng],
      ],
      summary: { distance: 0, duration: 0 },
      profile: "none",
    };
  };

  const fetchWeather = async (poi) => {
    if (!poi) return;
    try {
      const res = await fetch(
        `https://api.openweathermap.org/data/2.5/weather?lat=${poi.lat}&lon=${poi.lng}&units=metric&appid=${WEATHER_API_KEY}`
      );
      const data = await res.json();
      setWeather(data);
    } catch {
      setWeather(null);
    }
  };

  useEffect(() => {
    const loadSegment = async () => {
      setLoading(true);
      if (currentPOI && nextPOI) {
        const segment = await fetchRouteSegment(currentPOI, nextPOI);
        setSegmentData(segment);
      } else {
        setSegmentData(null);
      }
      await fetchWeather(currentPOI);
      setLoading(false);
    };
    loadSegment();
  }, [currentIndex, routeSpots]);

  const nextPOIHandler = () => {
    if (currentIndex < routeSpots.length - 1) setCurrentIndex(currentIndex + 1);
    else alert("You have reached the end of the route!");
  };

  const saveItinerary = async (status) => {
    try {
      const storedUser = JSON.parse(localStorage.getItem("user"));
      const userId = storedUser?.id;

      if (!userId) {
        alert("User not logged in!");
        return;
      }

      const res = await fetch("http://localhost:5000/api/itineraries", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          user_id: userId,
          name: `${status} Itinerary`,
          route_data: routeSpots,
        }),
      });

      if (!res.ok) throw new Error("Failed to save itinerary");
      const data = await res.json();
      console.log("Saved:", data);
    } catch (err) {
      console.error("Error saving itinerary:", err);
    }
  };

  const finishRoute = async () => {
    if (window.confirm("Finish route?")) {
      await saveItinerary("Completed");
      alert("Route completed and saved!");
      navigate("/home");
    }
  };

  const cancelRoute = async () => {
    if (window.confirm("Cancel route? You will lose progress.")) {
      await saveItinerary("Cancelled");
      alert("Route cancelled but saved.");
      navigate("/home");
    }
  };

  const formatDistance = (meters) => `${(meters / 1000).toFixed(2)} km`;
  const formatDuration = (seconds) => `${Math.floor(seconds / 60)} min`;

  if (!currentPOI) return <p>No route available.</p>;

  return (
    <div
      style={{
        display: "grid",
        gridTemplateColumns: "1.3fr 0.7fr",
        gap: "24px",
        padding: "24px",
        minHeight: "100vh",
        background: "linear-gradient(to bottom right, #e3f2fd, #e8f5e9)",
        fontFamily: "'Poppins', sans-serif",
      }}
    >
      <div
        style={{
          height: "650px",
          borderRadius: "18px",
          overflow: "hidden",
          boxShadow: "0 10px 25px rgba(0,0,0,0.15)",
          border: "4px solid #fff",
        }}
      >
        {loading ? (
          <p style={{ textAlign: "center", marginTop: "40%", fontWeight: "500", color: "#555" }}>
            Loading route...
          </p>
        ) : (
          <MapContainer
            center={[currentPOI.lat, currentPOI.lng]}
            zoom={14}
            style={{ height: "100%", width: "100%" }}
          >
            <TileLayer
              url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
              attribution="© OpenStreetMap contributors"
            />
            {segmentData && (
              <Polyline
                positions={segmentData.coords}
                color={segmentData.profile === "driving" ? "#1976d2" : "#ff9800"}
                dashArray={segmentData.profile === "driving" ? "" : "8,6"}
                weight={6}
                opacity={0.9}
              />
            )}
            {[currentPOI, nextPOI].filter(Boolean).map((poi, i) => (
              <Marker key={i} position={[poi.lat, poi.lng]}>
                <Popup>
                  <strong>{poi.name}</strong>
                  <p style={{ fontSize: "0.9rem" }}>{poi.description}</p>
                  {poi.image && (
                    <img
                      src={poi.image}
                      alt={poi.name}
                      style={{
                        width: "160px",
                        borderRadius: "8px",
                        marginTop: "6px",
                      }}
                    />
                  )}
                </Popup>
              </Marker>
            ))}
          </MapContainer>
        )}
      </div>

      <div
        style={{
          background: "#fff",
          borderRadius: "18px",
          padding: "24px",
          boxShadow: "0 8px 24px rgba(0,0,0,0.1)",
          border: "1px solid #e0e0e0",
        }}
      >
        <h2
          style={{
            marginBottom: "16px",
            color: "#1565c0",
            fontWeight: "700",
            fontSize: "1.5rem",
          }}
        >
          Current Stop
        </h2>
        <p style={{ fontWeight: "600", color: "#333" }}>{currentPOI.name}</p>
        <p style={{ color: "#666", marginBottom: "12px" }}>{currentPOI.description}</p>

        {currentPOI.image && (
          <img
            src={currentPOI.image}
            alt={currentPOI.name}
            style={{
              width: "100%",
              maxWidth: "300px",
              borderRadius: "12px",
              boxShadow: "0 6px 16px rgba(0,0,0,0.15)",
              marginBottom: "14px",
              transition: "transform 0.3s ease",
            }}
            onMouseOver={(e) => (e.currentTarget.style.transform = "scale(1.05)")}
            onMouseOut={(e) => (e.currentTarget.style.transform = "scale(1)")}
          />
        )}

        {segmentData && (
          <div
            style={{
              marginTop: "10px",
              background: "#f1f8e9",
              padding: "10px 14px",
              borderRadius: "8px",
            }}
          >
            <p style={{ margin: 0 }}>
              <strong>Distance:</strong> {formatDistance(segmentData.summary.distance)}
            </p>
            <p style={{ margin: 0 }}>
              <strong>Duration:</strong> {formatDuration(segmentData.summary.duration)}
            </p>
          </div>
        )}

        {weather && (
          <div
            style={{
              marginTop: "18px",
              background: "#e3f2fd",
              padding: "12px",
              borderRadius: "10px",
              textAlign: "center",
            }}
          >
            <h4 style={{ margin: "0 0 4px 0", color: "#1976d2" }}>Weather</h4>
            <p style={{ margin: 0, fontSize: "0.95rem", color: "#444" }}>
              {weather.name}: {weather.weather[0].main}, {weather.main.temp}°C
            </p>
            <p style={{ margin: "4px 0 0 0", fontSize: "0.8rem", color: "#666" }}>
              {new Date().toLocaleTimeString()}
            </p>
          </div>
        )}

        <div style={{ marginTop: "24px", display: "flex", gap: "10px", flexWrap: "wrap" }}>
          <button
            onClick={nextPOIHandler}
            disabled={currentIndex >= routeSpots.length - 1}
            style={{
              flex: 1,
              padding: "10px 20px",
              background: "#2196f3",
              color: "#fff",
              border: "none",
              borderRadius: "8px",
              fontWeight: "500",
              cursor: "pointer",
              transition: "background 0.3s",
            }}
            onMouseOver={(e) => (e.currentTarget.style.background = "#1976d2")}
            onMouseOut={(e) => (e.currentTarget.style.background = "#2196f3")}
          >
            Next Stop
          </button>
          <button
            onClick={finishRoute}
            style={{
              flex: 1,
              padding: "10px 20px",
              background: "#4caf50",
              color: "#fff",
              border: "none",
              borderRadius: "8px",
              fontWeight: "500",
              cursor: "pointer",
              transition: "background 0.3s",
            }}
            onMouseOver={(e) => (e.currentTarget.style.background = "#388e3c")}
            onMouseOut={(e) => (e.currentTarget.style.background = "#4caf50")}
          >
            Finish
          </button>
          <button
            onClick={cancelRoute}
            style={{
              flex: 1,
              padding: "10px 20px",
              background: "#f44336",
              color: "#fff",
              border: "none",
              borderRadius: "8px",
              fontWeight: "500",
              cursor: "pointer",
              transition: "background 0.3s",
            }}
            onMouseOver={(e) => (e.currentTarget.style.background = "#d32f2f")}
            onMouseOut={(e) => (e.currentTarget.style.background = "#f44336")}
          >
            Cancel
          </button>
        </div>
      </div>
    </div>
  );
}
