const [startChoice, setStartChoice] = useState("address");
const [selectedStartPoi, setSelectedStartPoi] = useState("");
const navigate = useNavigate();

useEffect(() => {
const storedUser = JSON.parse(localStorage.getItem("user"));
setUser(storedUser);


fetch("http://localhost:5000/api/pois")
  .then((res) => res.json())
  .then((data) => setPois(data))
  .catch(() => setPois([]));

}, []);

const getRouteInfo = async (start, end, mode) => {
const res = await fetch(
`https://api.openrouteservice.org/v2/directions/${mode}`,
{
method: "POST",
headers: {
Authorization: ORS_API_KEY,
"Content-Type": "application/json",
},
body: JSON.stringify({ coordinates: [start, end] }),
}
);

