import React, { useEffect, useState, useRef } from "react";
import { MapContainer, TileLayer, Marker } from "react-leaflet";
import "leaflet/dist/leaflet.css";
import L from "leaflet";

delete L.Icon.Default.prototype._getIconUrl;
L.Icon.Default.mergeOptions({
  iconRetinaUrl: require("leaflet/dist/images/marker-icon-2x.png"),
  iconUrl: require("leaflet/dist/images/marker-icon.png"),
  shadowUrl: require("leaflet/dist/images/marker-shadow.png"),
});

export default function AdminPOIsMap() {
  const [pois, setPois] = useState([]);
  const [selectedPoi, setSelectedPoi] = useState(null);
  const [addingPoi, setAddingPoi] = useState(false);
  const [imagePreview, setImagePreview] = useState(null);
  const token = localStorage.getItem("token");
  const fileInputRef = useRef(null);

  // Fetch POIs from DB
  useEffect(() => {
    fetch("http://localhost:5000/api/pois", {
      headers: { Authorization: `Bearer ${token}` },
    })
      .then((res) => res.json())
      .then((data) => setPois(Array.isArray(data) ? data : []))
      .catch((err) => console.error(err));
  }, [token]);

  // Update image preview
  useEffect(() => {
    if (!selectedPoi) {
      setImagePreview(null);
      return;
    }
    if (selectedPoi.imageFile) {
      setImagePreview(URL.createObjectURL(selectedPoi.imageFile));
    } else if (selectedPoi.image) {
      setImagePreview(`http://localhost:5000/images/${selectedPoi.image}`);
    } else {
      setImagePreview(null);
    }
  }, [selectedPoi]);

  // Add POI on map click
  const handleMapClick = (e) => {
    if (!addingPoi) return;

    setSelectedPoi({
      lat: e.latlng.lat,
      lng: e.latlng.lng,
      name: "",
      tags: "",
      description: "",
      visitors: "Moderate",
      isNew: true,
    });

    setAddingPoi(false);
  };

  // Update marker position while dragging
  const handleMarkerDrag = (e) => {
    if (!selectedPoi?.isNew) return;
    const { lat, lng } = e.target.getLatLng();
    setSelectedPoi((prev) => ({ ...prev, lat, lng }));
  };

  // Upload image
  const uploadImage = async (file) => {
    if (!file) return null;
    const formData = new FormData();
    formData.append("image", file);

    const res = await fetch("http://localhost:5000/api/upload-image", {
      method: "POST",
      headers: { Authorization: `Bearer ${token}` },
      body: formData,
    });

    if (!res.ok) throw new Error("Image upload failed");
    const data = await res.json();
    return data.filename;
  };

  // Image upload input
  const handleImageUpload = (e) => {
    const file = e.target.files[0];
    if (!file || !selectedPoi) return;
    setSelectedPoi({ ...selectedPoi, imageFile: file });
  };

  // Save POI
  const savePoi = async () => {
    if (!selectedPoi) return;
    try {
      const poiToSave = { ...selectedPoi };

      if (selectedPoi.imageFile) {
        const filename = await uploadImage(selectedPoi.imageFile);
        poiToSave.image = filename;
        delete poiToSave.imageFile;
      }

      if (selectedPoi.isNew) {
        const res = await fetch("http://localhost:5000/api/pois", {
          method: "POST",
          headers: {
            "Content-Type": "application/json",
            Authorization: `Bearer ${token}`,
          },
          body: JSON.stringify(poiToSave),
        });

        if (!res.ok) throw new Error("Failed to create POI");
        const savedPoi = await res.json();
        setPois([...pois, savedPoi]);
      } else {
        const res = await fetch(`http://localhost:5000/api/pois/${selectedPoi.id}`, {
          method: "PUT",
          headers: {
            "Content-Type": "application/json",
            Authorization: `Bearer ${token}`,
          },
          body: JSON.stringify(poiToSave),
        });
        if (!res.ok) throw new Error("Failed to update POI");
        setPois(pois.map((p) => (p.id === selectedPoi.id ? poiToSave : p)));
      }

      setSelectedPoi(null);
      setImagePreview(null);
      if (fileInputRef.current) fileInputRef.current.value = "";
    } catch (err) {
      console.error(err);
      alert("Error saving POI: " + err.message);
    }
  };

  // Cancel
  const cancelPoi = () => {
    setSelectedPoi(null);
    setImagePreview(null);
    if (fileInputRef.current) fileInputRef.current.value = "";
  };

  // Delete POI
  const deletePoi = async (poiId) => {
    if (!window.confirm("Delete this POI?")) return;
    try {
      const res = await fetch(`http://localhost:5000/api/pois/${poiId}`, {
        method: "DELETE",
        headers: { Authorization: `Bearer ${token}` },
      });
      if (!res.ok) throw new Error("Failed to delete POI");
      setPois(pois.filter((p) => p.id !== poiId));
      if (selectedPoi?.id === poiId) setSelectedPoi(null);
    } catch (err) {
      console.error(err);
      alert("Error deleting POI");
    }
  };

  return (
    <div style={{ display: "flex", height: "100vh" }}>
      {/* Sidebar */}
      <div style={{ width: "200px", background: "#333", color: "#fff", padding: 20 }}>
        <h2>Admin Panel</h2>
        <button
          onClick={() => setAddingPoi(true)}
          style={{
            marginTop: 20,
            background: addingPoi ? "#ff9800" : "#4caf50",
            color: "#fff",
            padding: "8px 12px",
            border: "none",
            borderRadius: "6px",
            cursor: "pointer",
          }}
        >
          {addingPoi ? "Click on Map to Drop" : "Add POI"}
        </button>
      </div>

      {/* Map */}
      <div style={{ display: "flex", flex: 1, minWidth: 0 }}>
        <div style={{ flex: 1, minWidth: 0 }}>
          <MapContainer
            center={[14.1619, 121.4626]}
            zoom={14}
            style={{ height: "100%", width: "100%" }}
            whenCreated={(map) => map.on("click", handleMapClick)}
          >
            <TileLayer
              attribution="&copy; OpenStreetMap contributors"
              url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
            />

            {/* Existing POIs */}
            {pois.map((p) => (
              <Marker
                key={p.id}
                position={[p.lat, p.lng]}
                eventHandlers={{ click: () => setSelectedPoi({ ...p, isNew: false }) }}
              />
            ))}

            {/* New draggable marker */}
            {selectedPoi?.isNew && (
              <Marker
                position={[selectedPoi.lat, selectedPoi.lng]}
                draggable={true}
                eventHandlers={{
                  dragend: handleMarkerDrag,
                }}
              />
            )}
          </MapContainer>
        </div>

        {/* POI Editor */}
        {selectedPoi && (
          <div
            style={{
              width: "350px",
              overflowY: "auto",
              padding: 20,
              background: "#fff",
              borderLeft: "1px solid #ccc",
              boxShadow: "-2px 0 5px rgba(0,0,0,0.1)",
            }}
          >
            <h3>{selectedPoi.isNew ? "Add New POI" : "Edit POI"}</h3>
            <input
              type="text"
              placeholder="Name"
              value={selectedPoi.name}
              onChange={(e) => setSelectedPoi({ ...selectedPoi, name: e.target.value })}
              style={{ width: "100%", marginBottom: 8 }}
            />
            <input
              type="text"
              placeholder="Tags"
              value={selectedPoi.tags}
              onChange={(e) => setSelectedPoi({ ...selectedPoi, tags: e.target.value })}
              style={{ width: "100%", marginBottom: 8 }}
            />
            <textarea
              placeholder="Description"
              value={selectedPoi.description}
              onChange={(e) => setSelectedPoi({ ...selectedPoi, description: e.target.value })}
              style={{ width: "100%", marginBottom: 8 }}
            />
            <input
              type="file"
              ref={fileInputRef}
              onChange={handleImageUpload}
              style={{ width: "100%", marginBottom: 8 }}
            />
            {imagePreview && (
              <img
                src={imagePreview}
                alt="Preview"
                style={{ width: "100%", borderRadius: 4, marginBottom: 8 }}
              />
            )}
            <select
              value={selectedPoi.visitors}
              onChange={(e) => setSelectedPoi({ ...selectedPoi, visitors: e.target.value })}
              style={{ width: "100%", marginBottom: 8 }}
            >
              <option>Very High</option>
              <option>High</option>
              <option>Moderate</option>
              <option>Low</option>
            </select>
            <div style={{ display: "flex", justifyContent: "space-between" }}>
              <button onClick={savePoi}>Save</button>
              <button onClick={cancelPoi}>Cancel</button>
              {!selectedPoi.isNew && (
                <button onClick={() => deletePoi(selectedPoi.id)} style={{ color: "red" }}>
                  Delete
                </button>
              )}
            </div>
          </div>
        )}
      </div>
    </div>
  );
}
