import React, { useEffect, useState } from "react";
import { useLocation } from "react-router-dom";
import { MapContainer, TileLayer, Marker, Polyline, Popup } from "react-leaflet";
import { fetchWeather } from "../services/weatherService";

export default function RouteDetails() {
  const location = useLocation();
  const { routeSpots, currentLocation, mode, selectedCategories, startTime } =
    location.state || {};

  const [weather, setWeather] = useState(null);

  useEffect(() => {
    if (currentLocation) {
      fetchWeather(currentLocation.lat, currentLocation.lng).then(setWeather);
    }
  }, [currentLocation]);

  if (!routeSpots || routeSpots.length === 0) {
    return <div>No route details available.</div>;
  }

  return (
    <div>
      <h2>Route Details</h2>
      <p>Mode: {mode}</p>
      <p>Start Time: {new Date(startTime).toLocaleString()}</p>

      {weather && (
        <p>
          Weather at start: {weather.main.temp}Â°C, {weather.weather[0].description}
        </p>
      )}

      <MapContainer
        center={[routeSpots[0].lat, routeSpots[0].lng]}
        zoom={13}
        style={{ height: "500px", width: "100%" }}
      >
        <TileLayer
          attribution="&copy; OpenStreetMap contributors"
          url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
        />

        {currentLocation && (
          <Marker position={[currentLocation.lat, currentLocation.lng]}>
            <Popup>Start Location</Popup>
          </Marker>
        )}

        {routeSpots.map((spot, idx) => (
          <Marker key={idx} position={[spot.lat, spot.lng]}>
            <Popup>{spot.name}</Popup>
          </Marker>
        ))}

        <Polyline
          positions={routeSpots.map((spot) => [spot.lat, spot.lng])}
          color="blue"
        />
      </MapContainer>
    </div>
  );
}
