import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";

const ORS_API_KEY = "YOUR_HEIGIT_KEY"; // replace with your real HeiGIT key

export default function RoutePlanner() {
  const [pois, setPois] = useState([]);
  const [user, setUser] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    // Load user from localStorage
    const storedUser = JSON.parse(localStorage.getItem("user"));
    setUser(storedUser);

    // Fetch POIs from backend
    fetch("http://localhost:5000/api/pois")
      .then(res => res.json())
      .then(data => setPois(data))
      .catch(err => {
        console.error("Error fetching POIs:", err);
        setPois([]);
      });
  }, []);

  // Geocode user address -> coordinates
  const geocodeAddress = async (address) => {
    const res = await fetch(
      `https://api.openrouteservice.org/geocode/search?api_key=${ORS_API_KEY}&text=${encodeURIComponent(address)}`
    );
    if (!res.ok) throw new Error("Geocoding failed");
    const data = await res.json();
    return data.features[0]?.geometry.coordinates; // [lng, lat]
  };

  // Get route distance & duration from ORS
  const getRouteInfo = async (start, end, mode = "driving-car") => {
    const res = await fetch(`https://api.openrouteservice.org/v2/directions/${mode}`, {
      method: "POST",
      headers: {
        "Authorization": ORS_API_KEY,
        "Content-Type": "application/json",
      },
      body: JSON.stringify({
        coordinates: [
          [start[0], start[1]], // [lng, lat]
          [end[0], end[1]],
        ],
      }),
    });

    if (!res.ok) throw new Error("Routing failed");
    const data = await res.json();
    const summary = data.routes[0].summary;
    return {
      distance: summary.distance / 1000, // km
      duration: summary.duration / 60,   // minutes
    };
  };

  const recommendRoute = async () => {
    if (!user || !user.preferences || user.preferences.length === 0 || !user.address) {
      alert("Set your preferences and address first in profile.");
      return;
    }

    try {
      // Convert user address to coordinates
      const userCoords = await geocodeAddress(user.address);
      if (!userCoords) {
        alert("Failed to geocode user address.");
        return;
      }

      const prefs = user.preferences.map(p => p.toLowerCase());

      // Score POIs based on preferences + actual travel distance
      const scoredPOIs = await Promise.all(
        pois.map(async (poi) => {
          let score = 0;

          // Preference match
          prefs.forEach(pref => {
            if (poi.tags?.toLowerCase().includes(pref)) score += 3;
          });

          // Route distance from user address
          try {
            const route = await getRouteInfo(userCoords, [poi.lng, poi.lat]);
            score += 2 / (route.distance + 0.1);
            return { ...poi, score, distance: route.distance, duration: route.duration };
          } catch {
            return { ...poi, score: 0, distance: Infinity, duration: Infinity };
          }
        })
      );

      // Sort POIs by score
      const sorted = scoredPOIs.sort((a, b) => b.score - a.score);

      // Select top 4 diverse POIs
      const selected = [];
      const categoriesUsed = new Set();
      for (let poi of sorted) {
        const cat = poi.category?.toLowerCase() || "other";
        if (!categoriesUsed.has(cat)) {
          selected.push(poi);
          categoriesUsed.add(cat);
        }
        if (selected.length >= 4) break;
      }

      if (selected.length === 0) {
        alert("No POIs match your preferences.");
        return;
      }

      // Pass selected POIs (with distance & duration) to RouteDetails
      navigate("/routedetails", { state: { itinerary: selected, start: userCoords } });

    } catch (err) {
      console.error(err);
      alert("Error planning route: " + err.message);
    }
  };

  return (
    <div style={{ textAlign: "center", marginTop: 40 }}>
      <button
        onClick={recommendRoute}
        style={{
          padding: "12px 24px",
          background: "#2196f3",
          color: "#fff",
          border: "none",
          borderRadius: 8,
          fontSize: 16,
          cursor: "pointer"
        }}
      >
        Plan My Smart Route
      </button>
    </div>
  );
}
