import React, { useState, useEffect, useRef } from "react";

export default function AdminReports() {
  const [activeReport, setActiveReport] = useState("users");
  const [reportData, setReportData] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [filterColumn, setFilterColumn] = useState(""); // dropdown value
  const [sortAsc, setSortAsc] = useState(true); // toggle asc/desc
  const reportRef = useRef();

  useEffect(() => {
    fetchReport(activeReport);
  }, [activeReport]);

  const fetchReport = async (type) => {
    setLoading(true);
    setError(null);
    try {
      const token = localStorage.getItem("token");
      const res = await fetch(`http://localhost:5000/api/reports?type=${type}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      if (!res.ok) throw new Error(`Failed to fetch report: ${res.status}`);
      const data = await res.json();
      setReportData(data);
    } catch (err) {
      console.error(err);
      setError(err.message);
      setReportData([]);
    } finally {
      setLoading(false);
    }
  };

  const handlePrint = () => {
    if (!reportRef.current) return;
    const printContent = reportRef.current.innerHTML;
    const WinPrint = window.open("", "", "width=900,height=650");
    WinPrint.document.write(`<html><head><title>Report</title></head><body>${printContent}</body></html>`);
    WinPrint.document.close();
    WinPrint.focus();
    WinPrint.print();
    WinPrint.close();
  };

  // Conditional sorting function
  const sortData = (array) => {
    if (!filterColumn) return array;
    return [...array].sort((a, b) => {
      if (filterColumn === "id") return sortAsc ? a.id - b.id : b.id - a.id;
      if (filterColumn === "username" || filterColumn === "name")
        return sortAsc
          ? String(a[filterColumn]).localeCompare(String(b[filterColumn]))
          : String(b[filterColumn]).localeCompare(String(a[filterColumn]));
      if (filterColumn === "visitors") {
        const priority = { "Very High": 4, High: 3, Moderate: 2, Low: 1 };
        const aVal = priority[a.visitors] || 0;
        const bVal = priority[b.visitors] || 0;
        return sortAsc ? aVal - bVal : bVal - aVal;
      }
      return 0;
    });
  };

  const renderReport = () => {
    if (loading) return <p>Loading...</p>;
    if (error) return <p style={{ color: "red" }}>{error}</p>;
    if (!reportData || reportData.length === 0) return <p>No report available.</p>;

    const data = sortData(
      activeReport === "users" ? reportData.users || [] : reportData.pois || []
    );

    if (activeReport === "users") {
      return (
        <div>
          <p><strong>Total Users:</strong> {reportData.totalUsers}</p>

          <div style={{ marginBottom: 10 }}>
            <select value={filterColumn} onChange={(e) => setFilterColumn(e.target.value)}>
              <option value="">--Sort by--</option>
              <option value="id">ID</option>
              <option value="username">Username</option>
            </select>
            <button onClick={() => setSortAsc(!sortAsc)} style={{ marginLeft: 5 }}>
              {sortAsc ? "Asc" : "Desc"}
            </button>
          </div>

          <table border="1" cellPadding="5" style={{ borderCollapse: "collapse" }}>
            <thead>
              <tr>
                <th>ID</th><th>Username</th><th>Email</th><th>Role</th>
              </tr>
            </thead>
            <tbody>
              {data.map(user => (
                <tr key={user.id}>
                  <td>{user.id}</td><td>{user.username}</td><td>{user.email}</td><td>{user.role}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      );
    }

    if (activeReport === "pois") {
      return (
        <div>
          <p><strong>Total POIs:</strong> {reportData.totalPOIs}</p>

          <div style={{ marginBottom: 10 }}>
            <select value={filterColumn} onChange={(e) => setFilterColumn(e.target.value)}>
              <option value="">--Sort by--</option>
              <option value="id">ID</option>
              <option value="name">Name</option>
              <option value="visitors">Visitors</option>
            </select>
            <button onClick={() => setSortAsc(!sortAsc)} style={{ marginLeft: 5 }}>
              {sortAsc ? "Asc" : "Desc"}
            </button>
          </div>

          <table border="1" cellPadding="5" style={{ borderCollapse: "collapse" }}>
            <thead>
              <tr>
                <th>ID</th><th>Name</th><th>Visitors</th>
              </tr>
            </thead>
            <tbody>
              {data.map(poi => (
                <tr key={poi.id}>
                  <td>{poi.id}</td><td>{poi.name}</td><td>{poi.visitors}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      );
    }

    return <p>No report available.</p>;
  };

  return (
    <div style={{ marginLeft: 220, padding: 20 }}>
      <h2>Admin Reports</h2>

      <div style={{ marginBottom: 20 }}>
        <button
          onClick={() => setActiveReport("users")}
          style={{ marginRight: 10, backgroundColor: activeReport === "users" ? "#ccc" : "#fff" }}
        >
          User Reports
        </button>
        <button
          onClick={() => setActiveReport("pois")}
          style={{ backgroundColor: activeReport === "pois" ? "#ccc" : "#fff" }}
        >
          POI Reports
        </button>
      </div>

      <div ref={reportRef} style={{ marginBottom: 20 }}>
        {renderReport()}
      </div>

      <button onClick={handlePrint}>Print / Save as PDF</button>
    </div>
  );
}
