import React, { useEffect, useState } from "react";
import { MapContainer, TileLayer, Marker, Popup } from "react-leaflet";
import "leaflet/dist/leaflet.css";
import L from "leaflet";
import Papa from "papaparse";

// Fix Leaflet default icon path
delete L.Icon.Default.prototype._getIconUrl;
L.Icon.Default.mergeOptions({
  iconRetinaUrl: require("leaflet/dist/images/marker-icon-2x.png"),
  iconUrl: require("leaflet/dist/images/marker-icon.png"),
  shadowUrl: require("leaflet/dist/images/marker-shadow.png"),
});

const CENTER = { lat: 14.1467, lng: 121.4708 };

export default function MapPage() {
  const [spots, setSpots] = useState([]);
  const [selectedSpot, setSelectedSpot] = useState(null);

  useEffect(() => {
    fetch("/data/spots.csv")
      .then((res) => res.text())
      .then((csv) => {
        Papa.parse(csv, {
          header: true,
          skipEmptyLines: true,
          complete: (res) => {
            const rows = res.data
              .filter((r) => r.name && r.lat && r.lng)
              .map((r) => ({
                name: r.name,
                lat: parseFloat(r.lat),
                lng: parseFloat(r.lng),
                description: r.description || "No description available",
                visitors: r.visitors || "Moderate",
                tags: r.tags ? r.tags.split(",").map((t) => t.trim()) : [],
              }));
            setSpots(rows);
          },
        });
      });
  }, []);

  return (
    <div style={{ height: "80vh", width: "100%" }}>
      <MapContainer center={CENTER} zoom={13} style={{ height: "100%", width: "100%" }}>
        <TileLayer
          attribution='&copy; <a href="https://www.openstreetmap.org/">OSM contributors</a>'
          url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
        />
        {spots.map((spot, i) => (
          <Marker
            key={i}
            position={[spot.lat, spot.lng]}
            eventHandlers={{
              click: () => setSelectedSpot(spot), // store the clicked POI
            }}
          >
            <Popup>
              <h3 className="font-bold">{spot.name}</h3>
              <p><strong>Visitors:</strong> {spot.visitors}</p>
              <p>{spot.description}</p>
              {spot.tags.length > 0 && (
                <p><strong>Tags:</strong> {spot.tags.join(", ")}</p>
              )}
            </Popup>
          </Marker>
        ))}
      </MapContainer>

      {/* Optional: Show a side panel with selected POI details */}
      {selectedSpot && (
        <div
          style={{
            position: "absolute",
            bottom: 20,
            left: 20,
            background: "white",
            padding: "15px",
            borderRadius: "10px",
            boxShadow: "0 2px 10px rgba(0,0,0,0.3)",
            maxWidth: "300px",
            zIndex: 1000,
          }}
        >
          <button
            onClick={() => setSelectedSpot(null)}
            style={{
              float: "right",
              background: "red",
              color: "white",
              border: "none",
              borderRadius: "5px",
              padding: "2px 6px",
              cursor: "pointer",
            }}
          >
            âœ–
          </button>
          <h3 className="font-bold">{selectedSpot.name}</h3>
          <p><strong>Visitors:</strong> {selectedSpot.visitors}</p>
          <p>{selectedSpot.description}</p>
          {selectedSpot.tags.length > 0 && (
            <p><strong>Tags:</strong> {selectedSpot.tags.join(", ")}</p>
          )}
        </div>
      )}
    </div>
  );
}
