import React, { useEffect, useState } from "react";

export default function ProfilePage() {
  const userId = 1; // TODO: replace with logged-in user id (from auth context or localStorage)
  const [user, setUser] = useState(null);
  const [preferences, setPreferences] = useState("");
  const [editing, setEditing] = useState(false);

  useEffect(() => {
    fetch(`http://localhost:5000/api/users/${userId}`)
      .then((res) => res.json())
      .then((data) => {
        setUser(data);
        setPreferences(data.preferences || "");
      })
      .catch((err) => console.error("Error fetching user:", err));
  }, [userId]);

  const handleSave = () => {
    fetch(`http://localhost:5000/api/users/${userId}/preferences`, {
      method: "PUT",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ preferences }),
    })
      .then((res) => res.json())
      .then(() => {
        setEditing(false);
        setUser({ ...user, preferences });
      })
      .catch((err) => console.error("Error updating preferences:", err));
  };

  if (!user) return <div style={{ padding: "24px" }}>Loading...</div>;

  return (
    <div style={{ padding: "24px", maxWidth: "600px", margin: "0 auto" }}>
      <h1 style={{ fontSize: "1.8rem", fontWeight: "bold", marginBottom: "16px" }}>
        Profile
      </h1>

      <div
        style={{
          border: "1px solid #ddd",
          borderRadius: "16px",
          padding: "20px",
          background: "#fff",
          boxShadow: "0 2px 6px rgba(0,0,0,0.1)",
        }}
      >
        <p>
          <strong>Username:</strong> {user.username}
        </p>
        <p>
          <strong>Email:</strong> {user.email}
        </p>

        <div style={{ marginTop: "16px" }}>
          <strong>Preferences:</strong>
          {editing ? (
            <div>
              <textarea
                value={preferences}
                onChange={(e) => setPreferences(e.target.value)}
                style={{
                  width: "100%",
                  height: "80px",
                  marginTop: "8px",
                  padding: "8px",
                  border: "1px solid #ddd",
                  borderRadius: "8px",
                }}
              />
              <div style={{ marginTop: "8px" }}>
                <button
                  onClick={handleSave}
                  style={{
                    padding: "8px 16px",
                    marginRight: "8px",
                    background: "#007bff",
                    color: "#fff",
                    border: "none",
                    borderRadius: "8px",
                    cursor: "pointer",
                  }}
                >
                  Save
                </button>
                <button
                  onClick={() => {
                    setEditing(false);
                    setPreferences(user.preferences || "");
                  }}
                  style={{
                    padding: "8px 16px",
                    background: "#ccc",
                    border: "none",
                    borderRadius: "8px",
                    cursor: "pointer",
                  }}
                >
                  Cancel
                </button>
              </div>
            </div>
          ) : (
            <p style={{ marginTop: "8px" }}>
              {user.preferences || "No preferences set."}
            </p>
          )}
        </div>

        {!editing && (
          <button
            onClick={() => setEditing(true)}
            style={{
              marginTop: "16px",
              padding: "8px 16px",
              background: "#28a745",
              color: "#fff",
              border: "none",
              borderRadius: "8px",
              cursor: "pointer",
            }}
          >
            Edit Preferences
          </button>
        )}
      </div>
    </div>
  );
}
