import React from "react";
import WeatherWidget from "./WeatherWidget";
import RoutePlanner from "./RoutePlanner";

export default function App() {
  return (
    <div style={{ display: "flex", height: "100vh" }}>
      <div style={{ width: 380, padding: 16, borderRight: "1px solid #e5e7eb", background: "#fff", overflowY: "auto" }}>
        <h1 style={{ margin: 0, fontSize: 20 }}>üèû Majayjay Tourism Circuit</h1>
        <p style={{ marginTop: 6, color: "#6b7280" }}>Smart, time-aware route suggestions from CSV.</p>

        <WeatherWidget />

        <div style={{ marginTop: 12 }}>
          <RoutePlanner />
        </div>

        <div style={{ marginTop: 16, fontSize: 12, color: "#6b7280" }}>
          Tips: Click ‚ÄúGet Recommendations‚Äù to build a list of POIs based on the current time (morning ‚Üí falls, midday ‚Üí restaurants, evening ‚Üí accommodations).
        </div>
      </div>

      <div style={{ flex: 1 }}>
        {/* RoutePlanner renders the map UI inside itself */}
      </div>
    </div>
  );
}
