import React, { useEffect, useState } from "react";
import { MapContainer, TileLayer, Marker, Popup, Polyline } from "react-leaflet";
import "leaflet/dist/leaflet.css";
import { useNavigate, useLocation } from "react-router-dom";

export default function RouteDetails() {
  const location = useLocation();
  const navigate = useNavigate();
  const routeSpots = location.state?.itinerary || [];
  const [currentIndex, setCurrentIndex] = useState(0);
  const [fullRoute, setFullRoute] = useState([]);
  const [loadingRoute, setLoadingRoute] = useState(true);

  const ORS_API_KEY = "YOUR_ORS_API_KEY"; // replace with your ORS API key

  // Fetch route segment from ORS
  const fetchRouteSegment = async (from, to) => {
    const isTrail = ["forest", "mountain", "trail"].some(t =>
      from.type?.toLowerCase().includes(t)
    );
    const profile = isTrail ? "foot-hiking" : "driving-car";

    const url = `https://api.openrouteservice.org/v2/directions/${profile}/geojson`;
    const body = { coordinates: [[from.lng, from.lat], [to.lng, to.lat]] };

    try {
      const res = await fetch(url, {
        method: "POST",
        headers: {
          "Authorization": ORS_API_KEY,
          "Content-Type": "application/json"
        },
        body: JSON.stringify(body)
      });

      const data = await res.json();
      const coords = data.features[0].geometry.coordinates.map(([lng, lat]) => [lat, lng]);
      const summary = data.features[0].properties.summary;

      return { coords, isTrail, summary };
    } catch (err) {
      console.error("Route fetch failed:", err);
      return { coords: [[from.lat, from.lng], [to.lat, to.lng]], isTrail: false, summary: { distance: 0, duration: 0 } };
    }
  };

  // Build full route segments
  useEffect(() => {
    const buildRoute = async () => {
      const segments = [];
      for (let i = 0; i < routeSpots.length - 1; i++) {
        const seg = await fetchRouteSegment(routeSpots[i], routeSpots[i + 1]);
        segments.push(seg);
      }
      setFullRoute(segments);
      setLoadingRoute(false);
    };
    if (routeSpots.length > 1) buildRoute();
    else setLoadingRoute(false);
  }, [routeSpots]);

  const nextPOI = () => {
    if (currentIndex < routeSpots.length - 1) setCurrentIndex(currentIndex + 1);
    else alert("You have reached the end of the route!");
  };

  const finishRoute = () => {
    if (window.confirm("Are you sure you want to finish the route?")) {
      alert("Route completed!");
      navigate("/home");
    }
  };

  const cancelRoute = () => {
    if (window.confirm("Are you sure you want to cancel the route? You will lose progress.")) {
      alert("Route cancelled.");
      navigate("/home");
    }
  };

  if (!routeSpots.length) return <p>No route available.</p>;

  const currentPOI = routeSpots[currentIndex];
  const currentSegment = fullRoute[currentIndex] || null;

  return (
    <div style={{ display: "grid", gridTemplateColumns: "1.2fr 0.8fr", gap: 20, padding: 20, minHeight: "100vh" }}>
      
      <div style={{ height: "600px", borderRadius: 12, overflow: "hidden", boxShadow: "0 6px 18px rgba(0,0,0,0.15)" }}>
        {loadingRoute ? (
          <p style={{ textAlign: "center", marginTop: "40%" }}>Loading route...</p>
        ) : (
          <MapContainer center={[currentPOI.lat || 0, currentPOI.lng || 0]} zoom={14} style={{ height: "100%", width: "100%" }}>
            <TileLayer
              attribution='&copy; <a href="https://www.openstreetmap.org/">OSM contributors</a>'
              url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
            />

            {/* Route segments */}
            {fullRoute.map((seg, i) => (
              <Polyline
                key={i}
                positions={seg.coords}
                color={i === currentIndex ? "orange" : seg.isTrail ? "green" : "blue"}
                dashArray={seg.isTrail ? "5,10" : undefined}
              />
            ))}

            {/* Markers */}
            {routeSpots.filter(spot => spot.lat && spot.lng).map((spot, i) => (
              <Marker key={i} position={[spot.lat, spot.lng]}>
                <Popup>
                  <strong>{spot.name}</strong>
                  <p>{spot.description}</p>
                  {spot.image && <img src={spot.image} alt={spot.name} style={{ width: 160, borderRadius: 8, marginTop: 6 }} />}
                </Popup>
              </Marker>
            ))}
          </MapContainer>
        )}
      </div>

      <div style={{ background: "#fff", borderRadius: 12, padding: 20, boxShadow: "0 6px 18px rgba(0,0,0,0.12)" }}>
        <h2>Current POI</h2>
        <p><strong>{currentPOI.name}</strong></p>
        <p>{currentPOI.description}</p>

        {/* Weather placeholder */}
        <p><strong>Weather:</strong> Sunny, 28Â°C</p>

        {/* Distance & Duration */}
        {currentSegment && (
          <>
            <p><strong>Distance to next POI:</strong> {(currentSegment.summary.distance / 1000).toFixed(2)} km</p>
            <p><strong>Estimated time:</strong> {(currentSegment.summary.duration / 60).toFixed(0)} min</p>
          </>
        )}

        {currentPOI.image && (
          <img
            src={currentPOI.image}
            alt={currentPOI.name}
            style={{ width: "100%", maxWidth: 280, borderRadius: 10, boxShadow: "0 6px 14px rgba(0,0,0,0.2)" }}
          />
        )}

        <p>POI {currentIndex + 1} of {routeSpots.length}</p>

        <div style={{ marginTop: 20, display: "flex", gap: 10 }}>
          {currentIndex < routeSpots.length - 1 && (
            <button onClick={nextPOI}>Next POI</button>
          )}
          <button onClick={cancelRoute}>Cancel Route</button>
          {currentIndex === routeSpots.length - 1 && (
            <button onClick={finishRoute}>Finish Route</button>
          )}
        </div>
      </div>
    </div>
  );
}
