import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";

export default function RoutePlanner() {
  const navigate = useNavigate();
  const [pois, setPois] = useState([]);
  const [categories, setCategories] = useState([]);
  const [selectedCategories, setSelectedCategories] = useState([]);
  const [user, setUser] = useState(null);
  const [userLocation, setUserLocation] = useState(null);
  const [mode, setMode] = useState("car");

  useEffect(() => {
    const storedUser = JSON.parse(localStorage.getItem("user"));
    setUser(storedUser);

    fetch("http://localhost:5000/api/pois")
      .then((res) => res.json())
      .then((data) => {
        setPois(data);
        const cats = [...new Set(data.flatMap((p) => p.tags?.split(",").map((t) => t.trim()) || []))];
        setCategories(cats);
      });

    if ("geolocation" in navigator) {
      navigator.geolocation.getCurrentPosition(
        (pos) => setUserLocation({ lat: pos.coords.latitude, lng: pos.coords.longitude }),
        () => setUserLocation({ lat: 14.1461, lng: 121.5012 })
      );
    } else setUserLocation({ lat: 14.1461, lng: 121.5012 });
  }, []);

  const toggleCategory = (cat) => {
    setSelectedCategories((prev) =>
      prev.includes(cat) ? prev.filter((c) => c !== cat) : [...prev, cat]
    );
  };

  const planRoute = () => {
    if (!userLocation) return alert("Waiting for location...");

    let filtered = pois;
    if (selectedCategories.length)
      filtered = pois.filter((poi) =>
        selectedCategories.some((cat) => poi.tags?.toLowerCase().includes(cat.toLowerCase()))
      );

    if (!filtered.length) filtered = pois; // fallback

    const likedPoiIds = user?.liked_pois || [];
    filtered = filtered.map((poi) => ({ ...poi, recommended: likedPoiIds.includes(poi.id) }));

    navigate("/routedetails", {
      state: {
        startPoint: userLocation,
        filteredPois: filtered,
        transportMode: mode === "foot" ? "foot-walking" : mode === "bike" ? "cycling-regular" : "driving-car",
      },
    });
  };

  return (
    <div style={{ maxWidth: 400, margin: "40px auto", textAlign: "center" }}>
      <h2>Choose your interests</h2>
      {categories.map((cat) => (
        <label key={cat} style={{ display: "block", margin: "6px 0" }}>
          <input
            type="checkbox"
            checked={selectedCategories.includes(cat)}
            onChange={() => toggleCategory(cat)}
          />{" "}
          {cat}
        </label>
      ))}

      <div style={{ margin: "20px 0" }}>
        <label>
          Transport:
          <select value={mode} onChange={(e) => setMode(e.target.value)} style={{ marginLeft: 8 }}>
            <option value="foot">On Foot</option>
            <option value="bike">Bike</option>
            <option value="motor">Motor</option>
            <option value="car">Car</option>
          </select>
        </label>
      </div>

      <button
        onClick={planRoute}
        style={{
          padding: "12px 24px",
          background: "#2196f3",
          color: "#fff",
          border: "none",
          borderRadius: 8,
          fontSize: 16,
          cursor: "pointer",
        }}
      >
        Plan My Route
      </button>
    </div>
  );
}
