import React, { useEffect, useState } from "react";
import { MapContainer, TileLayer, Marker, Popup, Polyline } from "react-leaflet";
import "leaflet/dist/leaflet.css";
import { useNavigate, useLocation } from "react-router-dom";

export default function RouteDetails() {
  const location = useLocation();
  const navigate = useNavigate();
  const routeSpots = location.state?.itinerary || [];

  const [currentIndex, setCurrentIndex] = useState(0);
  const [segmentData, setSegmentData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [weather, setWeather] = useState(null);

  const ORS_API_KEY = "YOUR_ORS_API_KEY_HERE";
  const WEATHER_API_KEY = "YOUR_OPENWEATHER_API_KEY_HERE";

  const currentPOI = routeSpots[currentIndex];
  const nextPOI = routeSpots[currentIndex + 1];

  // --- Snap to road ---
  const snapToRoad = async (poi) => {
    if (!poi) return poi;
    try {
      const res = await fetch("https://api.openrouteservice.org/v2/snap/driving-car", {
        method: "POST",
        headers: {
          Authorization: ORS_API_KEY,
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ coordinates: [[poi.lng, poi.lat]] }),
      });
      const data = await res.json();
      if (data.snappedLocations?.length) {
        const snapped = data.snappedLocations[0].location;
        return { lat: snapped[1], lng: snapped[0] };
      }
      return poi;
    } catch {
      return poi;
    }
  };

  // --- Fetch route between points ---
  const fetchRouteSegment = async (from, to) => {
    if (!from || !to) return null;

    const snappedFrom = await snapToRoad(from);
    const snappedTo = await snapToRoad(to);

    try {
      const res = await fetch("https://api.openrouteservice.org/v2/directions/driving-car/geojson", {
        method: "POST",
        headers: { Authorization: ORS_API_KEY, "Content-Type": "application/json" },
        body: JSON.stringify({
          coordinates: [
            [snappedFrom.lng, snappedFrom.lat],
            [snappedTo.lng, snappedTo.lat],
          ],
          preference: "fastest",
          instructions: false,
        }),
      });
      const data = await res.json();
      if (data.features?.length) {
        const coords = data.features[0].geometry.coordinates.map(([lng, lat]) => [lat, lng]);
        return { coords, summary: data.features[0].properties.summary, profile: "driving" };
      }
    } catch {}

    try {
      const res = await fetch("https://api.openrouteservice.org/v2/directions/foot-walking/geojson", {
        method: "POST",
        headers: { Authorization: ORS_API_KEY, "Content-Type": "application/json" },
        body: JSON.stringify({
          coordinates: [
            [snappedFrom.lng, snappedFrom.lat],
            [snappedTo.lng, snappedTo.lat],
          ],
          instructions: false,
        }),
      });
      const data = await res.json();
      if (data.features?.length) {
        const coords = data.features[0].geometry.coordinates.map(([lng, lat]) => [lat, lng]);
        return { coords, summary: data.features[0].properties.summary, profile: "non-drivable" };
      }
    } catch {}

    return { coords: [[from.lat, from.lng], [to.lat, to.lng]], summary: { distance: 0, duration: 0 }, profile: "none" };
  };

  // --- Weather fetch ---
  const fetchWeather = async (poi) => {
    if (!poi) return;
    try {
      const res = await fetch(
        `https://api.openweathermap.org/data/2.5/weather?lat=${poi.lat}&lon=${poi.lng}&units=metric&appid=${WEATHER_API_KEY}`
      );
      const data = await res.json();
      setWeather(data);
    } catch {
      setWeather(null);
    }
  };

  // --- Load map segment and weather ---
  useEffect(() => {
    const loadSegment = async () => {
      setLoading(true);
      if (currentPOI && nextPOI) {
        const segment = await fetchRouteSegment(currentPOI, nextPOI);
        setSegmentData(segment);
      } else {
        setSegmentData(null);
      }
      await fetchWeather(currentPOI);
      setLoading(false);
    };
    loadSegment();
  }, [currentIndex, routeSpots]);

  // --- Navigation between POIs ---
  const nextPOIHandler = () => {
    if (currentIndex < routeSpots.length - 1) setCurrentIndex(currentIndex + 1);
    else alert("You have reached the end of the route!");
  };

  // --- Save itinerary ---
  const saveItinerary = async (status) => {
    const token = localStorage.getItem("token");
    if (!token) {
      alert("You must be logged in!");
      return false;
    }
    try {
      const res = await fetch("http://localhost:5000/api/itineraries", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify({
          name: `${status} Itinerary`,
          route_data: routeSpots,
        }),
      });
      if (!res.ok) {
        const err = await res.json();
        alert("Failed to save itinerary: " + err.message);
        return false;
      }
      const data = await res.json();
      console.log("Saved itinerary:", data);
      return true;
    } catch (err) {
      console.error(err);
      alert("Error saving itinerary. Check console.");
      return false;
    }
  };

  // --- Finish / Cancel handlers ---
  const finishRoute = async () => {
    if (window.confirm("Finish route?")) {
      const success = await saveItinerary("Completed");
      if (success) {
        alert("Route completed and saved!");
        navigate("/home");
      }
    }
  };

  const cancelRoute = async () => {
    if (window.confirm("Cancel route? You will lose progress.")) {
      const success = await saveItinerary("Cancelled");
      if (success) {
        alert("Route cancelled but saved.");
        navigate("/home");
      }
    }
  };

  const formatDistance = (meters) => `${(meters / 1000).toFixed(2)} km`;
  const formatDuration = (seconds) => `${Math.floor(seconds / 60)} min`;

  if (!currentPOI) return <p>No route available.</p>;

  return (
    <div style={{ display: "grid", gridTemplateColumns: "1.2fr 0.8fr", gap: 20, padding: 20, minHeight: "100vh" }}>
      <div style={{ height: "600px", borderRadius: 12, overflow: "hidden", boxShadow: "0 6px 18px rgba(0,0,0,0.15)" }}>
        {loading ? (
          <p style={{ textAlign: "center", marginTop: "40%" }}>Loading route...</p>
        ) : (
          <MapContainer center={[currentPOI.lat, currentPOI.lng]} zoom={14} style={{ height: "100%", width: "100%" }}>
            <TileLayer url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png" attribution="© OpenStreetMap contributors" />
            {segmentData && (
              <Polyline
                positions={segmentData.coords}
                color={segmentData.profile === "driving" ? "blue" : "orange"}
                dashArray={segmentData.profile === "driving" ? "" : "8,6"}
              />
            )}
            {[currentPOI, nextPOI].filter(Boolean).map((poi, i) => (
              <Marker key={i} position={[poi.lat, poi.lng]}>
                <Popup>
                  <strong>{poi.name}</strong>
                  <p>{poi.description}</p>
                  {poi.image && <img src={poi.image} alt={poi.name} style={{ width: 160, borderRadius: 8, marginTop: 6 }} />}
                </Popup>
              </Marker>
            ))}
          </MapContainer>
        )}
      </div>

      <div style={{ background: "#fff", borderRadius: 12, padding: 20, boxShadow: "0 6px 18px rgba(0,0,0,0.12)" }}>
        <h2 style={{ marginBottom: 12, color: "#0d47a1" }}>Current POI</h2>
        <p>
          <strong>{currentPOI.name}</strong>
        </p>
        <p>{currentPOI.description}</p>
        {currentPOI.image && (
          <img
            src={currentPOI.image}
            alt={currentPOI.name}
            style={{ width: "100%", maxWidth: 280, borderRadius: 10, boxShadow: "0 6px 14px rgba(0,0,0,0.2)" }}
          />
        )}

        {segmentData && (
          <div style={{ marginTop: 10 }}>
            <p>Distance to next POI: {formatDistance(segmentData.summary.distance)}</p>
            <p>Estimated time: {formatDuration(segmentData.summary.duration)}</p>
          </div>
        )}

        {weather && (
          <div style={{ marginTop: 16, background: "#e3f2fd", padding: 10, borderRadius: 8 }}>
            <h4 style={{ marginBottom: 6 }}>Weather</h4>
            <p>
              {weather.name}: {weather.weather[0].main}, {weather.main.temp}°C
            </p>
          </div>
        )}

        <div style={{ marginTop: 20 }}>
          <button onClick={nextPOIHandler} disabled={currentIndex >= routeSpots.length - 1} style={{ marginRight: 8, padding: "10px 20px", background: "#2196f3", color: "#fff", border: "none", borderRadius: 6 }}>
            Next Stop
          </button>
          <button onClick={finishRoute} style={{ marginRight: 8, padding: "10px 20px", background: "#4caf50", color: "#fff", border: "none", borderRadius: 6 }}>
            Finish
          </button>
          <button onClick={cancelRoute} style={{ padding: "10px 20px", background: "#f44336", color: "#fff", border: "none", borderRadius: 6 }}>
            Cancel
          </button>
        </div>
      </div>
    </div>
  );
}
