// src/pages/AdminDashboard.js
import React, { useState, useEffect } from "react";
import { MapContainer, TileLayer, Marker, Popup, useMapEvents } from "react-leaflet";
import "leaflet/dist/leaflet.css";

function LocationPicker({ setPosition }) {
  useMapEvents({
    click(e) {
      setPosition(e.latlng);
    },
  });
  return null;
}

export default function AdminDashboard({ isAdmin }) {
  const [pois, setPois] = useState([]);
  const [newPOI, setNewPOI] = useState({ name: "", type: "nature", visitors: "Moderate", description: "", image: "" });
  const [selectedPosition, setSelectedPosition] = useState(null);

  useEffect(() => {
    if (!isAdmin) return;
    fetch("/api/pois")
      .then(res => res.json())
      .then(setPois);
  }, [isAdmin]);

  if (!isAdmin) return <h2>Access Denied</h2>;

  const savePOI = async () => {
    if (!selectedPosition || !newPOI.name) {
      alert("Please provide a name and pick a location on the map.");
      return;
    }
    const poi = { ...newPOI, lat: selectedPosition.lat, lng: selectedPosition.lng };
    await fetch("/api/pois", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(poi),
    });
    setPois([...pois, poi]);
    setNewPOI({ name: "", type: "nature", visitors: "Moderate", description: "", image: "" });
    setSelectedPosition(null);
  };

  const deletePOI = async (index) => {
    await fetch(`/api/pois/${index}`, { method: "DELETE" });
    setPois(pois.filter((_, i) => i !== index));
  };

  return (
    <div style={{ display: "grid", gridTemplateColumns: "2fr 1fr", gap: "16px", padding: "20px" }}>
      {/* Map & Add POI */}
      <div style={{ height: "600px" }}>
        <MapContainer center={[14.1467, 121.4708]} zoom={13} style={{ height: "100%", width: "100%" }}>
          <TileLayer url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png" attribution='&copy; OSM contributors' />
          {selectedPosition && <Marker position={selectedPosition} />}
          {pois.map((poi, i) => (
            <Marker key={i} position={[poi.lat, poi.lng]}>
              <Popup>
                <strong>{poi.name}</strong>
                <p>{poi.description}</p>
                <p>Type: {poi.type} | Visitors: {poi.visitors}</p>
                {poi.image && <img src={poi.image} alt={poi.name} style={{ width: "100px", borderRadius: "6px" }} />}
                <br />
                <button onClick={() => deletePOI(i)}>Delete</button>
              </Popup>
            </Marker>
          ))}
          <LocationPicker setPosition={setSelectedPosition} />
        </MapContainer>
      </div>

      {/* Add POI Form */}
      <div>
        <h2>Add New POI</h2>
        <label>Name:</label>
        <input value={newPOI.name} onChange={e => setNewPOI({ ...newPOI, name: e.target.value })} /><br/>
        <label>Type:</label>
        <select value={newPOI.type} onChange={e => setNewPOI({ ...newPOI, type: e.target.value })}>
          <option value="nature">Nature</option>
          <option value="culture">Culture</option>
          <option value="history">History</option>
        </select><br/>
        <label>Visitors:</label>
        <select value={newPOI.visitors} onChange={e => setNewPOI({ ...newPOI, visitors: e.target.value })}>
          <option>Very High</option>
          <option>High</option>
          <option>Moderate</option>
          <option>Low</option>
        </select><br/>
        <label>Description:</label>
        <textarea value={newPOI.description} onChange={e => setNewPOI({ ...newPOI, description: e.target.value })} /><br/>
        <label>Image URL:</label>
        <input value={newPOI.image} onChange={e => setNewPOI({ ...newPOI, image: e.target.value })} /><br/>
        <p>Click on the map to set POI location.</p>
        <button onClick={savePOI}>Save POI</button>
      </div>
    </div>
  );
}
