import React, { useEffect, useState } from "react";
import Papa from "papaparse";

export default function NatureCulturePage() {
  const [places, setPlaces] = useState([]);
  const [selectedPlace, setSelectedPlace] = useState(null);

  useEffect(() => {
    fetch("/data/spots.csv")
      .then((res) => res.text())
      .then((csv) => {
        Papa.parse(csv, {
          header: true,
          skipEmptyLines: true,
          complete: (res) => {
            const rows = res.data
              .filter(
                (r) =>
                  r.name &&
                  r.tags &&
                  (r.tags.toLowerCase().includes("nature") ||
                    r.tags.toLowerCase().includes("culture"))
              )
              .map((r) => ({ ...r, image: r.image || null }));
            setPlaces(rows);
          },
        });
      });
  }, []);

  return (
    <div className="p-6">
      {selectedPlace ? (
        <div className="max-w-3xl mx-auto bg-white shadow-lg rounded-2xl p-6">
          {selectedPlace.image ? (
            <img
              src={selectedPlace.image}
              alt={selectedPlace.name}
              className="h-64 w-full object-cover rounded-lg mb-4"
            />
          ) : (
            <div className="h-64 w-full flex items-center justify-center bg-gray-100 text-gray-500 mb-4">
              No image
            </div>
          )}
          <h2 className="text-2xl font-bold mb-2">{selectedPlace.name}</h2>
          <p className="text-gray-700 mb-2">{selectedPlace.description}</p>
          {selectedPlace.visitors && (
            <p className="text-gray-500 mb-2">
              Visitors: {selectedPlace.visitors}
            </p>
          )}
          {selectedPlace.tags && (
            <p className="text-gray-500 mb-4">Tags: {selectedPlace.tags}</p>
          )}
          <button
            onClick={() => setSelectedPlace(null)}
            className="px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700"
          >
            Back
          </button>
        </div>
      ) : (
        <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-6">
          {places.map((p, i) => (
            <div
              key={i}
              onClick={() => setSelectedPlace(p)}
              className="cursor-pointer rounded-2xl shadow-md overflow-hidden border bg-white hover:shadow-lg transition"
            >
              {p.image ? (
                <img
                  src={p.image}
                  alt={p.name}
                  className="h-48 w-full object-cover"
                />
              ) : (
                <div className="h-48 w-full flex items-center justify-center bg-gray-100 text-gray-500">
                  No image
                </div>
              )}
              <div className="p-3">
                <h3 className="text-lg font-bold">{p.name}</h3>
                {p.tags && (
                  <p className="text-sm text-gray-500 truncate">{p.tags}</p>
                )}
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
}
