import React, { useEffect, useState } from "react";
import { MapContainer, TileLayer, Marker, Popup, Polyline } from "react-leaflet";
import "leaflet/dist/leaflet.css";
import { useNavigate, useLocation } from "react-router-dom";

export default function RouteDetails() {
  const location = useLocation();
  const navigate = useNavigate();
  const routeSpots = location.state?.itinerary || [];
  const [currentIndex, setCurrentIndex] = useState(0);
  const [currentSegment, setCurrentSegment] = useState(null);
  const [loading, setLoading] = useState(true);
  const [weather, setWeather] = useState(null);

  const ORS_API_KEY = "YOUR_ORS_API_KEY";
  const WEATHER_API_KEY = "YOUR_OPENWEATHER_API_KEY";

  const fetchRouteSegment = async (from, to) => {
    const isTrail = ["forest", "mountain", "trail"].some(t =>
      from.type?.toLowerCase().includes(t)
    );
    const profile = isTrail ? "foot-hiking" : "driving-car";

    const url = `https://api.openrouteservice.org/v2/directions/${profile}?api_key=${ORS_API_KEY}&start=${from.lng},${from.lat}&end=${to.lng},${to.lat}`;
    try {
      const res = await fetch(url);
      const data = await res.json();
      const coords = data.features[0].geometry.coordinates.map(([lng, lat]) => [lat, lng]);
      const summary = data.features[0].properties.summary; // duration (s) & distance (m)
      return { coords, isTrail, summary };
    } catch {
      return { coords: [[from.lat, from.lng], [to.lat, to.lng]], isTrail, summary: { distance: 0, duration: 0 } };
    }
  };

  const fetchWeather = async (lat, lng) => {
    try {
      const res = await fetch(`https://api.openweathermap.org/data/2.5/weather?lat=${lat}&lon=${lng}&units=metric&appid=${WEATHER_API_KEY}`);
      const data = await res.json();
      return {
        temp: data.main.temp,
        description: data.weather[0].description,
      };
    } catch {
      return null;
    }
  };

  useEffect(() => {
    const loadSegment = async () => {
      setLoading(true);
      if (currentIndex < routeSpots.length - 1) {
        const segment = await fetchRouteSegment(routeSpots[currentIndex], routeSpots[currentIndex + 1]);
        setCurrentSegment(segment);
      } else {
        setCurrentSegment(null);
      }
      const w = await fetchWeather(routeSpots[currentIndex].lat, routeSpots[currentIndex].lng);
      setWeather(w);
      setLoading(false);
    };
    if (routeSpots.length) loadSegment();
  }, [currentIndex, routeSpots]);

  const nextPOI = () => {
    if (currentIndex < routeSpots.length - 1) setCurrentIndex(currentIndex + 1);
    else alert("You have reached the end of the route!");
  };

  const finishRoute = () => {
    if (window.confirm("Are you sure you want to finish the route?")) {
      alert("Route completed!");
      navigate("/home");
    }
  };

  const cancelRoute = () => {
    if (window.confirm("Are you sure you want to cancel the route? You will lose progress.")) {
      alert("Route cancelled.");
      navigate("/home");
    }
  };

  const currentPOI = routeSpots[currentIndex];

  if (!routeSpots.length) return <p>No route available.</p>;

  return (
    <div style={{ display: "grid", gridTemplateColumns: "1.5fr 1fr", gap: 20, padding: 20 }}>
      {/* Map */}
      <div style={{ height: "600px", borderRadius: 12, overflow: "hidden" }}>
        {loading ? (
          <p style={{ textAlign: "center", marginTop: "40%" }}>Loading route...</p>
        ) : (
          <MapContainer center={[currentPOI.lat, currentPOI.lng]} zoom={14} style={{ height: "100%", width: "100%" }}>
            <TileLayer
              attribution='&copy; <a href="https://www.openstreetmap.org/">OSM contributors</a>'
              url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
            />
            {currentSegment && (
              <Polyline
                positions={currentSegment.coords}
                color={currentSegment.isTrail ? "green" : "blue"}
                dashArray={currentSegment.isTrail ? "5,10" : undefined}
              />
            )}
            <Marker position={[currentPOI.lat, currentPOI.lng]}>
              <Popup>
                <strong>{currentPOI.name}</strong>
                <p>{currentPOI.description}</p>
                {currentPOI.image && <img src={currentPOI.image} alt={currentPOI.name} style={{ width: "160px", borderRadius: 8, marginTop: 6 }} />}
              </Popup>
            </Marker>
          </MapContainer>
        )}
      </div>

      {/* POI Info Panel */}
      <div style={{ background: "#fff", borderRadius: 12, padding: 20 }}>
        <h2 style={{ margin: 0, color: "#0d47a1" }}>{currentPOI.name}</h2>
        <p>{currentPOI.description}</p>
        {currentPOI.image && <img src={currentPOI.image} alt={currentPOI.name} style={{ width: "100%", borderRadius: 10 }} />}
        {weather && (
          <p style={{ marginTop: 12, fontStyle: "italic" }}>
            Current weather: {weather.temp}Â°C, {weather.description}
          </p>
        )}
        {currentSegment && (
          <p style={{ marginTop: 12, fontStyle: "italic" }}>
            Distance to next POI: {(currentSegment.summary.distance / 1000).toFixed(2)} km, Estimated time: {(currentSegment.summary.duration / 60).toFixed(0)} mins
          </p>
        )}
        <p style={{ marginTop: 20, fontStyle: "italic" }}>POI {currentIndex + 1} of {routeSpots.length}</p>

        <div style={{ display: "flex", gap: 12, marginTop: 20 }}>
          {currentIndex < routeSpots.length - 1 && <button onClick={nextPOI}>Next POI</button>}
          <button onClick={cancelRoute}>Cancel Route</button>
          {currentIndex === routeSpots.length - 1 && <button onClick={finishRoute}>Finish Route</button>}
        </div>
      </div>
    </div>
  );
}
