import React, { useEffect, useState } from "react";

export default function AdminPOIs() {
  const [pois, setPois] = useState([]);
  const [editingPoi, setEditingPoi] = useState(null);
  const [searchTerm, setSearchTerm] = useState("");
  const [visitorFilter, setVisitorFilter] = useState("");

  useEffect(() => {
    const token = localStorage.getItem("token");
    fetch("http://localhost:5000/api/pois", {
      headers: { Authorization: `Bearer ${token}` },
    })
      .then((res) => res.json())
      .then((data) => setPois(Array.isArray(data) ? data : []))
      .catch((err) => console.error(err));
  }, []);

  const savePoi = () => {
    const token = localStorage.getItem("token");
    fetch(`http://localhost:5000/api/pois/${editingPoi.id}`, {
      method: "PUT",
      headers: { "Content-Type": "application/json", Authorization: `Bearer ${token}` },
      body: JSON.stringify(editingPoi),
    })
      .then((res) => res.json())
      .then(() => {
        setPois(pois.map((p) => (p.id === editingPoi.id ? editingPoi : p)));
        setEditingPoi(null);
      })
      .catch((err) => console.error(err));
  };

  const deletePoi = (id) => {
    if (!window.confirm("Delete this POI?")) return;
    const token = localStorage.getItem("token");
    fetch(`http://localhost:5000/api/pois/${id}`, {
      method: "DELETE",
      headers: { Authorization: `Bearer ${token}` },
    })
      .then(() => setPois(pois.filter((p) => p.id !== id)))
      .catch((err) => console.error(err));
  };

  const filteredPois = pois.filter((p) => {
    const matchesSearch =
      p.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
      p.tags.toLowerCase().includes(searchTerm.toLowerCase());
    const matchesVisitor = visitorFilter ? p.visitors === visitorFilter : true;
    return matchesSearch && matchesVisitor;
  });

  return (
    <div style={{ marginLeft: "240px", padding: "20px" }}>
      <h2>Points of Interest</h2>

      <div style={{ marginBottom: "16px", display: "flex", gap: "12px", flexWrap: "wrap" }}>
        <input
          type="text"
          placeholder="Search by name or tags..."
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
          style={{ padding: "6px", flex: "1" }}
        />
        <select
          value={visitorFilter}
          onChange={(e) => setVisitorFilter(e.target.value)}
          style={{ padding: "6px" }}
        >
          <option value="">All Visitors</option>
          <option value="Very High">Very High</option>
          <option value="High">High</option>
          <option value="Moderate">Moderate</option>
          <option value="Low">Low</option>
        </select>
      </div>

      <ul>
        {filteredPois.map((p) => (
          <li key={p.id} style={{ marginBottom: "8px" }}>
            {p.name} ({p.tags}) - Visitors: {p.visitors}
            <button onClick={() => setEditingPoi(p)} style={{ marginLeft: 8 }}>Edit</button>
            <button onClick={() => deletePoi(p.id)} style={{ marginLeft: 8, color: "red" }}>Delete</button>
          </li>
        ))}
      </ul>

      {editingPoi && (
        <div style={{ marginTop: 20, border: "1px solid #ccc", padding: 10, borderRadius: "6px", maxWidth: "400px" }}>
          <h3>Edit {editingPoi.name}</h3>
          <input
            type="text"
            value={editingPoi.name}
            onChange={(e) => setEditingPoi({ ...editingPoi, name: e.target.value })}
            style={{ width: "100%", marginBottom: "8px", padding: "6px" }}
          />
          <input
            type="text"
            value={editingPoi.tags}
            onChange={(e) => setEditingPoi({ ...editingPoi, tags: e.target.value })}
            style={{ width: "100%", marginBottom: "8px", padding: "6px" }}
          />
          <textarea
            value={editingPoi.description}
            onChange={(e) => setEditingPoi({ ...editingPoi, description: e.target.value })}
            style={{ width: "100%", marginBottom: "8px", padding: "6px" }}
          />
          <input
            type="text"
            value={editingPoi.image}
            onChange={(e) => setEditingPoi({ ...editingPoi, image: e.target.value })}
            style={{ width: "100%", marginBottom: "8px", padding: "6px" }}
          />
          <select
            value={editingPoi.visitors}
            onChange={(e) => setEditingPoi({ ...editingPoi, visitors: e.target.value })}
            style={{ width: "100%", marginBottom: "8px", padding: "6px" }}
          >
            <option>Very High</option>
            <option>High</option>
            <option>Moderate</option>
            <option>Low</option>
          </select>
          <button onClick={savePoi}>Save</button>
          <button onClick={() => setEditingPoi(null)} style={{ marginLeft: 8 }}>Cancel</button>
        </div>
      )}
    </div>
  );
}
