import React, { useEffect, useState } from "react";
import { MapContainer, TileLayer, Marker, Popup, useMapEvents } from "react-leaflet";
import "leaflet/dist/leaflet.css";
import L from "leaflet";

delete L.Icon.Default.prototype._getIconUrl;
L.Icon.Default.mergeOptions({
  iconRetinaUrl: require("leaflet/dist/images/marker-icon-2x.png"),
  iconUrl: require("leaflet/dist/images/marker-icon.png"),
  shadowUrl: require("leaflet/dist/images/marker-shadow.png"),
});

export default function AdminPOIsMap() {
  const [pois, setPois] = useState([]);
  const [editingPoiId, setEditingPoiId] = useState(null);
  const [poiEdits, setPoiEdits] = useState({});
  const [searchTerm, setSearchTerm] = useState("");
  const [tagFilter, setTagFilter] = useState("");
  const [addingPoi, setAddingPoi] = useState(false);
  const [newPoi, setNewPoi] = useState(null);

  const token = localStorage.getItem("token");

  useEffect(() => {
    fetch("http://localhost:5000/api/pois", {
      headers: { Authorization: `Bearer ${token}` },
    })
      .then((res) => res.json())
      .then((data) => setPois(Array.isArray(data) ? data : []))
      .catch((err) => console.error(err));
  }, [token]);

  const allTags = Array.from(new Set(pois.flatMap((p) => (p.tags ? p.tags.split(",") : []))));

  const filteredPois = pois.filter((p) => {
    const matchesSearch = p.name.toLowerCase().includes(searchTerm.toLowerCase());
    const matchesTag = tagFilter ? p.tags?.split(",").includes(tagFilter) : true;
    return matchesSearch && matchesTag;
  });

  // --- Save existing POI ---
  const savePoi = async (poiId) => {
    const updatedPoi = { ...poiEdits[poiId] };
    try {
      const res = await fetch(`http://localhost:5000/api/pois/${poiId}`, {
        method: "PUT",
        headers: { "Content-Type": "application/json", Authorization: `Bearer ${token}` },
        body: JSON.stringify(updatedPoi),
      });
      if (!res.ok) throw new Error("Failed to update POI");
      setPois(pois.map((p) => (p.id === poiId ? updatedPoi : p)));
      setEditingPoiId(null);
      setPoiEdits((prev) => {
        const copy = { ...prev };
        delete copy[poiId];
        return copy;
      });
    } catch (err) {
      console.error(err);
      alert("Error saving POI");
    }
  };

  // --- Delete POI ---
  const deletePoi = async (poiId) => {
    if (!window.confirm("Delete this POI?")) return;
    try {
      const res = await fetch(`http://localhost:5000/api/pois/${poiId}`, {
        method: "DELETE",
        headers: { Authorization: `Bearer ${token}` },
      });
      if (!res.ok) throw new Error("Failed to delete POI");
      setPois(pois.filter((p) => p.id !== poiId));
    } catch (err) {
      console.error(err);
      alert("Error deleting POI");
    }
  };

  // --- Handle map click for new POI ---
  function AddPoiClickHandler() {
    useMapEvents({
      click(e) {
        if (addingPoi) {
          setNewPoi({
            lat: e.latlng.lat,
            lng: e.latlng.lng,
            name: "",
            tags: "",
            description: "",
            image: "",
            visitors: "Moderate",
          });
          setAddingPoi(false);
        }
      },
    });
    return null;
  }

  // --- Upload image ---
  const handleImageUpload = async (file) => {
    const formData = new FormData();
    formData.append("image", file);

    const res = await fetch("http://localhost:5000/api/upload", {
      method: "POST",
      body: formData,
    });
    if (!res.ok) throw new Error("Upload failed");
    const data = await res.json();
    return data.filename; // should return filename like "taytayfalls.jpg"
  };

  // --- Save new POI ---
  const saveNewPoi = async () => {
    try {
      const res = await fetch("http://localhost:5000/api/pois", {
        method: "POST",
        headers: { "Content-Type": "application/json", Authorization: `Bearer ${token}` },
        body: JSON.stringify(newPoi),
      });
      if (!res.ok) throw new Error("Failed to create POI");
      const savedPoi = await res.json();
      setPois([...pois, savedPoi]);
      setNewPoi(null);
    } catch (err) {
      console.error(err);
      alert("Error creating POI");
    }
  };

  return (
    <div style={{ marginLeft: "240px", padding: "20px" }}>
      <h2>Points of Interest Map</h2>

      {/* Filters */}
      <div style={{ marginBottom: "16px", display: "flex", gap: "12px", flexWrap: "wrap" }}>
        <input
          type="text"
          placeholder="Search by name..."
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
          style={{ padding: "6px", flex: "1" }}
        />
        <select
          value={tagFilter}
          onChange={(e) => setTagFilter(e.target.value)}
          style={{ padding: "6px" }}
        >
          <option value="">All Tags</option>
          {allTags.map((tag) => (
            <option key={tag} value={tag.trim()}>{tag.trim()}</option>
          ))}
        </select>
        <button type="button" onClick={() => setAddingPoi(true)}>Add POI</button>
      </div>

      {/* Map */}
      <MapContainer center={[14.1619, 121.4626]} zoom={14} style={{ height: "600px", width: "100%" }}>
        <TileLayer
          attribution='&copy; OpenStreetMap contributors'
          url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
        />
        <AddPoiClickHandler />

        {/* Existing POIs */}
        {filteredPois.map((p) => (
          <Marker key={p.id} position={[p.lat, p.lng]}>
            <Popup>
              {editingPoiId === p.id ? (
                <div>
                  <input
                    type="text"
                    placeholder="Name"
                    value={poiEdits[p.id]?.name || p.name}
                    onChange={(e) =>
                      setPoiEdits({
                        ...poiEdits,
                        [p.id]: { ...poiEdits[p.id], name: e.target.value, lat: p.lat, lng: p.lng, tags: poiEdits[p.id]?.tags || p.tags, description: poiEdits[p.id]?.description || p.description, visitors: poiEdits[p.id]?.visitors || p.visitors, image: poiEdits[p.id]?.image || p.image }
                      })
                    }
                    style={{ width: "100%", marginBottom: 4 }}
                  />

                  <input
                    type="text"
                    placeholder="Tags"
                    value={poiEdits[p.id]?.tags || p.tags}
                    onChange={(e) =>
                      setPoiEdits({
                        ...poiEdits,
                        [p.id]: { ...poiEdits[p.id], tags: e.target.value, lat: p.lat, lng: p.lng, name: poiEdits[p.id]?.name || p.name, description: poiEdits[p.id]?.description || p.description, visitors: poiEdits[p.id]?.visitors || p.visitors, image: poiEdits[p.id]?.image || p.image }
                      })
                    }
                    style={{ width: "100%", marginBottom: 4 }}
                  />

                  <textarea
                    placeholder="Description"
                    value={poiEdits[p.id]?.description || p.description}
                    onChange={(e) =>
                      setPoiEdits({
                        ...poiEdits,
                        [p.id]: { ...poiEdits[p.id], description: e.target.value, lat: p.lat, lng: p.lng, name: poiEdits[p.id]?.name || p.name, tags: poiEdits[p.id]?.tags || p.tags, visitors: poiEdits[p.id]?.visitors || p.visitors, image: poiEdits[p.id]?.image || p.image }
                      })
                    }
                    style={{ width: "100%", marginBottom: 4 }}
                  />

                  <input
                    type="file"
                    accept="image/*"
                    onChange={async (e) => {
                      const file = e.target.files[0];
                      if (!file) return;
                      const filename = await handleImageUpload(file);
                      setPoiEdits({
                        ...poiEdits,
                        [p.id]: { ...poiEdits[p.id], image: filename, lat: p.lat, lng: p.lng, name: poiEdits[p.id]?.name || p.name, tags: poiEdits[p.id]?.tags || p.tags, description: poiEdits[p.id]?.description || p.description, visitors: poiEdits[p.id]?.visitors || p.visitors }
                      });
                    }}
                    style={{ width: "100%", marginBottom: 4 }}
                  />

                  <select
                    value={poiEdits[p.id]?.visitors || p.visitors}
                    onChange={(e) =>
                      setPoiEdits({
                        ...poiEdits,
                        [p.id]: { ...poiEdits[p.id], visitors: e.target.value, lat: p.lat, lng: p.lng, name: poiEdits[p.id]?.name || p.name, tags: poiEdits[p.id]?.tags || p.tags, description: poiEdits[p.id]?.description || p.description, image: poiEdits[p.id]?.image || p.image }
                      })
                    }
                    style={{ width: "100%", marginBottom: 4 }}
                  >
                    <option>Very High</option>
                    <option>High</option>
                    <option>Moderate</option>
                    <option>Low</option>
                  </select>

                  <button type="button" onClick={() => savePoi(p.id)}>Save</button>
                  <button type="button" onClick={() => setEditingPoiId(null)} style={{ marginLeft: 4 }}>Cancel</button>
                  <button type="button" onClick={() => deletePoi(p.id)} style={{ marginLeft: 4, color: "red" }}>Delete</button>
                </div>
              ) : (
                <div>
                  <strong>{p.name}</strong><br/>
                  Tags: {p.tags}<br/>
                  Visitors: {p.visitors}<br/>
                  {p.description}<br/>
                  <button type="button" onClick={() => setEditingPoiId(p.id)}>Edit</button>
                  <button type="button" onClick={() => deletePoi(p.id)} style={{ marginLeft: 4, color: "red" }}>Delete</button>
                </div>
              )}
            </Popup>
          </Marker>
        ))}

        {/* New POI */}
        {newPoi && (
          <Marker position={[newPoi.lat, newPoi.lng]}>
            <Popup>
              <div>
                <input type="text" placeholder="Name" value={newPoi.name} onChange={(e) => setNewPoi({...newPoi, name: e.target.value})} style={{width:"100%", marginBottom:4}}/>
                <input type="text" placeholder="Tags" value={newPoi.tags} onChange={(e) => setNewPoi({...newPoi, tags: e.target.value})} style={{width:"100%", marginBottom:4}}/>
                <textarea placeholder="Description" value={newPoi.description} onChange={(e) => setNewPoi({...newPoi, description: e.target.value})} style={{width:"100%", marginBottom:4}}/>
                <input type="file" accept="image/*" onChange={async(e)=>{ const file = e.target.files[0]; if(!file) return; const filename = await handleImageUpload(file); setNewPoi({...newPoi,image:filename});}} style={{width:"100%",marginBottom:4}}/>
                <select value={newPoi.visitors} onChange={(e)=>setNewPoi({...newPoi, visitors:e.target.value})} style={{width:"100%",marginBottom:4}}>
                  <option>Very High</option>
                  <option>High</option>
                  <option>Moderate</option>
                  <option>Low</option>
                </select>
                <button type="button" onClick={saveNewPoi}>Save</button>
                <button type="button" onClick={()=>setNewPoi(null)} style={{marginLeft:4}}>Cancel</button>
              </div>
            </Popup>
          </Marker>
        )}
      </MapContainer>
    </div>
  );
}
