import React, { useEffect, useState } from "react";
import { MapContainer, TileLayer, Marker, Popup } from "react-leaflet";
import "leaflet/dist/leaflet.css";
import Papa from "papaparse";
import { useNavigate } from "react-router-dom";


const CENTER = { lat: 14.1467, lng: 121.4708 };
const MAX_TOP_VISITED = 10;

export default function Home() {
  const navigate = useNavigate();
  const [spots, setSpots] = useState([]);
  const [route, setRoute] = useState([]);
  const [weather, setWeather] = useState({ weather: [{ main: "Clear" }] });
  const [userPreferences, setUserPreferences] = useState({
    preferNature: true,
    preferCulture: false,
  });
  const [startTime, setStartTime] = useState(""); // User-defined start time

  // Load CSV data
  useEffect(() => {
    fetch("/data/spots.csv")
      .then(res => res.text())
      .then(csv => {
        Papa.parse(csv, {
          header: true,
          skipEmptyLines: true,
          complete: (results) => {
            const rows = results.data
              .filter(r => r.name && r.lat && r.lng)
              .map(r => ({
                name: r.name.trim(),
                lat: parseFloat(r.lat),
                lng: parseFloat(r.lng),
                visitors: r.visitors.trim() || "Moderate",
                image: r.image || null,
                description: r.description || "No description",
                type: r.tags || "nature",
              }));
            setSpots(rows);
          }
        });
      });
  }, []);

  // Scoring function
  const scoreSpots = () => {
    return spots.map((spot, i) => {
      let score = 0;
      if (userPreferences.preferNature && spot.type.includes("nature")) score += 0.3;
      if (userPreferences.preferCulture && spot.type.includes("history")) score += 0.2;
      if (weather.weather[0].main.toLowerCase().includes("rain") && spot.type.includes("nature")) score -= 0.2;
      const visitorRanking = { "Very High": 3, High: 2, Moderate: 1, Low: 0 };
      score += visitorRanking[spot.visitors] || 1;
      return { ...spot, score };
    }).sort((a, b) => b.score - a.score).slice(0, MAX_TOP_VISITED);
  };

  // Distance calculation (approx)
  const calculateDistance = (a, b) => {
    const R = 6371; // km
    const dLat = ((b.lat - a.lat) * Math.PI) / 180;
    const dLng = ((b.lng - a.lng) * Math.PI) / 180;
    const lat1 = (a.lat * Math.PI) / 180;
    const lat2 = (b.lat * Math.PI) / 180;

    const aa = Math.sin(dLat/2) * Math.sin(dLat/2) +
               Math.sin(dLng/2) * Math.sin(dLng/2) * Math.cos(lat1) * Math.cos(lat2);
    const c = 2 * Math.atan2(Math.sqrt(aa), Math.sqrt(1-aa));
    return R * c;
  };

  // Optimize route based on distance
  const orderByDistance = (spots) => {
    if (!spots.length) return [];
    const ordered = [];
    const remaining = [...spots];
    let current = remaining.shift();
    ordered.push(current);
    while (remaining.length) {
      let nearestIndex = 0;
      let minDistance = calculateDistance(current, remaining[0]);
      for (let i = 1; i < remaining.length; i++) {
        const dist = calculateDistance(current, remaining[i]);
        if (dist < minDistance) {
          minDistance = dist;
          nearestIndex = i;
        }
      }
      current = remaining.splice(nearestIndex, 1)[0];
      ordered.push(current);
    }
    return ordered;
  };

  // Generate itinerary with times
  const generateItinerary = () => {
    const start = startTime ? new Date(startTime) : new Date();
    const scored = scoreSpots();
    const optimized = orderByDistance(scored);

    // Assign arrival/departure times (2 hours per spot for simplicity)
    const itinerary = optimized.map((spot, i) => {
      const arrival = new Date(start.getTime() + i * 2 * 60 * 60 * 1000);
      const departure = new Date(arrival.getTime() + 2 * 60 * 60 * 1000);
      return { ...spot, arrival, departure };
    });

    console.log("Generated Itinerary:", itinerary);
    setRoute(itinerary);
    navigate("/route-details", { state: { itinerary } });
  };

  const topVisited = [...spots].sort((a,b) => {
    const visitorRanking = { "Very High": 3, High: 2, Moderate: 1, Low: 0 };
    return (visitorRanking[b.visitors] || 1) - (visitorRanking[a.visitors] || 1);
  }).slice(0, MAX_TOP_VISITED);

  return (
    <div className="grid grid-cols-1 md:grid-cols-2 gap-6 p-4">
  {/* Left Card - Map */}
  <div className="bg-white shadow-lg rounded-2xl p-4">
    <h2 className="text-xl font-bold text-gray-800 mb-4 border-b pb-2">
      Majayjay Overview
    </h2>
    <div className="map-wrap rounded-xl overflow-hidden border h-[500px]">
      {spots.length > 0 ? (
        <MapContainer
          center={CENTER}
          zoom={13}
          style={{ height: "100%", width: "100%" }}
        >
          <TileLayer
            attribution='&copy; <a href="https://www.openstreetmap.org/">OSM contributors</a>'
            url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
          />
          {spots.map((s, i) => (
            <Marker key={i} position={[s.lat, s.lng]}>
              <Popup>
                <strong className="text-lg">{s.name}</strong>
                <br />
                <span className="text-sm text-gray-600">
                  Visitors: {s.visitors}
                </span>
                {s.image && (
                  <>
                    <br />
                    <img
                      src={s.image}
                      alt={s.name}
                      className="w-40 rounded-lg mt-2 shadow"
                    />
                  </>
                )}
                <p className="mt-2 text-sm text-gray-700">{s.description}</p>
              </Popup>
            </Marker>
          ))}
        </MapContainer>
      ) : (
        <div className="flex items-center justify-center h-full text-gray-500">
          No spots available
        </div>
      )}
    </div>
  </div>

  {/* Right Card - Top Visited */}
  <div className="bg-white shadow-lg rounded-2xl p-4">
    <h2 className="text-xl font-bold text-gray-800 mb-4 border-b pb-2">
      Top {MAX_TOP_VISITED} Most Visited Places
    </h2>

    <ul className="list-disc pl-6 space-y-2 text-gray-700">
      {topVisited.map((s, i) => (
        <li key={i} className="text-sm">
          <strong className="text-base text-blue-700">{s.name}</strong> â€”{" "}
          {s.visitors} visitors
        </li>
      ))}
    </ul>

    <div className="mt-6">
      <label className="block text-sm font-medium text-gray-700 mb-1">
        Start Time:
      </label>
      <input
        type="datetime-local"
        value={startTime}
        onChange={(e) => setStartTime(e.target.value)}
        className="w-full px-3 py-2 border rounded-lg focus:ring-2 focus:ring-blue-500 focus:outline-none"
      />
    </div>

    <button
      className="mt-4 w-full bg-blue-600 text-white py-2 px-4 rounded-lg hover:bg-blue-700 shadow-md transition"
      onClick={generateItinerary}
    >
      Generate Route
    </button>
  </div>
</div>

  );
}
