import React, { useEffect, useState } from "react";
import Papa from "papaparse";

export default function EstablishmentsPage() {
  const [lodgings, setLodgings] = useState([]);
  const [restaurants, setRestaurants] = useState([]);

  useEffect(() => {
    fetch("/data/spots.csv")
      .then((res) => res.text())
      .then((csv) => {
        Papa.parse(csv, {
          header: true,
          skipEmptyLines: true,
          complete: (res) => {
            const rows = res.data
              .filter((r) => r.name)
              .map((r) => ({
                ...r,
                tags: r.tags
                  ? r.tags.split(",").map((t) => t.trim().toLowerCase())
                  : [],
                image: r.image || null,
                description: r.description || "",
              }));

            setLodgings(
              rows.filter((r) =>
                ["resort", "lodge", "hotel", "motel"].some((tag) =>
                  r.tags.includes(tag)
                )
              )
            );

            setRestaurants(
              rows.filter((r) =>
                ["restaurant", "coffee"].some((tag) => r.tags.includes(tag))
              )
            );
          },
        });
      });
  }, []);

  const renderCards = (items) => (
    <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-6">
      {items.map((item, i) => (
        <div
          key={i}
          className="bg-white border rounded-xl shadow-lg overflow-hidden flex flex-col"
        >
          <div className="h-48 w-full">
            {item.image ? (
              <img
                src={item.image}
                alt={item.name}
                className="h-full w-full object-cover"
              />
            ) : (
              <div className="h-full w-full flex items-center justify-center bg-gray-200 text-gray-500">
                No image
              </div>
            )}
          </div>
          <div className="p-4 flex flex-col flex-grow">
            <h3 className="text-lg font-bold mb-2">{item.name}</h3>
            <p className="text-gray-600 text-sm flex-grow">{item.description}</p>
            <div className="mt-2 text-xs text-gray-400">
              {item.tags.join(", ")}
            </div>
          </div>
        </div>
      ))}
    </div>
  );

  return (
    <div className="p-6">
      <h1 className="text-2xl font-bold mb-6">Establishments</h1>

      <section className="mb-10">
        <h2 className="text-xl font-semibold mb-4">Lodgings</h2>
        {lodgings.length > 0 ? renderCards(lodgings) : <p>No lodgings found.</p>}
      </section>

      <section>
        <h2 className="text-xl font-semibold mb-4">Restaurants</h2>
        {restaurants.length > 0
          ? renderCards(restaurants)
          : <p>No restaurants found.</p>}
      </section>
    </div>
  );
}
