import React, { useEffect, useState, useRef } from "react";
import { MapContainer, TileLayer, Marker, Popup, Polyline } from "react-leaflet";
import "leaflet/dist/leaflet.css";
import L from "leaflet";
import { useNavigate, useLocation } from "react-router-dom";

export default function RouteDetails() {
  const location = useLocation();
  const navigate = useNavigate();
  const routeSpots = location.state?.itinerary || [];
  const itineraryIdFromState = location.state?.id || null;

  const [poiDetails, setPoiDetails] = useState([]);
  const [userLocation, setUserLocation] = useState(null);
  const [tracking, setTracking] = useState(false);
  const [activeIndex, setActiveIndex] = useState(0);
  const [routeToStop, setRouteToStop] = useState([]);
  const [nextPreview, setNextPreview] = useState(null);

  const mapRef = useRef(null);
  const watchIdRef = useRef(null);
  const ORS_API_KEY = "YOUR_ORS_API_KEY";

  const numberedIcon = (number) =>
    L.divIcon({
      html: `<div style="background:#1976d2;color:#fff;width:28px;height:28px;border-radius:50%;text-align:center;line-height:28px;font-weight:600;border:2px solid #fff;">${number}</div>`,
      className: "",
      iconSize: [28, 28],
    });

  const userIcon = L.divIcon({
    html: `<div style="background:#4caf50;color:#fff;width:30px;height:30px;border-radius:50%;text-align:center;line-height:30px;font-weight:700;border:2px solid #fff;">üßç</div>`,
    className: "",
    iconSize: [30, 30],
  });

  // --- Fetch POI details ---
  const fetchPoiDetails = async () => {
    try {
      const res = await fetch("http://localhost:5000/api/pois");
      const data = await res.json();
      const matched = routeSpots.map((spot) =>
        data.find((poi) => poi.name === spot.name || poi.id === spot.id)
      );
      setPoiDetails(matched);
    } catch (err) {
      console.error("Error fetching POI details:", err);
    }
  };

  // --- Fetch route between two points ---
  const fetchRoute = async (from, to) => {
    try {
      const res = await fetch("https://api.openrouteservice.org/v2/directions/driving-car/geojson", {
        method: "POST",
        headers: { Authorization: ORS_API_KEY, "Content-Type": "application/json" },
        body: JSON.stringify({
          coordinates: [
            [from.lng, from.lat],
            [to.lng, to.lat],
          ],
          instructions: false,
        }),
      });
      const data = await res.json();
      if (data.features?.length) {
        return data.features[0].geometry.coordinates.map(([lng, lat]) => [lat, lng]);
      }
    } catch (err) {
      console.error("Failed to fetch route:", err);
    }
    return [];
  };

  // --- Get initial location ---
  useEffect(() => {
    if (!navigator.geolocation) return;
    navigator.geolocation.getCurrentPosition(
      (pos) => setUserLocation({ lat: pos.coords.latitude, lng: pos.coords.longitude }),
      (err) => console.warn("Location error:", err),
      { enableHighAccuracy: true }
    );
  }, []);

  // --- Load POI details ---
  useEffect(() => {
    fetchPoiDetails();
  }, []);

  // --- Start Tour ---
  const startTour = async () => {
    if (!userLocation || routeSpots.length === 0) return;
    setTracking(true);
    setActiveIndex(0);
    setNextPreview(routeSpots[1] || null);

    const firstStop = routeSpots[0];
    const route = await fetchRoute(userLocation, firstStop);
    setRouteToStop(route);

    // Start watching user movement
    watchIdRef.current = navigator.geolocation.watchPosition(
      async (pos) => {
        const current = { lat: pos.coords.latitude, lng: pos.coords.longitude };
        setUserLocation(current);

        // Update the route line dynamically
        const newRoute = await fetchRoute(current, routeSpots[activeIndex]);
        setRouteToStop(newRoute);
      },
      (err) => console.warn("Watch error:", err),
      { enableHighAccuracy: true, maximumAge: 1000 }
    );
  };

  // --- Go to Next Stop manually ---
  const handleNextStop = async () => {
    if (activeIndex + 1 >= routeSpots.length) {
      alert("üéâ You've completed the tour!");
      stopTour();
      return;
    }

    const nextStop = routeSpots[activeIndex + 1];
    setActiveIndex(activeIndex + 1);
    setNextPreview(routeSpots[activeIndex + 2] || null);

    if (userLocation) {
      const newRoute = await fetchRoute(userLocation, nextStop);
      setRouteToStop(newRoute);
    }
  };

  // --- Stop Tour ---
  const stopTour = () => {
    setTracking(false);
    if (watchIdRef.current) navigator.geolocation.clearWatch(watchIdRef.current);
    setRouteToStop([]);
    setActiveIndex(0);
    setNextPreview(null);
  };

  if (!userLocation || !poiDetails.length) return <p>Loading route...</p>;

  return (
    <div
      style={{
        display: "grid",
        gridTemplateColumns: "1.3fr 0.7fr",
        gap: "24px",
        padding: "24px",
        minHeight: "100vh",
        fontFamily: "'Poppins', sans-serif",
        background: "linear-gradient(to bottom right, #e3f2fd, #e8f5e9)",
      }}
    >
      {/* Map Section */}
      <div
        style={{
          height: "650px",
          borderRadius: "18px",
          overflow: "hidden",
          boxShadow: "0 10px 25px rgba(0,0,0,0.15)",
          border: "4px solid #fff",
        }}
      >
        <MapContainer
          ref={mapRef}
          center={[userLocation.lat, userLocation.lng]}
          zoom={14}
          style={{ height: "100%", width: "100%" }}
        >
          <TileLayer url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png" />
          <Marker position={[userLocation.lat, userLocation.lng]} icon={userIcon}>
            <Popup>üìç You are here</Popup>
          </Marker>

          {routeSpots.map((poi, i) => (
            <Marker key={i} position={[poi.lat, poi.lng]} icon={numberedIcon(i + 1)}>
              <Popup>
                <strong>{poiDetails[i]?.name}</strong>
              </Popup>
            </Marker>
          ))}

          {routeToStop.length > 0 && (
            <Polyline positions={routeToStop} color="#ff5722" weight={5} opacity={0.9} />
          )}
        </MapContainer>
      </div>

      {/* Sidebar Section */}
      <div
        style={{
          background: "#fff",
          borderRadius: "18px",
          padding: "24px",
          boxShadow: "0 8px 24px rgba(0,0,0,0.1)",
          overflowY: "auto",
          maxHeight: "650px",
        }}
      >
        <h2 style={{ color: "#1565c0", fontWeight: "700", marginBottom: "16px" }}>
          {tracking ? "üó∫Ô∏è Live Tour" : "Route Overview"}
        </h2>

        {!tracking ? (
          <button
            onClick={startTour}
            style={{
              width: "100%",
              padding: "12px",
              background: "#4caf50",
              color: "#fff",
              border: "none",
              borderRadius: "8px",
              fontWeight: "600",
            }}
          >
            üöó Start Tour
          </button>
        ) : (
          <>
            <button
              onClick={stopTour}
              style={{
                width: "100%",
                padding: "12px",
                background: "#f44336",
                color: "#fff",
                border: "none",
                borderRadius: "8px",
                fontWeight: "600",
                marginBottom: "10px",
              }}
            >
              ‚ùå Stop Tour
            </button>

            <div style={{ marginTop: "10px" }}>
              <h3 style={{ color: "#1976d2" }}>üéØ Current Stop</h3>
              <p>
                <strong>{poiDetails[activeIndex]?.name}</strong>
                <br />
                {poiDetails[activeIndex]?.description}
              </p>

              {poiDetails[activeIndex]?.image && (
                <img
                  src={`http://localhost:5000/images/${poiDetails[activeIndex].image}`}
                  alt={poiDetails[activeIndex]?.name}
                  style={{
                    width: "100%",
                    borderRadius: "10px",
                    boxShadow: "0 4px 8px rgba(0,0,0,0.1)",
                    marginBottom: "10px",
                  }}
                />
              )}

              {nextPreview && (
                <>
                  <h4 style={{ marginTop: "16px", color: "#444" }}>Next Stop Preview ‚û°Ô∏è</h4>
                  <p>
                    <strong>{nextPreview.name}</strong>
                  </p>
                </>
              )}

              {nextPreview && (
                <button
                  onClick={handleNextStop}
                  style={{
                    width: "100%",
                    padding: "10px",
                    background: "#2196f3",
                    color: "#fff",
                    border: "none",
                    borderRadius: "8px",
                    fontWeight: "600",
                    marginTop: "12px",
                  }}
                >
                  ‚è≠Ô∏è Next Stop
                </button>
              )}
            </div>
          </>
        )}

        {!tracking && (
          <div style={{ marginTop: "20px" }}>
            {poiDetails.map((poi, i) => (
              <div key={i} style={{ marginBottom: "12px", borderBottom: "1px solid #ddd" }}>
                <h4>Stop {i + 1}: {poi?.name}</h4>
                <p>{poi?.description}</p>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
}
