import React, { useEffect, useState, useRef } from "react";
import { MapContainer, TileLayer, Marker, Popup, Polyline } from "react-leaflet";
import L from "leaflet";
import { useNavigate, useLocation } from "react-router-dom";
import "leaflet/dist/leaflet.css";

export default function RouteDetails() {
  const location = useLocation();
  const navigate = useNavigate();

  const filteredPois = location.state?.filteredPois || [];
  const transportMode = location.state?.transportMode || "driving-car";
  const startPoint = location.state?.startPoint || null;
  const routeId = location.state?.routeId || null;
  const user = JSON.parse(localStorage.getItem("user"));

  const [selectedPois, setSelectedPois] = useState(filteredPois);
  const [userLocation, setUserLocation] = useState(startPoint);
  const [routeToStop, setRouteToStop] = useState([]);
  const [activeIndex, setActiveIndex] = useState(0);
  const [tracking, setTracking] = useState(false);
  const [nextPreview, setNextPreview] = useState(null);
  const [likedPois, setLikedPois] = useState({});

  const watchIdRef = useRef(null);
  const ORS_API_KEY = "YOUR_ORS_API_KEY_HERE";

  // ---------- ICONS ----------
  const numberedIcon = (number, isActive = false) =>
    L.divIcon({
      html: `<div style="background:${isActive ? '#388e3c' : '#1976d2'}; color:#fff; width:28px; height:28px; border-radius:50%; text-align:center; line-height:28px; font-weight:600; border:2px solid #fff; box-shadow:0 2px 6px rgba(0,0,0,0.3);">${number}</div>`,
      className: "",
      iconSize: [28, 28],
    });

  const userIcon = L.divIcon({
    html: `<div style="background:#4caf50;color:#fff;width:30px;height:30px;border-radius:50%;text-align:center;line-height:30px;font-weight:700;border:2px solid #fff;box-shadow:0 2px 8px rgba(0,0,0,0.3);">U</div>`,
    className: "",
    iconSize: [30, 30],
  });

  // ---------- LOCATION ----------
  useEffect(() => {
    if (!navigator.geolocation) return;
    navigator.geolocation.getCurrentPosition(
      (pos) => setUserLocation({ lat: pos.coords.latitude, lng: pos.coords.longitude }),
      () => startPoint && setUserLocation(startPoint),
      { enableHighAccuracy: true }
    );
  }, [startPoint]);

  // ---------- SNAP TO ROAD ----------
  const snapToRoad = async (poi) => {
    try {
      const res = await fetch("https://api.openrouteservice.org/v2/snap/driving-car", {
        method: "POST",
        headers: { Authorization: ORS_API_KEY, "Content-Type": "application/json" },
        body: JSON.stringify({ coordinates: [[poi.lng, poi.lat]] }),
      });
      const data = await res.json();
      if (data.snappedLocations?.length) {
        const [lng, lat] = data.snappedLocations[0].location;
        return { lat, lng };
      }
      return poi;
    } catch {
      return poi;
    }
  };

  // ---------- FETCH ROUTE ----------
  const fetchRoute = async (from, to) => {
    if (!from || !to) return [];
    const snappedFrom = await snapToRoad(from);
    const snappedTo = await snapToRoad(to);
    try {
      const res = await fetch(`https://api.openrouteservice.org/v2/directions/${transportMode}/geojson`, {
        method: "POST",
        headers: { Authorization: ORS_API_KEY, "Content-Type": "application/json" },
        body: JSON.stringify({
          coordinates: [
            [snappedFrom.lng, snappedFrom.lat],
            [snappedTo.lng, snappedTo.lat],
          ],
          preference: "fastest",
          instructions: false,
        }),
      });
      const data = await res.json();
      if (data.features?.length) {
        return data.features[0].geometry.coordinates.map(([lng, lat]) => [lat, lng]);
      }
    } catch {}
    return [[from.lat, from.lng], [to.lat, to.lng]];
  };

  // ---------- START TOUR ----------
  const startTour = async () => {
    if (!userLocation || selectedPois.length === 0) return;
    setTracking(true);
    setActiveIndex(0);
    setNextPreview(selectedPois[1] || null);

    const firstStop = selectedPois[0];
    const initialRoute = await fetchRoute(userLocation, firstStop);
    setRouteToStop(initialRoute);

    watchIdRef.current = navigator.geolocation.watchPosition(
      async (pos) => {
        const current = { lat: pos.coords.latitude, lng: pos.coords.longitude };
        setUserLocation(current);
        const liveRoute = await fetchRoute(current, selectedPois[activeIndex]);
        setRouteToStop(liveRoute);
      },
      (err) => console.warn(err),
      { enableHighAccuracy: true, maximumAge: 2000, timeout: 10000 }
    );
  };

  // ---------- LIKE / DISLIKE ----------
  const handleLike = async (poiId, liked) => {
    try {
      if (!user?.token) return console.error("User not logged in");

      const res = await fetch("http://localhost:5000/api/user_feedback", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${user.token}`,
        },
        body: JSON.stringify({ poi_id: Number(poiId), liked }),
      });

      const data = await res.json();
      if (data.success) {
        setLikedPois((prev) => ({ ...prev, [poiId]: liked }));
      } else {
        console.error("Failed to save feedback:", data.message);
      }
    } catch (err) {
      console.error("Error sending feedback:", err);
    }
  };

  // ---------- REMOVE POI ----------
  const removePoi = async (poiId) => {
    await handleLike(poiId, 0); // Save as disliked
    setSelectedPois((prev) => prev.filter((p) => p.id !== poiId));
  };

  // ---------- NEXT STOP ----------
  const handleNextStop = async () => {
    if (activeIndex + 1 >= selectedPois.length) {
      stopTour("finished");
      alert("Tour completed!");
      return;
    }
    setActiveIndex((prev) => prev + 1);
    setNextPreview(selectedPois[activeIndex + 2] || null);

    if (userLocation) {
      const updatedRoute = await fetchRoute(userLocation, selectedPois[activeIndex + 1]);
      setRouteToStop(updatedRoute);
    }
  };

  // ---------- STOP TOUR ----------
  const stopTour = async (status = "cancelled") => {
    if (watchIdRef.current) navigator.geolocation.clearWatch(watchIdRef.current);
    setTracking(false);
    setRouteToStop([]);
    setActiveIndex(0);
    setNextPreview(null);

    if (routeId) {
      try {
        await fetch(`http://localhost:5000/api/itineraries/${routeId}/status`, {
          method: "PUT",
          headers: { "Content-Type": "application/json", Authorization: `Bearer ${user?.token}` },
          body: JSON.stringify({ status }),
        });
      } catch (err) {
        console.error("Error updating route status:", err);
      }
    }
  };

  if (!userLocation) return <p>Loading route...</p>;

  return (
    <div style={{ display: "grid", gridTemplateColumns: "1.3fr 0.7fr", gap: 24, padding: 24, minHeight: "100vh" }}>
      {/* MAP */}
      <div style={{ height: 650, borderRadius: 18, overflow: "hidden", boxShadow: "0 10px 25px rgba(0,0,0,0.15)" }}>
<MapContainer center={[userLocation.lat, userLocation.lng]} zoom={13} style={{ height: "100%", width: "100%" }}>
  <TileLayer url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png" attribution="¬© OpenStreetMap" />

  {/* User location */}
  <Marker position={[userLocation.lat, userLocation.lng]} icon={userIcon}>
    <Popup>You are here</Popup>
  </Marker>

  {/* Route line */}
  {routeToStop.length > 0 && <Polyline positions={routeToStop} color="#1976d2" weight={6} opacity={0.9} />}

  {/* POIs */}
  {selectedPois.map((poi, i) => (
    <Marker key={poi.id} position={[poi.lat, poi.lng]} icon={numberedIcon(i + 1, i === activeIndex)}>
      <Popup closeOnClick={false}>
        <div style={{ minWidth: 200 }}>
          <strong>{poi.name}</strong>
          <p>{poi.description || "No description available."}</p>
          {poi.image && (
            <img
              src={`http://localhost:5000/images/${poi.image}`}
              alt={poi.name}
              style={{ width: "100%", borderRadius: 8, marginBottom: 6 }}
            />
          )}

          {/* Like / Dislike buttons */}
          <div style={{ display: "flex", gap: 6 }}>
            <button
              onClick={() => handleLike(poi.id, 1)}
              style={{
                flex: 1,
                padding: 8,
                background: likedPois[poi.id] === 1 ? "#4caf50" : "#e0e0e0",
                color: likedPois[poi.id] === 1 ? "#fff" : "#000",
                border: "none",
                borderRadius: 6,
                cursor: "pointer",
                fontWeight: 600,
              }}
            >
              üëç Like
            </button>
            <button
              onClick={() => handleLike(poi.id, 0)}
              style={{
                flex: 1,
                padding: 8,
                background: likedPois[poi.id] === 0 ? "#f44336" : "#e0e0e0",
                color: likedPois[poi.id] === 0 ? "#fff" : "#000",
                border: "none",
                borderRadius: 6,
                cursor: "pointer",
                fontWeight: 600,
              }}
            >
              üëé Dislike
            </button>
          </div>

          {/* Remove POI */}
          <button
            onClick={() => removePoi(poi.id)}
            style={{
              marginTop: 8,
              width: "100%",
              padding: 8,
              background: "#9e9e9e",
              color: "#fff",
              border: "none",
              borderRadius: 6,
              fontWeight: 600,
              cursor: "pointer",
            }}
          >
            Remove POI
          </button>
        </div>
      </Popup>
    </Marker>
  ))}
</MapContainer>

      </div>

      {/* SIDEBAR */}
      <div style={{ background: "#fff", borderRadius: 18, padding: 24, boxShadow: "0 8px 24px rgba(0,0,0,0.1)", overflowY: "auto", maxHeight: 650 }}>
        {!tracking ? (
          <>
            <h2 style={{ color: "#1565c0", fontWeight: 700 }}>Selected POIs</h2>
            {selectedPois.map((poi, i) => (
              <div key={i} style={{ marginBottom: 18, borderBottom: "1px solid #eee", paddingBottom: 12 }}>
                <strong>{poi.name}</strong>
                <p>{poi.description || "No description available."}</p>
              </div>
            ))}
            <button onClick={startTour} style={{ width: "100%", padding: 12, background: "#4caf50", color: "#fff", border: "none", borderRadius: 8, fontWeight: 600, cursor: "pointer", marginTop: 16 }}>Start Tour</button>
          </>
        ) : (
          <>
            <h3 style={{ color: "#1976d2" }}>Now Visiting:</h3>
            <strong>{selectedPois[activeIndex]?.name}</strong>
            <p>{selectedPois[activeIndex]?.description}</p>
            {nextPreview && <p style={{ color: "#666" }}>Next: <strong>{nextPreview.name}</strong></p>}
            <button onClick={handleNextStop} style={{ width: "100%", padding: 12, background: "#1976d2", color: "#fff", border: "none", borderRadius: 8, fontWeight: 600, cursor: "pointer", marginTop: 12 }}>Next Stop</button>
            <button onClick={() => stopTour("finished")} style={{ width: "100%", padding: 12, background: "#1976d2", color: "#fff", border: "none", borderRadius: 8, fontWeight: 600, cursor: "pointer", marginTop: 12 }}>Finish Tour</button>
            <button onClick={() => stopTour("cancelled")} style={{ width: "100%", padding: 12, background: "#f44336", color: "#fff", border: "none", borderRadius: 8, fontWeight: 600, cursor: "pointer", marginTop: 10 }}>Cancel Tour</button>
          </>
        )}
        <button onClick={() => navigate("/home")} style={{ width: "100%", padding: 12, background: "#9e9e9e", color: "#fff", border: "none", borderRadius: 8, fontWeight: 600, cursor: "pointer", marginTop: 12 }}>Back to Home</button>
      </div>
    </div>
  );
}
