import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import polyline from "@mapbox/polyline";

const ORS_API_KEY =
  "eyJvcmciOiI1YjNjZTM1OTc4NTExMTAwMDFjZjYyNDgiLCJpZCI6ImJhNWU3NmIxOTRmMzQyYTg4NzY4MDc2MzVmZDY1ZDRhIiwiaCI6Im11cm11cjY0In0=";

export default function RoutePlanner() {
  const [pois, setPois] = useState([]);
  const [user, setUser] = useState(null);
  const [showPopup, setShowPopup] = useState(false);
  const [startTime, setStartTime] = useState("now");
  const [scheduledDate, setScheduledDate] = useState("");
  const [mode, setMode] = useState("car");
  const [userLocation, setUserLocation] = useState(null);
  const navigate = useNavigate();

  // ---------------- Load user and ML recommendations ----------------
  useEffect(() => {
    const storedUser = JSON.parse(localStorage.getItem("user"));
    setUser(storedUser);

    if (!storedUser) return;

    fetch("http://localhost:5000/api/recommendations", {
      headers: { Authorization: "Bearer " + localStorage.getItem("token") },
    })
      .then((res) => res.json())
      .then((data) => setPois(data.recommendations || []))
      .catch(() => setPois([]));

    if ("geolocation" in navigator) {
      navigator.geolocation.getCurrentPosition(
        (pos) =>
          setUserLocation({ lat: pos.coords.latitude, lng: pos.coords.longitude }),
        () => setUserLocation({ lat: 14.1461, lng: 121.5012 }) // fallback
      );
    } else {
      setUserLocation({ lat: 14.1461, lng: 121.5012 });
    }
  }, []);

  // ---------------- Get distance & route from ORS ----------------
  const getRouteInfo = async (start, end, mode) => {
    const res = await fetch(`https://api.openrouteservice.org/v2/directions/${mode}`, {
      method: "POST",
      headers: {
        Authorization: ORS_API_KEY,
        "Content-Type": "application/json",
      },
      body: JSON.stringify({ coordinates: [start, end] }),
    });

    if (!res.ok) throw new Error("Routing failed");
    const data = await res.json();
    const coords = polyline.decode(data.routes[0].geometry).map((c) => [c[0], c[1]]);
    return { distance: data.routes[0].summary.distance, coords };
  };

  // ---------------- Recommend route based on ML + distance ----------------
  const recommendRoute = async () => {
    if (!user || !user.preferences || user.preferences.length === 0) {
      alert("Set your preferences first in your profile.");
      return;
    }

    if (!userLocation) {
      alert("Waiting for current location...");
      return;
    }

    // Filter out POIs the user disliked (if you store dislikes in preferences)
    const likedPois = pois.filter((poi) => !user.dislikes?.includes(poi.id));

    const scored = await Promise.all(
      likedPois.map(async (poi) => {
        let score = poi.score || 0; // ML recommendation score

        try {
          const { distance } = await getRouteInfo(
            [userLocation.lng, userLocation.lat],
            [poi.lng, poi.lat],
            mode === "foot"
              ? "foot-walking"
              : mode === "bike"
              ? "cycling-regular"
              : "driving-car"
          );

          const km = distance / 1000;
          const distanceScore = mode === "foot" || mode === "bike" ? 10 / (km + 0.5) : 5 / (km + 1);
          score += distanceScore;

          return { ...poi, score };
        } catch {
          return { ...poi, score: 0 };
        }
      })
    );

    const sorted = scored.sort((a, b) => b.score - a.score);
    const selected = sorted.slice(0, 5);

    if (selected.length === 0) {
      alert("No POIs match your preferences.");
      return;
    }

    const itinerary = selected.map((poi) => ({ lat: poi.lat, lng: poi.lng, name: poi.name }));

    setShowPopup(false);

    navigate("/routedetails", {
      state: {
        startPoint: { lat: userLocation.lat, lng: userLocation.lng },
        itinerary,
        transportMode:
          mode === "foot"
            ? "foot-walking"
            : mode === "bike"
            ? "cycling-regular"
            : "driving-car",
        startTime: startTime === "now" ? new Date().toISOString() : scheduledDate,
      },
    });
  };

  // ---------------- Render ----------------
  return (
    <div style={{ textAlign: "center", marginTop: 40 }}>
      <button
        onClick={() => setShowPopup(true)}
        style={{
          padding: "12px 24px",
          background: "#2196f3",
          color: "#fff",
          border: "none",
          borderRadius: 8,
          fontSize: 16,
          cursor: "pointer",
        }}
      >
        Plan My Route
      </button>

      {showPopup && (
        <div
          style={{
            position: "fixed",
            top: 0,
            left: 0,
            width: "100%",
            height: "100%",
            background: "rgba(0,0,0,0.6)",
            display: "flex",
            alignItems: "center",
            justifyContent: "center",
            zIndex: 9999,
          }}
        >
          <div
            style={{
              background: "#fff",
              padding: 20,
              borderRadius: 10,
              width: 340,
              textAlign: "center",
              zIndex: 10000,
            }}
          >
            <h3 style={{ marginBottom: 12 }}>Plan Your Trip</h3>

            <label>
              <input
                type="radio"
                value="now"
                checked={startTime === "now"}
                onChange={() => setStartTime("now")}
              />{" "}
              Start Now
            </label>
            <br />
            <label>
              <input
                type="radio"
                value="later"
                checked={startTime === "later"}
                onChange={() => setStartTime("later")}
              />{" "}
              Start Later
            </label>

            {startTime === "later" && (
              <input
                type="datetime-local"
                value={scheduledDate}
                onChange={(e) => setScheduledDate(e.target.value)}
                style={{ marginTop: 8, display: "block", width: "100%" }}
              />
            )}

            <hr style={{ margin: "12px 0" }} />

            <label>Mode of Transport:</label>
            <select
              value={mode}
              onChange={(e) => setMode(e.target.value)}
              style={{ display: "block", margin: "8px auto", padding: 6 }}
            >
              <option value="foot">On Foot</option>
              <option value="bike">Bike</option>
              <option value="motor">Motor</option>
              <option value="car">Car</option>
            </select>

            <div style={{ marginTop: 16 }}>
              <button
                onClick={recommendRoute}
                style={{
                  marginRight: 8,
                  padding: "8px 16px",
                  background: "#3b82f6",
                  color: "#fff",
                  border: "none",
                  borderRadius: 6,
                  cursor: "pointer",
                }}
              >
                Confirm
              </button>
              <button
                onClick={() => setShowPopup(false)}
                style={{
                  padding: "8px 16px",
                  background: "#aaa",
                  color: "#fff",
                  border: "none",
                  borderRadius: 6,
                  cursor: "pointer",
                }}
              >
                Cancel
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}
