import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";

export default function RoutePlanner() {
  const [pois, setPois] = useState([]);
  const [user, setUser] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    // Load user from localStorage
    const storedUser = JSON.parse(localStorage.getItem("user"));
    setUser(storedUser);

    // Fetch POIs from backend
    fetch("http://localhost:5000/api/pois")
      .then(res => res.json())
      .then(data => setPois(data))
      .catch(err => {
        console.error("Error fetching POIs:", err);
        setPois([]);
      });
  }, []);

  // Haversine formula to calculate distance between two points
  const getDistance = (a, b) => {
    const R = 6371; // km
    const dLat = (b.lat - a.lat) * (Math.PI / 180);
    const dLng = (b.lng - a.lng) * (Math.PI / 180);
    const lat1 = a.lat * (Math.PI / 180);
    const lat2 = b.lat * (Math.PI / 180);
    const d = Math.sin(dLat / 2) ** 2 + Math.sin(dLng / 2) ** 2 * Math.cos(lat1) * Math.cos(lat2);
    const c = 2 * Math.atan2(Math.sqrt(d), Math.sqrt(1 - d));
    return R * c; // distance in km
  };

  const recommendRoute = () => {
    if (!user || !user.preferences || user.preferences.length === 0 || !user.location) {
      alert("Set your preferences and current location first in profile.");
      return;
    }

    // Convert preferences to lowercase
    const prefs = user.preferences.map(p => p.toLowerCase());

    // Score POIs based on preferences + distance
    const scoredPOIs = pois.map(poi => {
      let score = 0;

      // Preference match
      prefs.forEach(pref => {
        if (poi.tags?.toLowerCase().includes(pref)) score += 3; // weight 3
      });

      // Distance (closer POIs score higher)
      const distance = getDistance(user.location, poi);
      score += 2 / (distance + 0.1); // prevent divide by zero

      return { ...poi, score, distance };
    });

    // Sort POIs by score descending
    const sorted = scoredPOIs.sort((a, b) => b.score - a.score);

    // Optional: ensure category diversity
    const selected = [];
    const categoriesUsed = new Set();
    for (let poi of sorted) {
      const cat = poi.category?.toLowerCase() || "other";
      if (!categoriesUsed.has(cat)) {
        selected.push(poi);
        categoriesUsed.add(cat);
      }
      if (selected.length >= 4) break; // top 4
    }

    if (selected.length === 0) {
      alert("No POIs match your preferences.");
      return;
    }

    // Navigate to route details with selected POIs
    navigate("/routedetails", { state: { itinerary: selected } });
  };

  return (
    <div style={{ textAlign: "center", marginTop: 40 }}>
      <button
        onClick={recommendRoute}
        style={{
          padding: "12px 24px",
          background: "#2196f3",
          color: "#fff",
          border: "none",
          borderRadius: 8,
          fontSize: 16,
          cursor: "pointer"
        }}
      >
        Plan My Smart Route
      </button>
    </div>
  );
}
