import React from "react";
import { BrowserRouter as Router, Routes, Route, Navigate } from "react-router-dom";

import LoginPage from "../pages/LoginPage";
import SignupPage from "../pages/SignupPage";
import MapPage from "../pages/MapPage";
import AdminDashboard from "../pages/AdminDashboard";
import NatureCulturePage from "../pages/NatureCulture";
import EstablishmentsPage from "../pages/Establishments";
import RouteDetails from "../pages/RouteDetails";

// Fake auth for now – replace with your real auth logic
const getUserRole = () => {
  return localStorage.getItem("role"); // 'user', 'admin', or null
};

function ProtectedRoute({ children, role }) {
  const userRole = getUserRole();

  if (!userRole) {
    return <Navigate to="/login" replace />;
  }

  if (role && userRole !== role) {
    return <Navigate to="/login" replace />;
  }

  return children;
}

export default function App() {
  return (
    <Router>
      <Routes>
        {/* Auth Pages */}
        <Route path="/login" element={<LoginPage />} />
        <Route path="/signup" element={<SignupPage />} />

        {/* User Routes */}
        <Route
          path="/mappage"
          element={
            <ProtectedRoute role="user">
              <MapPage />
            </ProtectedRoute>
          }
        />
        <Route
          path="/natureculture"
          element={
            <ProtectedRoute role="user">
              <NatureCulturePage />
            </ProtectedRoute>
          }
        />
        <Route
          path="/establishments"
          element={
            <ProtectedRoute role="user">
              <EstablishmentsPage />
            </ProtectedRoute>
          }
        />
        <Route
          path="/routedetails"
          element={
            <ProtectedRoute role="user">
              <RouteDetails />
            </ProtectedRoute>
          }
        />

        {/* Admin Route */}
        <Route
          path="/admindashboard"
          element={
            <ProtectedRoute role="admin">
              <AdminDashboard />
            </ProtectedRoute>
          }
        />

        {/* Default Route */}
        <Route path="*" element={<Navigate to="/login" replace />} />
      </Routes>
    </Router>
  );
}
