import React, { useEffect, useState } from "react";
import Papa from "papaparse";

export default function EstablishmentsPage() {
  const [lodgings, setLodgings] = useState([]);
  const [restaurants, setRestaurants] = useState([]);

  useEffect(() => {
    fetch("/data/spots.csv")
      .then((res) => res.text())
      .then((csv) => {
        Papa.parse(csv, {
          header: true,
          skipEmptyLines: true,
          complete: (res) => {
            const rows = res.data.map((r) => ({
              ...r,
              tags: r.tags ? r.tags.split(",").map((t) => t.trim().toLowerCase()) : [],
              image: r.image ? `/images/${r.image.replace(/^\/+/, "").split("/").pop()}` : null,
              description: r.description || "No description available",
            }));

            // filter lodgings: lodge, motel, resort, hotel
            setLodgings(
              rows.filter((r) =>
                r.tags.some((tag) =>
                  ["lodge", "resort", "motel", "hotel"].includes(tag)
                )
              )
            );

            // filter restaurants: restaurant, coffee, bistro, cafe
            setRestaurants(
              rows.filter((r) =>
                r.tags.some((tag) =>
                  ["restaurant", "coffee", "bistro", "cafe"].includes(tag)
                )
              )
            );
          },
        });
      });
  }, []);

  const renderCards = (items) => (
    <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-6">
      {items.map((item, i) => (
        <div
          key={i}
          className="relative rounded-xl shadow-lg overflow-hidden bg-white transform transition-transform duration-500 hover:scale-105"
        >
          {item.image ? (
            <img
              src={item.image}
              alt={item.name}
              className="h-48 w-full object-cover"
            />
          ) : (
            <div className="h-48 w-full flex items-center justify-center bg-gray-100 text-gray-500">
              No image
            </div>
          )}
          <div className="p-4">
            <h3 className="text-lg font-semibold">{item.name}</h3>
            <p className="text-sm text-gray-600">{item.description}</p>
          </div>
          <div className="absolute bottom-2 left-2 text-xs text-gray-700 bg-white/80 px-2 py-1 rounded">
            {item.tags.join(", ")}
          </div>
        </div>
      ))}
    </div>
  );

  return (
    <div className="p-6">
      <h1 className="text-2xl font-bold mb-6">Establishments</h1>

      <section className="mb-10">
        <h2 className="text-xl font-semibold mb-4">Lodgings</h2>
        {lodgings.length > 0 ? renderCards(lodgings) : <p>No lodgings found.</p>}
      </section>

      <section>
        <h2 className="text-xl font-semibold mb-4">Restaurants</h2>
        {restaurants.length > 0 ? renderCards(restaurants) : <p>No restaurants found.</p>}
      </section>
    </div>
  );
}
