import React, { useEffect, useState, useRef } from "react";
import { MapContainer, TileLayer, Marker, Popup, Polyline } from "react-leaflet";
import "leaflet/dist/leaflet.css";
import L from "leaflet";
import { useNavigate, useLocation } from "react-router-dom";

export default function RouteDetails() {
  const location = useLocation();
  const navigate = useNavigate();
  const selectedPois = location.state?.itinerary || [];

  const [poiDetails, setPoiDetails] = useState([]);
  const [userLocation, setUserLocation] = useState(null);
  const [tracking, setTracking] = useState(false);
  const [activeIndex, setActiveIndex] = useState(0);
  const [nextPreview, setNextPreview] = useState(null);
  const [routeToStop, setRouteToStop] = useState([]);
  const [itineraryId, setItineraryId] = useState(location.state?.itineraryId || null);
  const [status, setStatus] = useState("pending");
  const [saveMessage, setSaveMessage] = useState("");

  const mapRef = useRef(null);
  const watchIdRef = useRef(null);

  const ORS_API_KEY =
    "eyJvcmciOiI1YjNjZTM1OTc4NTExMTAwMDFjZjYyNDgiLCJpZCI6ImJhNWU3NmIxOTRmMzQyYTg4NzY4MDc2MzVmZDY1ZDRhIiwiaCI6Im11cm11cjY0In0=";

  // ---------- ICONS ----------
  const numberedIcon = (number, isActive = false) =>
    L.divIcon({
      html: `<div style="
        background:${isActive ? '#388e3c' : '#1976d2'};
        color:#fff;
        width:28px;
        height:28px;
        border-radius:50%;
        text-align:center;
        line-height:28px;
        font-weight:600;
        border:2px solid #fff;
        box-shadow:0 2px 6px rgba(0,0,0,0.3);
      ">${number}</div>`,
      className: "",
      iconSize: [28, 28],
    });

  const userIcon = L.divIcon({
    html: `<div style="
      background:#4caf50;
      color:#fff;
      width:30px;
      height:30px;
      border-radius:50%;
      text-align:center;
      line-height:30px;
      font-weight:700;
      border:2px solid #fff;
      box-shadow:0 2px 8px rgba(0,0,0,0.3);
    ">U</div>`,
    className: "",
    iconSize: [30, 30],
  });

  // ---------- LOCATION ----------
  useEffect(() => {
    if (!navigator.geolocation) return;
    navigator.geolocation.getCurrentPosition(
      (pos) => setUserLocation({ lat: pos.coords.latitude, lng: pos.coords.longitude }),
      (err) => console.warn("Location error:", err),
      { enableHighAccuracy: true }
    );
  }, []);

  // ---------- FETCH FULL POI DETAILS ----------
  const fetchPoiDetails = async () => {
    try {
      const res = await fetch("http://localhost:5000/api/pois");
      const allPois = await res.json();

      // Match selected POIs with full details
      const matched = selectedPois.map((poi) =>
        allPois.find((p) => p.name === poi.name || p.id === poi.id)
      );

      setPoiDetails(matched.filter(Boolean)); // remove any nulls
    } catch (err) {
      console.error("Error fetching POI details:", err);
    }
  };

  useEffect(() => {
    fetchPoiDetails();
  }, [selectedPois]);

  // ---------- FETCH ROUTE ----------
  const fetchRoute = async (from, to) => {
    if (!from || !to) return [];
    try {
      const res = await fetch("https://api.openrouteservice.org/v2/directions/driving-car/geojson", {
        method: "POST",
        headers: { Authorization: ORS_API_KEY, "Content-Type": "application/json" },
        body: JSON.stringify({
          coordinates: [
            [from.lng, from.lat],
            [to.lng, to.lat],
          ],
          preference: "fastest",
          instructions: false,
        }),
      });
      const data = await res.json();
      if (data.features?.length) {
        return data.features[0].geometry.coordinates.map(([lng, lat]) => [lat, lng]);
      }
    } catch {
      return [[from.lat, from.lng], [to.lat, to.lng]];
    }
    return [];
  };

  // ---------- TOUR CONTROL ----------
  const startTour = async () => {
    if (!userLocation || poiDetails.length === 0) return;

    setTracking(true);
    setActiveIndex(0);
    setNextPreview(poiDetails[1] || null);

    const initialRoute = await fetchRoute(userLocation, poiDetails[0]);
    setRouteToStop(initialRoute);

    watchIdRef.current = navigator.geolocation.watchPosition(
      async (pos) => {
        const current = { lat: pos.coords.latitude, lng: pos.coords.longitude };
        setUserLocation(current);
        const liveRoute = await fetchRoute(current, poiDetails[activeIndex]);
        setRouteToStop(liveRoute);
      },
      (err) => console.warn("Watch error:", err),
      { enableHighAccuracy: true, maximumAge: 2000, timeout: 10000 }
    );
  };

  const handleNextStop = async () => {
    if (activeIndex + 1 >= poiDetails.length) {
      stopTour();
      alert("Tour completed!");
      return;
    }
    const nextStop = poiDetails[activeIndex + 1];
    setActiveIndex((prev) => prev + 1);
    setNextPreview(poiDetails[activeIndex + 2] || null);

    if (userLocation) {
      const updatedRoute = await fetchRoute(userLocation, nextStop);
      setRouteToStop(updatedRoute);
    }
  };

  const stopTour = () => {
    if (watchIdRef.current) navigator.geolocation.clearWatch(watchIdRef.current);
    setTracking(false);
    setRouteToStop([]);
    setActiveIndex(0);
    setNextPreview(null);
  };

  if (!poiDetails.length || !userLocation) return <p>Loading route...</p>;

  return (
    <div style={{ display: "grid", gridTemplateColumns: "1.3fr 0.7fr", gap: "24px", padding: "24px", minHeight: "100vh" }}>
      {/* MAP */}
      <div style={{ height: "650px", borderRadius: "18px", overflow: "hidden", boxShadow: "0 10px 25px rgba(0,0,0,0.15)", border: "4px solid #fff" }}>
        <MapContainer center={[userLocation.lat, userLocation.lng]} zoom={13} style={{ height: "100%", width: "100%" }}>
          <TileLayer url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png" attribution="Â© OpenStreetMap" />
          <Marker position={[userLocation.lat, userLocation.lng]} icon={userIcon}>
            <Popup>You are here</Popup>
          </Marker>

          {routeToStop.length > 0 && <Polyline positions={routeToStop} color="#1976d2" weight={6} opacity={0.9} />}

          {poiDetails.map((poi, i) => {
            const isActive = i === activeIndex;
            return (
              <Marker key={i} position={[poi.lat, poi.lng]} icon={numberedIcon(i + 1, isActive)}>
                <Popup>
                  <strong>Stop {i + 1}: {poi.name}</strong>
                  <p>{poi.description}</p>
                  {poi.image && <img src={`http://localhost:5000/images/${poi.image}`} alt={poi.name} style={{ width: "160px", borderRadius: "8px", marginTop: "6px" }} />}
                </Popup>
              </Marker>
            );
          })}
        </MapContainer>
      </div>

      {/* SIDEBAR */}
      <div style={{ background: "#fff", borderRadius: "18px", padding: "24px", boxShadow: "0 8px 24px rgba(0,0,0,0.1)", border: "1px solid #e0e0e0", overflowY: "auto", maxHeight: "650px" }}>
        {!tracking ? (
          <>
            <h2 style={{ color: "#1565c0", fontWeight: "700" }}>Route Overview</h2>
            {poiDetails.map((poi, i) => (
              <div key={i} style={{ marginBottom: "18px", borderBottom: "1px solid #eee", paddingBottom: "12px" }}>
                <h4 style={{ color: "#1976d2" }}>Stop {i + 1}: {poi.name}</h4>
                {poi.image && <img src={`http://localhost:5000/images/${poi.image}`} alt={poi.name} style={{ width: "100%", borderRadius: "8px", marginBottom: "8px" }} />}
                <p style={{ color: "#555" }}>{poi.description}</p>
              </div>
            ))}
            <button onClick={startTour} style={{ width: "100%", padding: "12px 20px", background: "#4caf50", color: "#fff", border: "none", borderRadius: "8px", fontWeight: "600", cursor: "pointer" }}>
              Start Tour
            </button>
          </>
        ) : (
          <>
            <h3 style={{ color: "#1976d2" }}>Now Visiting:</h3>
            <div style={{ marginBottom: "12px" }}>
              <strong>{poiDetails[activeIndex].name}</strong>
              <p>{poiDetails[activeIndex].description}</p>
              {poiDetails[activeIndex].image && <img src={`http://localhost:5000/images/${poiDetails[activeIndex].image}`} alt={poiDetails[activeIndex].name} style={{ width: "100%", borderRadius: "8px" }} />}
            </div>

            {nextPreview ? (
              <>
                <p style={{ color: "#666" }}>Next: <strong>{nextPreview.name}</strong></p>
                <button onClick={handleNextStop} style={{ width: "100%", padding: "12px 20px", background: "#1976d2", color: "#fff", border: "none", borderRadius: "8px", fontWeight: "600", cursor: "pointer", marginTop: "12px" }}>Next Stop</button>
              </>
            ) : (
              <button onClick={stopTour} style={{ width: "100%", padding: "12px 20px", background: "#1976d2", color: "#fff", border: "none", borderRadius: "8px", fontWeight: "600", cursor: "pointer", marginTop: "12px" }}>Finish Tour</button>
            )}

            <button onClick={stopTour} style={{ width: "100%", padding: "12px 20px", background: "#f44336", color: "#fff", border: "none", borderRadius: "8px", fontWeight: "600", cursor: "pointer", marginTop: "10px" }}>Cancel Tour</button>
          </>
        )}
      </div>
    </div>
  );
}
