import React, { useEffect, useState } from "react";

export default function NatureCulturePage() {
  const [places, setPlaces] = useState([]);
  const [selectedPlace, setSelectedPlace] = useState(null);

  useEffect(() => {
    fetch("http://localhost:5000/api/spots/nature-culture")
      .then((res) => res.json())
      .then((data) => {
        setPlaces(data);
      })
      .catch((err) => console.error("Error fetching spots:", err));
  }, []);

  return (
    <div style={{ padding: "24px" }}>
      <h1 style={{ marginBottom: "24px", fontSize: "1.8rem", fontWeight: "bold" }}>
        Nature & Culture Spots
      </h1>

      <div
        style={{
          display: "grid",
          gridTemplateColumns: "repeat(auto-fill, minmax(250px, 1fr))",
          gap: "16px",
        }}
      >
        {places.map((p) => (
          <div
            key={p.id}
            onClick={() => setSelectedPlace(p)}
            style={{
              cursor: "pointer",
              border: "1px solid #ddd",
              borderRadius: "16px",
              overflow: "hidden",
              background: "#fff",
              boxShadow: "0 2px 6px rgba(0,0,0,0.1)",
              transition: "transform 0.2s",
            }}
          >
            {p.image ? (
              <img
                src={p.image}
                alt={p.name}
                style={{ height: "180px", width: "100%", objectFit: "cover" }}
              />
            ) : (
              <div
                style={{
                  height: "180px",
                  display: "flex",
                  alignItems: "center",
                  justifyContent: "center",
                  background: "#eee",
                  color: "#666",
                }}
              >
                No image
              </div>
            )}
            <div style={{ padding: "12px" }}>
              <h3 style={{ fontWeight: "bold", marginBottom: "4px" }}>{p.name}</h3>
              <p style={{ fontSize: "0.9rem", color: "#888" }}>{p.tags}</p>
            </div>
          </div>
        ))}
      </div>

      {selectedPlace && (
        <div
          onClick={() => setSelectedPlace(null)}
          style={{
            position: "fixed",
            top: 0,
            left: 0,
            width: "100vw",
            height: "100vh",
            background: "rgba(0,0,0,0.5)",
            display: "flex",
            alignItems: "center",
            justifyContent: "center",
            zIndex: 1000,
          }}
        >
          <div
            onClick={(e) => e.stopPropagation()}
            style={{
              background: "#fff",
              borderRadius: "16px",
              maxWidth: "600px",
              width: "90%",
              maxHeight: "90vh",
              overflowY: "auto",
              padding: "24px",
            }}
          >
            {selectedPlace.image && (
              <img
                src={selectedPlace.image}
                alt={selectedPlace.name}
                style={{
                  width: "100%",
                  height: "300px",
                  objectFit: "cover",
                  borderRadius: "12px",
                  marginBottom: "16px",
                }}
              />
            )}
            <h2 style={{ fontSize: "1.5rem", fontWeight: "bold", marginBottom: "8px" }}>
              {selectedPlace.name}
            </h2>
            <p style={{ color: "#888", marginBottom: "12px" }}>{selectedPlace.tags}</p>
            <p style={{ marginBottom: "12px" }}>{selectedPlace.description}</p>
            <p>Visitors: {selectedPlace.visitors || "Not available"}</p>
            <button
              onClick={() => setSelectedPlace(null)}
              style={{
                marginTop: "16px",
                padding: "10px 20px",
                background: "#007bff",
                color: "#fff",
                border: "none",
                borderRadius: "8px",
                cursor: "pointer",
              }}
            >
              Close
            </button>
          </div>
        </div>
      )}
    </div>
  );
}
