import React, { useEffect, useState } from "react";
import { MapContainer, TileLayer, Marker, Popup, Polyline } from "react-leaflet";
import "leaflet/dist/leaflet.css";
import { useNavigate, useLocation } from "react-router-dom";

export default function RouteDetails() {
  const location = useLocation();
  const navigate = useNavigate();
  const routeSpots = location.state?.itinerary || [];
  const [currentIndex, setCurrentIndex] = useState(0);
  const [routeData, setRouteData] = useState([]);
  const [loading, setLoading] = useState(true);

  const ORS_API_KEY = "YOUR_ORS_API_KEY"; // Replace with your OpenRouteService API key

  // Fetch route segment between two POIs following roads
  const fetchRouteSegment = async (from, to) => {
    const profile = "driving-car"; // use "foot-hiking" for trails

    try {
      const res = await fetch(`https://api.openrouteservice.org/v2/directions/${profile}/geojson`, {
        method: "POST",
        headers: {
          "Authorization": ORS_API_KEY,
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          coordinates: [
            [from.lng, from.lat],
            [to.lng, to.lat],
          ],
        }),
      });

      const data = await res.json();
      if (!data.features || !data.features.length) throw new Error("No route found");

      const coords = data.features[0].geometry.coordinates.map(([lng, lat]) => [lat, lng]);
      const summary = data.features[0].properties.summary; // { distance, duration }

      return { coords, summary };
    } catch (err) {
      console.error("Route fetch failed:", err);
      return { coords: [[from.lat, from.lng], [to.lat, to.lng]], summary: { distance: 0, duration: 0 } };
    }
  };

  // Build all route segments
  useEffect(() => {
    const buildRoutes = async () => {
      const segments = [];
      for (let i = 0; i < routeSpots.length - 1; i++) {
        const segment = await fetchRouteSegment(routeSpots[i], routeSpots[i + 1]);
        segments.push(segment);
      }
      setRouteData(segments);
      setLoading(false);
    };

    if (routeSpots.length > 1) buildRoutes();
    else setLoading(false);
  }, [routeSpots]);

  const nextPOI = () => {
    if (currentIndex < routeSpots.length - 1) setCurrentIndex(currentIndex + 1);
    else alert("You have reached the end of the route!");
  };

  const finishRoute = () => {
    if (window.confirm("Are you sure you want to finish the route?")) {
      alert("Route completed!");
      navigate("/home");
    }
  };

  const cancelRoute = () => {
    if (window.confirm("Are you sure you want to cancel the route? You will lose progress.")) {
      alert("Route cancelled.");
      navigate("/home");
    }
  };

  const currentPOI = routeSpots[currentIndex];
  const nextSegment = routeData[currentIndex]; // segment from current POI to next POI

  if (!routeSpots.length) return <p>No route available.</p>;

  const formatDistance = (meters) => `${(meters / 1000).toFixed(2)} km`;
  const formatDuration = (seconds) => `${Math.floor(seconds / 60)} min`;

  return (
    <div style={{ display: "grid", gridTemplateColumns: "1.2fr 0.8fr", gap: 20, padding: 20, minHeight: "100vh", background: "linear-gradient(135deg, #e0f7fa, #f1f8e9)" }}>
      <div style={{ height: "600px", borderRadius: 12, overflow: "hidden", boxShadow: "0 6px 18px rgba(0,0,0,0.15)" }}>
        {loading ? (
          <p style={{ textAlign: "center", marginTop: "40%" }}>Loading route...</p>
        ) : (
          <MapContainer center={[currentPOI.lat, currentPOI.lng]} zoom={14} style={{ height: "100%", width: "100%" }}>
            <TileLayer
              attribution='&copy; <a href="https://www.openstreetmap.org/">OSM contributors</a>'
              url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
            />

            {nextSegment && <Polyline positions={nextSegment.coords} color="blue" />}

            {routeSpots.filter(spot => spot.lat && spot.lng).map((spot, i) => (
              <Marker key={i} position={[spot.lat, spot.lng]}>
                <Popup>
                  <strong>{spot.name}</strong>
                  <p>{spot.description}</p>
                  {spot.image && <img src={spot.image} alt={spot.name} style={{ width: "160px", borderRadius: 8, marginTop: 6 }} />}
                </Popup>
              </Marker>
            ))}
          </MapContainer>
        )}
      </div>

      <div style={{ background: "#fff", borderRadius: 12, padding: 20, boxShadow: "0 6px 18px rgba(0,0,0,0.12)" }}>
        <h2 style={{ margin: "0 0 12px 0", color: "#0d47a1" }}>Current POI</h2>
        <p><strong>{currentPOI.name}</strong></p>
        <p>{currentPOI.description}</p>
        {currentPOI.image && (
          <img
            src={currentPOI.image}
            alt={currentPOI.name}
            style={{ width: "100%", maxWidth: 280, borderRadius: 10, boxShadow: "0 6px 14px rgba(0,0,0,0.2)", transition: "transform 0.3s ease" }}
            onMouseOver={e => (e.currentTarget.style.transform = "scale(1.05)")}
            onMouseOut={e => (e.currentTarget.style.transform = "scale(1)")}
          />
        )}
        {nextSegment && (
          <p style={{ marginTop: 10 }}>
            Distance to next POI: {formatDistance(nextSegment.summary.distance)} <br />
            Estimated time: {formatDuration(nextSegment.summary.duration)}
          </p>
        )}
        <p style={{ marginTop: 20, fontStyle: "italic" }}>POI {currentIndex + 1} of {routeSpots.length}</p>
      </div>

      <div style={{ position: "fixed", bottom: 0, left: 0, right: 0, background: "linear-gradient(90deg, #2196f3, #42a5f5)", padding: "12px 20px", display: "flex", justifyContent: "center", gap: 16, zIndex: 1000 }}>
        {currentIndex < routeSpots.length - 1 && (
          <button onClick={nextPOI} style={{ padding: "10px 20px", border: "none", borderRadius: 8, background: "#4caf50", color: "#fff", fontWeight: 600, cursor: "pointer" }}>Next POI</button>
        )}
        <button onClick={cancelRoute} style={{ padding: "10px 20px", border: "none", borderRadius: 8, background: "#f44336", color: "#fff", fontWeight: 600, cursor: "pointer" }}>Cancel Route</button>
        {currentIndex === routeSpots.length - 1 && (
          <button onClick={finishRoute} style={{ padding: "10px 20px", border: "none", borderRadius: 8, background: "#ff9800", color: "#fff", fontWeight: 600, cursor: "pointer" }}>Finish Route</button>
        )}
      </div>
    </div>
  );
}
