import React, { useState, useEffect } from "react";

export default function AdminPreferences() {
  const [users, setUsers] = useState([]);
  const [search, setSearch] = useState("");
  const [filter, setFilter] = useState("");

  useEffect(() => {
    fetch("http://localhost:5000/api/users")
      .then(res => res.json())
      .then(data => {
        // Calculate top preference for each user
        const usersWithTopPref = data.map(u => {
          let topPreference = "-";
          if (u.preferences && u.preferences.length > 0) {
            const counts = {};
            u.preferences.forEach(pref => {
              counts[pref] = (counts[pref] || 0) + 1;
            });
            topPreference = Object.keys(counts).reduce((a, b) => counts[a] > counts[b] ? a : b);
          }
          return { ...u, topPreference };
        });
        setUsers(usersWithTopPref);
      })
      .catch(err => console.error(err));
  }, []);

  const filteredUsers = users
    .filter(u => u.username.toLowerCase().includes(search.toLowerCase()))
    .filter(u => !filter || u.topPreference === filter);

  const resetPreferences = (userId) => {
    fetch(`http://localhost:5000/api/users/${userId}/preferences`, {
      method: "PUT",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ preferences: [] }),
    })
      .then(() => {
        setUsers(users.map(u => u.id === userId ? { ...u, preferences: [], topPreference: "-" } : u));
      })
      .catch(err => console.error(err));
  };

  return (
    <div style={{ marginLeft: 220, padding: 20 }}>
      <h2>Admin Preferences</h2>
      <div style={{ marginBottom: 16 }}>
        <input
          placeholder="Search user..."
          value={search}
          onChange={e => setSearch(e.target.value)}
          style={{ padding: 6, marginRight: 8 }}
        />
        <input
          placeholder="Filter by top preference..."
          value={filter}
          onChange={e => setFilter(e.target.value)}
          style={{ padding: 6 }}
        />
      </div>
      <table style={{ width: "100%", borderCollapse: "collapse" }}>
        <thead>
          <tr>
            <th style={{ border: "1px solid #ccc", padding: 8 }}>Username</th>
            <th style={{ border: "1px solid #ccc", padding: 8 }}>Email</th>
            <th style={{ border: "1px solid #ccc", padding: 8 }}>Top Preference</th>
            <th style={{ border: "1px solid #ccc", padding: 8 }}>Actions</th>
          </tr>
        </thead>
        <tbody>
          {filteredUsers.map(u => (
            <tr key={u.id}>
              <td style={{ border: "1px solid #ccc", padding: 8 }}>{u.username}</td>
              <td style={{ border: "1px solid #ccc", padding: 8 }}>{u.email}</td>
              <td style={{ border: "1px solid #ccc", padding: 8 }}>{u.topPreference}</td>
              <td style={{ border: "1px solid #ccc", padding: 8 }}>
                <button onClick={() => resetPreferences(u.id)}>Reset</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}
