import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";

export default function RoutePlanner() {
  const [pois, setPois] = useState([]);              // All POIs
  const [displayedPois, setDisplayedPois] = useState([]); // Selected + ML recommendations
  const [selectedKeywords, setSelectedKeywords] = useState([]);
  const [selectedPois, setSelectedPois] = useState([]);
  const [showPopup, setShowPopup] = useState(false);
  const [user, setUser] = useState(null);
  const navigate = useNavigate();

  // Load all POIs and user info
  useEffect(() => {
    const storedUser = JSON.parse(localStorage.getItem("user"));
    setUser(storedUser);

    fetch("http://localhost:5000/api/pois")
      .then((res) => res.json())
      .then((data) => setPois(data))
      .catch(() => setPois([]));
  }, []);

  // Call ML recommendation endpoint whenever keywords change
  useEffect(() => {
    if (!user) return;
    const query = selectedKeywords.join(",");
    fetch(`http://localhost:5000/api/recommended_pois?user_id=${user.id}&keywords=${query}`)
      .then((res) => res.json())
      .then((recommended) => {
        // Merge selected POIs + recommended POIs, remove duplicates
        const merged = [
          ...new Map([...selectedPois, ...recommended].map((p) => [p.id, p])).values(),
        ];
        setDisplayedPois(merged);
      })
      .catch(() => {
        // Fallback: just show selected POIs
        setDisplayedPois([...selectedPois]);
      });
  }, [selectedKeywords, selectedPois, user]);

  const toggleKeyword = (kw) => {
    setSelectedKeywords((prev) =>
      prev.includes(kw) ? prev.filter((k) => k !== kw) : [...prev, kw]
    );
  };

  const togglePoi = (poi) => {
    if (poi.removed) return; // cannot select removed
    setSelectedPois((prev) =>
      prev.includes(poi) ? prev.filter((p) => p !== poi) : [...prev, poi]
    );
  };

  const recommendRoute = () => {
    if (selectedPois.length === 0) {
      alert("Please select at least one POI.");
      return;
    }
    navigate("/routedetails", {
      state: {
        startPoint: null,
        filteredPois: selectedPois,
        transportMode: "driving-car",
        startTime: new Date().toISOString(),
      },
    });
    setShowPopup(false);
  };

  return (
    <div style={{ textAlign: "center", marginTop: 40 }}>
      <button
        onClick={() => setShowPopup(true)}
        style={{
          padding: "14px 28px",
          background: "#1976d2",
          color: "#fff",
          border: "none",
          borderRadius: 10,
          fontSize: 18,
          cursor: "pointer",
        }}
      >
        Plan My Route
      </button>

      {showPopup && (
        <div
          style={{
            position: "fixed",
            top: 0,
            left: 0,
            width: "100%",
            height: "100%",
            background: "rgba(0,0,0,0.7)",
            display: "flex",
            alignItems: "center",
            justifyContent: "center",
            zIndex: 9999,
          }}
        >
          <div
            style={{
              background: "#fff",
              padding: 24,
              borderRadius: 12,
              width: "90%",
              maxWidth: 600,
              maxHeight: "80vh",
              overflowY: "auto",
              textAlign: "left",
            }}
          >
            <h2 style={{ textAlign: "center", color: "#1565c0" }}>Select POIs</h2>

            {/* Keywords */}
            <div style={{ marginBottom: 16 }}>
              <p style={{ fontWeight: 600 }}>Select Keywords:</p>
              {["relax", "coffee", "nature", "adventure", "food"].map((kw) => (
                <button
                  key={kw}
                  onClick={() => toggleKeyword(kw)}
                  style={{
                    margin: 4,
                    padding: "6px 12px",
                    borderRadius: 6,
                    border: selectedKeywords.includes(kw)
                      ? "2px solid #1976d2"
                      : "1px solid #aaa",
                    background: selectedKeywords.includes(kw) ? "#bbdefb" : "#f0f0f0",
                    cursor: "pointer",
                  }}
                >
                  {kw}
                </button>
              ))}
            </div>

            {/* POIs List */}
            <div>
              {displayedPois.map((poi) => {
                const isSelected = selectedPois.includes(poi);
                const isRemoved = poi.removed; // removed flag from backend
                return (
                  <div
                    key={poi.id}
                    style={{
                      marginBottom: 12,
                      padding: 10,
                      borderRadius: 8,
                      border: "1px solid #ddd",
                      background: isRemoved
                        ? "#eee"
                        : isSelected
                        ? "#e3f2fd"
                        : "#fff",
                      cursor: isRemoved ? "not-allowed" : "pointer",
                    }}
                    onClick={() => togglePoi(poi)}
                  >
                    <strong>{poi.name}</strong>
                    <p style={{ margin: "4px 0", color: "#555" }}>{poi.description}</p>
                    <small style={{ color: "#999" }}>{poi.tags}</small>
                  </div>
                );
              })}
            </div>

            <div style={{ marginTop: 20, textAlign: "center" }}>
              <button
                onClick={recommendRoute}
                style={{
                  padding: "10px 20px",
                  marginRight: 8,
                  background: "#4caf50",
                  color: "#fff",
                  border: "none",
                  borderRadius: 8,
                  cursor: "pointer",
                  fontWeight: 600,
                }}
              >
                Confirm Selection
              </button>

              <button
                onClick={() => setShowPopup(false)}
                style={{
                  padding: "10px 20px",
                  background: "#f44336",
                  color: "#fff",
                  border: "none",
                  borderRadius: 8,
                  cursor: "pointer",
                  fontWeight: 600,
                }}
              >
                Cancel
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}
