import React, { useEffect, useState } from "react";
import { MapContainer, TileLayer, Marker, Popup } from "react-leaflet";
import "leaflet/dist/leaflet.css";
import "leaflet-routing-machine/dist/leaflet-routing-machine.css";
import L from "leaflet";
import "leaflet-routing-machine";
import Papa from "papaparse";

const CENTER = { lat: 14.1467, lng: 121.4708 };

export default function Home() {
  const [spots, setSpots] = useState([]);
  const [route, setRoute] = useState([]);
  const [userPreferences, setUserPreferences] = useState({
    preferNature: true,
    preferCulture: false,
  });

  // Fetch CSV data
  useEffect(() => {
    fetch("/data/spots.csv")
      .then((res) => res.text())
      .then((csv) => {
        Papa.parse(csv, {
          header: true,
          skipEmptyLines: true,
          complete: (results) => {
            const rows = results.data
              .filter((r) => r.name && r.lat && r.lng)
              .map((r) => ({
                name: r.name.trim(),
                lat: parseFloat(r.lat),
                lng: parseFloat(r.lng),
                visitors: r.visitors.trim() || "Moderate",
                image: r.image || null,
                description: r.description || "No description",
                type: r.tags || "nature",
              }));
            setSpots(rows);
          },
        });
      })
      .catch((error) => console.error("Error loading CSV:", error));
  }, []);

  // Generate Route with ML predictions
  const generateRoute = async () => {
    if (!spots.length) return alert("No spots available!");

    try {
      const res = await fetch("http://localhost:5000/predict", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          spots,
          userPreferences,
          weather: { weather: [{ main: "Clear" }] }, // dummy weather
        }),
      });
      const data = await res.json();
      console.log("ML Server route:", data.route);
      setRoute(data.route);
    } catch (error) {
      console.error("ML server not reachable. Using default scoring.", error);

      // Fallback: sort by original visitor frequency
      const visitorRank = { "Very High": 3, High: 2, Moderate: 1, Low: 0 };
      const sorted = [...spots].sort(
        (a, b) => visitorRank[b.visitors] - visitorRank[a.visitors]
      );
      setRoute(sorted);
    }
  };

  // Draw route using Leaflet Routing Machine
  useEffect(() => {
    if (!route.length || !window.map) return;

    // Remove existing route
    if (window.routingControl) window.routingControl.remove();

    const waypoints = route.map((spot) => L.latLng(spot.lat, spot.lng));

    window.routingControl = L.Routing.control({
      waypoints,
      lineOptions: { styles: [{ color: "blue", weight: 4 }] },
      show: false,
      addWaypoints: false,
      draggableWaypoints: false,
      routeWhileDragging: false,
    }).addTo(window.map);
  }, [route]);

  return (
    <div style={{ display: "grid", gap: 16, gridTemplateColumns: "1fr 1fr" }}>
      <div className="card">
        <h2>Majayjay Overview</h2>
        <div className="map-wrap" style={{ height: "500px" }}>
          {spots.length > 0 ? (
            <MapContainer
              center={CENTER}
              zoom={13}
              style={{ height: "100%", width: "100%" }}
              whenCreated={(mapInstance) => (window.map = mapInstance)}
            >
              <TileLayer
                attribution='&copy; <a href="https://www.openstreetmap.org/">OSM contributors</a>'
                url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
              />
              {route.length > 0
                ? route.map((s, i) => (
                    <Marker key={i} position={[s.lat, s.lng]}>
                      <Popup>
                        <strong>{s.name}</strong>
                        <br />
                        Visitors: {s.visitors} <br />
                        {s.image && (
                          <img
                            src={s.image}
                            alt={s.name}
                            style={{
                              width: "150px",
                              borderRadius: "8px",
                              marginTop: "4px",
                            }}
                          />
                        )}
                        <p>{s.description}</p>
                      </Popup>
                    </Marker>
                  ))
                : spots.map((s, i) => (
                    <Marker key={i} position={[s.lat, s.lng]}>
                      <Popup>
                        <strong>{s.name}</strong>
                        <br />
                        Visitors: {s.visitors} <br />
                        {s.image && (
                          <img
                            src={s.image}
                            alt={s.name}
                            style={{
                              width: "150px",
                              borderRadius: "8px",
                              marginTop: "4px",
                            }}
                          />
                        )}
                        <p>{s.description}</p>
                      </Popup>
                    </Marker>
                  ))}
            </MapContainer>
          ) : (
            <div>No spots available to display</div>
          )}
        </div>
      </div>

      <div className="card">
        <h2>Most Visited Places</h2>
        <ul style={{ margin: 0, paddingLeft: 18 }}>
          {route.length > 0
            ? route.map((s, i) => (
                <li key={i}>
                  <strong>{s.name}</strong> — {s.visitors} visitors
                  {s.predicted_frequency
                    ? ` (Predicted: ${s.predicted_frequency})`
                    : ""}
                </li>
              ))
            : spots.map((s, i) => (
                <li key={i}>
                  <strong>{s.name}</strong> — {s.visitors} visitors
                </li>
              ))}
        </ul>
        <button onClick={generateRoute}>Generate Route</button>
      </div>
    </div>
  );
}
