import React, { useEffect, useState } from "react";
import { MapContainer, TileLayer, Marker, Popup, Polyline } from "react-leaflet";
import "leaflet/dist/leaflet.css";
import { useNavigate, useLocation } from "react-router-dom";

export default function RouteDetails() {
  const location = useLocation();
  const navigate = useNavigate();
  const routeSpots = location.state?.itinerary || [];
  const [currentIndex, setCurrentIndex] = useState(0);
  const [currentSegment, setCurrentSegment] = useState(null);
  const [loading, setLoading] = useState(true);

  const ORS_API_KEY = "eyJvcmciOiI1YjNjZTM1OTc4NTExMTAwMDFjZjYyNDgiLCJpZCI6ImJhNWU3NmIxOTRmMzQyYTg4NzY4MDc2MzVmZDY1ZDRhIiwiaCI6Im11cm11cjY0In0=";

  // Fetch the route for the current segment (current POI -> next POI)
  const fetchCurrentSegment = async () => {
    if (currentIndex >= routeSpots.length - 1) {
      setCurrentSegment(null);
      setLoading(false);
      return;
    }

    const from = routeSpots[currentIndex];
    const to = routeSpots[currentIndex + 1];

    try {
      const res = await fetch("https://api.openrouteservice.org/v2/directions/driving-car/geojson", {
        method: "POST",
        headers: {
          "Authorization": ORS_API_KEY,
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          coordinates: [
            [from.lng, from.lat],
            [to.lng, to.lat],
          ],
        }),
      });

      const data = await res.json();
      if (!data.features || !data.features.length) throw new Error("No route found");

      const coords = data.features[0].geometry.coordinates.map(([lng, lat]) => [lat, lng]);
      const summary = data.features[0].properties.summary; // { distance, duration }

      setCurrentSegment({ coords, summary });
    } catch (err) {
      console.error("Route fetch failed:", err);
      setCurrentSegment({
        coords: [[routeSpots[currentIndex].lat, routeSpots[currentIndex].lng], [routeSpots[currentIndex + 1].lat, routeSpots[currentIndex + 1].lng]],
        summary: { distance: 0, duration: 0 },
      });
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    if (routeSpots.length > 1) {
      setLoading(true);
      fetchCurrentSegment();
    } else {
      setLoading(false);
    }
  }, [currentIndex, routeSpots]);

  const nextPOI = () => {
    if (currentIndex < routeSpots.length - 1) setCurrentIndex(currentIndex + 1);
    else alert("You have reached the end of the route!");
  };

  const finishRoute = () => {
    if (window.confirm("Finish route?")) {
      alert("Route completed!");
      navigate("/home");
    }
  };

  const cancelRoute = () => {
    if (window.confirm("Cancel route?")) {
      alert("Route cancelled.");
      navigate("/home");
    }
  };

  if (!routeSpots.length) return <p>No route available.</p>;

  const currentPOI = routeSpots[currentIndex];
  const formatDistance = (meters) => `${(meters / 1000).toFixed(2)} km`;
  const formatDuration = (seconds) => `${Math.floor(seconds / 60)} min`;

  return (
    <div style={{ display: "grid", gridTemplateColumns: "1.2fr 0.8fr", gap: 20, padding: 20, minHeight: "100vh", background: "linear-gradient(135deg, #e0f7fa, #f1f8e9)" }}>
      <div style={{ height: "600px", borderRadius: 12, overflow: "hidden", boxShadow: "0 6px 18px rgba(0,0,0,0.15)" }}>
        {loading ? (
          <p style={{ textAlign: "center", marginTop: "40%" }}>Loading route...</p>
        ) : (
          <MapContainer center={[currentPOI.lat, currentPOI.lng]} zoom={14} style={{ height: "100%", width: "100%" }}>
            <TileLayer
              attribution='&copy; <a href="https://www.openstreetmap.org/">OSM contributors</a>'
              url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
            />

            {/* Draw route from current POI to next POI only */}
            {nextSegment && <Polyline positions={nextSegment.coords} color="blue" />}

            {/* Show only current and next POI */}
            {[currentPOI, routeSpots[currentIndex + 1]].filter(Boolean).map((poi, i) => (
              <Marker key={i} position={[poi.lat, poi.lng]}>
                <Popup>
                  <strong>{poi.name}</strong>
                  <p>{poi.description}</p>
                  {poi.image && <img src={poi.image} alt={poi.name} style={{ width: "160px", borderRadius: 8, marginTop: 6 }} />}
                </Popup>
              </Marker>
            ))}
          </MapContainer>
        )}
      </div>

      <div style={{ background: "#fff", borderRadius: 12, padding: 20, boxShadow: "0 6px 18px rgba(0,0,0,0.12)" }}>
        <h2 style={{ margin: "0 0 12px 0", color: "#0d47a1" }}>Current POI</h2>
        <p><strong>{currentPOI.name}</strong></p>
        <p>{currentPOI.description}</p>
        {currentPOI.image && <img src={currentPOI.image} alt={currentPOI.name} style={{ width: "100%", maxWidth: 280, borderRadius: 10, boxShadow: "0 6px 14px rgba(0,0,0,0.2)" }} />}

        {currentSegment && (
          <p style={{ marginTop: 10 }}>
            Distance to next POI: {formatDistance(currentSegment.summary.distance)} <br />
            Estimated time: {formatDuration(currentSegment.summary.duration)}
          </p>
        )}

        <p style={{ marginTop: 20, fontStyle: "italic" }}>POI {currentIndex + 1} of {routeSpots.length}</p>
      </div>

      <div style={{ position: "fixed", bottom: 0, left: 0, right: 0, background: "linear-gradient(90deg, #2196f3, #42a5f5)", padding: "12px 20px", display: "flex", justifyContent: "center", gap: 16, zIndex: 1000 }}>
        {currentIndex < routeSpots.length - 1 && <button onClick={nextPOI} style={{ padding: "10px 20px", border: "none", borderRadius: 8, background: "#4caf50", color: "#fff", fontWeight: 600, cursor: "pointer" }}>Next POI</button>}
        <button onClick={cancelRoute} style={{ padding: "10px 20px", border: "none", borderRadius: 8, background: "#f44336", color: "#fff", fontWeight: 600, cursor: "pointer" }}>Cancel Route</button>
        {currentIndex === routeSpots.length - 1 && <button onClick={finishRoute} style={{ padding: "10px 20px", border: "none", borderRadius: 8, background: "#ff9800", color: "#fff", fontWeight: 600, cursor: "pointer" }}>Finish Route</button>}
      </div>
    </div>
  );
}
