import React, { useEffect, useState, useRef } from "react";
import { MapContainer, TileLayer, Marker, useMap } from "react-leaflet";
import "leaflet/dist/leaflet.css";
import L from "leaflet";

delete L.Icon.Default.prototype._getIconUrl;
L.Icon.Default.mergeOptions({
  iconRetinaUrl: require("leaflet/dist/images/marker-icon-2x.png"),
  iconUrl: require("leaflet/dist/images/marker-icon.png"),
  shadowUrl: require("leaflet/dist/images/marker-shadow.png"),
});

function ZoomToLocation({ selectedPoi }) {
  const map = useMap();
  useEffect(() => {
    if (selectedPoi) map.flyTo([selectedPoi.lat, selectedPoi.lng], 16, { duration: 1 });
  }, [selectedPoi, map]);
  return null;
}

export default function AdminPOIsMap() {
  const [pois, setPois] = useState([]);
  const [selectedPoi, setSelectedPoi] = useState(null);
  const [addingPoi, setAddingPoi] = useState(false); // true when waiting for map click
  const [imagePreview, setImagePreview] = useState(null);
  const token = localStorage.getItem("token");
  const fileInputRef = useRef(null);
  const [mapCenter] = useState([14.1619, 121.4626]);
  const mapRef = useRef(null);

  // fetch POIs
  useEffect(() => {
    fetch("http://localhost:5000/api/pois", {
      headers: { Authorization: `Bearer ${token}` },
    })
      .then((res) => res.json())
      .then((data) => setPois(Array.isArray(data) ? data : []))
      .catch((err) => console.error(err));
  }, [token]);

  // handle image preview for selectedPoi
  useEffect(() => {
    if (!selectedPoi) {
      setImagePreview(null);
      return;
    }
    if (selectedPoi.imageFile) {
      setImagePreview(URL.createObjectURL(selectedPoi.imageFile));
    } else if (selectedPoi.image) {
      setImagePreview(`http://localhost:5000/images/${selectedPoi.image}`);
    } else {
      setImagePreview(null);
    }
  }, [selectedPoi]);

  // map click: called by React Leaflet onClick prop (so it always sees latest state)
  const handleMapClick = (e) => {
    if (!addingPoi) return;
    // place the temporary POI and open right-side editor
    setSelectedPoi({
      lat: e.latlng.lat,
      lng: e.latlng.lng,
      name: "",
      tags: "",
      description: "",
      visitors: "Moderate",
      isNew: true,
    });
    setAddingPoi(false); // exit add mode after placing
  };

  // handle cropping/drag end for the temp marker (we use Marker draggable event)
  // implemented inline in JSX for readability

  // image selection
  const handleImageUpload = (e) => {
    const file = e.target.files[0];
    if (!file || !selectedPoi) return;
    setSelectedPoi({ ...selectedPoi, imageFile: file });
  };

  // upload image helper
  const uploadImage = async (file) => {
    if (!file) return null;
    const fd = new FormData();
    fd.append("image", file);
    const res = await fetch("http://localhost:5000/api/upload-image", {
      method: "POST",
      headers: { Authorization: `Bearer ${token}` },
      body: fd,
    });
    if (!res.ok) throw new Error("Image upload failed");
    const data = await res.json();
    return data.filename;
  };

  // save POI (create or update)
  const savePoi = async () => {
    if (!selectedPoi) return;
    try {
      const poiToSave = { ...selectedPoi };

      if (selectedPoi.imageFile) {
        const filename = await uploadImage(selectedPoi.imageFile);
        poiToSave.image = filename;
        delete poiToSave.imageFile;
      }

      if (selectedPoi.isNew) {
        const res = await fetch("http://localhost:5000/api/pois", {
          method: "POST",
          headers: { "Content-Type": "application/json", Authorization: `Bearer ${token}` },
          body: JSON.stringify(poiToSave),
        });
        if (!res.ok) throw new Error("Failed to create POI");
        const savedPoi = await res.json();
        setPois((prev) => [...prev, savedPoi]);
      } else {
        const res = await fetch(`http://localhost:5000/api/pois/${selectedPoi.id}`, {
          method: "PUT",
          headers: { "Content-Type": "application/json", Authorization: `Bearer ${token}` },
          body: JSON.stringify(poiToSave),
        });
        if (!res.ok) throw new Error("Failed to update POI");
        // update local list
        setPois((prev) => prev.map((p) => (p.id === selectedPoi.id ? { ...p, ...poiToSave } : p)));
      }

      setSelectedPoi(null);
      setImagePreview(null);
      if (fileInputRef.current) fileInputRef.current.value = "";
      // small visual confirmation (simple)
      window.alert("POI saved successfully.");
    } catch (err) {
      console.error(err);
      window.alert("Error saving POI: " + err.message);
    }
  };

  // delete POI
  const deletePoi = async (id) => {
    if (!window.confirm("Delete this POI?")) return;
    try {
      const res = await fetch(`http://localhost:5000/api/pois/${id}`, {
        method: "DELETE",
        headers: { Authorization: `Bearer ${token}` },
      });
      if (!res.ok) throw new Error("Failed to delete POI");
      setPois((prev) => prev.filter((p) => p.id !== id));
      if (selectedPoi?.id === id) setSelectedPoi(null);
      window.alert("POI deleted.");
    } catch (err) {
      console.error(err);
      window.alert("Error deleting POI: " + err.message);
    }
  };

  // helper: start add mode
  const startAddMode = () => {
    setAddingPoi(true);
    setSelectedPoi(null);
    // let user know
    // you can remove the alert if you prefer a toast or other UI
    // window.alert("Click on the map to place a new POI marker. Drag the marker to adjust before saving.");
  };

  // UI: we will shift the whole content to the right (left navbar area kept)
  const leftNavWidth = 740; // px
  const extraShift = 16; // extra to ensure no overlap
  const totalLeftShift = leftNavWidth + extraShift; // 90px from earlier conversation

  return (
    <div style={{ display: "flex", height: "100vh" }}>
      {/* left navbar placeholder (kept small as you requested) */}
      <div style={{ width: `${leftNavWidth}px`, background: "#333" }} />

      {/* main container shifted to the right to avoid overlap with navbar */}
      <div
        style={{
          flex: 1,
          display: "flex",
          flexDirection: "column",
          marginLeft: `${totalLeftShift}px`,
          position: "relative",
        }}
      >
        {/* Add POI button - top-right inside map container */}
        <button
          onClick={startAddMode}
          style={{
            position: "absolute",
            top: 12,
            right: 20,
            zIndex: 1100,
            background: addingPoi ? "#ff9800" : "#4caf50",
            color: "#fff",
            border: "none",
            padding: "10px 14px",
            borderRadius: 6,
            cursor: "pointer",
            boxShadow: "0 2px 6px rgba(0,0,0,0.2)",
          }}
        >
          {addingPoi ? "Click map to place POI" : "‚ûï Add POI"}
        </button>

        {/* Map area */}
        <div style={{ flex: 1 }}>
          <MapContainer
            center={mapCenter}
            zoom={14}
            style={{
              height: "100%",
              width: "100%",
              cursor: addingPoi ? "crosshair" : "auto", // visual cue
            }}
            whenCreated={(mapInstance) => (mapRef.current = mapInstance)}
            onClick={handleMapClick} // React-aware handler ‚Äî fixes stale-state issues
          >
            <TileLayer
              attribution='&copy; OpenStreetMap contributors'
              url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
            />

            <ZoomToLocation selectedPoi={selectedPoi} />

            {/* existing POIs */}
            {pois.map((p) => (
              <Marker
                key={p.id}
                position={[p.lat, p.lng]}
                eventHandlers={{
                  click: () => {
                    setSelectedPoi({ ...p, isNew: false });
                    // ensure add mode off
                    setAddingPoi(false);
                  },
                }}
              />
            ))}

            {/* temporary draggable marker */}
            {selectedPoi?.isNew && (
              <Marker
                position={[selectedPoi.lat, selectedPoi.lng]}
                draggable
                eventHandlers={{
                  dragend: (e) => {
                    const { lat, lng } = e.target.getLatLng();
                    setSelectedPoi((prev) => ({ ...prev, lat, lng }));
                  },
                }}
              />
            )}
          </MapContainer>
        </div>

        {/* bottom POI list */}
        <div
          style={{
            height: 250,
            overflowY: "auto",
            background: "#fff",
            borderTop: "1px solid #ccc",
            padding: "12px 18px",
          }}
        >
          <h3 style={{ marginBottom: 10 }}>üìã Points of Interest</h3>
          {pois.length === 0 ? (
            <p style={{ color: "#666" }}>No POIs found.</p>
          ) : (
            <table style={{ width: "100%", borderCollapse: "collapse" }}>
              <thead>
                <tr style={{ background: "#f7f7f7", textAlign: "left" }}>
                  <th style={{ padding: 8 }}>Name</th>
                  <th style={{ padding: 8 }}>Visitors</th>
                  <th style={{ padding: 8 }}>Tags</th>
                  <th style={{ padding: 8 }}>Description</th>
                </tr>
              </thead>
              <tbody>
                {pois.map((poi) => (
                  <tr
                    key={poi.id}
                    onClick={() => {
                      setSelectedPoi({ ...poi, isNew: false });
                      setAddingPoi(false);
                    }}
                    style={{
                      cursor: "pointer",
                      borderBottom: "1px solid #eee",
                      background: selectedPoi?.id === poi.id ? "#e8f4ff" : "transparent",
                    }}
                  >
                    <td style={{ padding: "6px 8px" }}>{poi.name}</td>
                    <td style={{ padding: "6px 8px" }}>{poi.visitors}</td>
                    <td style={{ padding: "6px 8px" }}>{poi.tags}</td>
                    <td style={{ padding: "6px 8px" }}>{poi.description}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          )}
        </div>
      </div>

      {/* right-side editor / add panel */}
      {selectedPoi && (
        <div
          style={{
            width: 350,
            background: "#fff",
            borderLeft: "1px solid #ccc",
            padding: 18,
            overflowY: "auto",
          }}
        >
          <h3 style={{ marginTop: 0 }}>{selectedPoi.isNew ? "Add New POI" : "Edit POI"}</h3>

          <label style={{ fontSize: 12, color: "#444" }}>Name</label>
          <input
            type="text"
            placeholder="Name"
            value={selectedPoi.name}
            onChange={(e) => setSelectedPoi({ ...selectedPoi, name: e.target.value })}
            style={{ width: "100%", marginBottom: 10 }}
          />

          <label style={{ fontSize: 12, color: "#444" }}>Tags</label>
          <input
            type="text"
            placeholder="Tags (comma separated)"
            value={selectedPoi.tags}
            onChange={(e) => setSelectedPoi({ ...selectedPoi, tags: e.target.value })}
            style={{ width: "100%", marginBottom: 10 }}
          />

          <label style={{ fontSize: 12, color: "#444" }}>Description</label>
          <textarea
            placeholder="Description"
            value={selectedPoi.description}
            onChange={(e) => setSelectedPoi({ ...selectedPoi, description: e.target.value })}
            style={{ width: "100%", marginBottom: 10 }}
          />

          <label style={{ fontSize: 12, color: "#444" }}>Image</label>
          <input ref={fileInputRef} type="file" onChange={handleImageUpload} style={{ width: "100%", marginBottom: 10 }} />
          {imagePreview && <img src={imagePreview} alt="Preview" style={{ width: "100%", borderRadius: 6, marginBottom: 10 }} />}

          <label style={{ fontSize: 12, color: "#444" }}>Visitors</label>
          <select
            value={selectedPoi.visitors}
            onChange={(e) => setSelectedPoi({ ...selectedPoi, visitors: e.target.value })}
            style={{ width: "100%", marginBottom: 12 }}
          >
            <option>Very High</option>
            <option>High</option>
            <option>Moderate</option>
            <option>Low</option>
          </select>

          <div style={{ display: "flex", gap: 8 }}>
            <button onClick={savePoi} style={{ flex: 1, padding: "8px 10px" }}>
              üíæ Save
            </button>
            <button onClick={() => setSelectedPoi(null)} style={{ flex: 1, padding: "8px 10px" }}>
              ‚ùå Cancel
            </button>
            {!selectedPoi.isNew && (
              <button onClick={() => deletePoi(selectedPoi.id)} style={{ background: "#f44336", color: "#fff", padding: "8px 10px" }}>
                üóë Delete
              </button>
            )}
          </div>
        </div>
      )}
    </div>
  );
}
