import React, { useEffect, useState, useRef } from "react";
import { MapContainer, TileLayer, Marker, Popup } from "react-leaflet";
import "leaflet/dist/leaflet.css";
import WeatherWidget from "./components/WeatherWidget";
import RoutePlanner from "./RoutePlanner";

const CENTER = { lat: 14.1467, lng: 121.4708 };

export default function Home() {
  const [spots, setSpots] = useState([]);
  const [route, setRoute] = useState([]);
  const [weather, setWeather] = useState("Sunny");
  const mapRef = useRef(null);
  const markerRefs = useRef({});

  useEffect(() => {
    fetch("http://localhost:5000/api/pois")
      .then((res) => res.json())
      .then((data) => {
        const rows = data
          .filter((r) => r.name && r.lat && r.lng)
          .map((r) => ({
            name: r.name,
            lat: parseFloat(r.lat),
            lng: parseFloat(r.lng),
            description: r.description || "No description available",
            visitors: r.visitors || 0,
            tags: r.tags ? r.tags.split(",").map((t) => t.trim()) : [],
            image: r.image
              ? `/images/${r.image.replace(/^\/+/, "").split("/").pop()}`
              : null,
          }));

        // Sort by visitors descending and take top 10
        const top10 = rows.sort((a, b) => b.visitors - a.visitors).slice(0, 10);
        setSpots(top10);
      })
      .catch((err) => console.error("Error fetching POIs:", err));
  }, []);

  const focusOnSpot = (spot) => {
    if (mapRef.current) mapRef.current.flyTo([spot.lat, spot.lng], 15, { duration: 1.5 });
    if (markerRefs.current[spot.name]) markerRefs.current[spot.name].openPopup();
  };

  return (
    <div style={{ display: "grid", gridTemplateColumns: "1fr 1fr", gap: "20px", padding: "16px" }}>
      <div style={{ background: "#fff", borderRadius: "12px", padding: "16px", boxShadow: "0 4px 10px rgba(0,0,0,0.1)" }}>
        <h2 style={{ fontSize: "20px", fontWeight: "bold", marginBottom: "12px" }}>Majayjay Map</h2>

        <WeatherWidget setWeather={setWeather} />

        <RoutePlanner spots={spots} setRoute={setRoute} weather={weather} />

        <div style={{ height: "500px", borderRadius: "10px", overflow: "hidden", marginTop: "12px" }}>
          {spots.length > 0 ? (
            <MapContainer
              center={CENTER}
              zoom={13}
              style={{ height: "100%", width: "100%" }}
              whenCreated={(mapInstance) => (mapRef.current = mapInstance)}
            >
              <TileLayer
                attribution='&copy; <a href="https://www.openstreetmap.org/">OSM contributors</a>'
                url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
              />
              {(route.length ? route : spots).map((spot, i) => (
                <Marker
                  key={i}
                  position={[spot.lat, spot.lng]}
                  ref={(ref) => { if (ref) markerRefs.current[spot.name] = ref; }}
                  eventHandlers={{ click: () => focusOnSpot(spot) }}
                >
                  <Popup>
                    <strong>{spot.name}</strong>
                    <br />
                    Visitors: {spot.visitors}
                    {spot.image && <br />}
                    {spot.image && <img src={spot.image} alt={spot.name} style={{ width: "150px", borderRadius: "8px", marginTop: "6px" }} />}
                    <p>{spot.description}</p>
                  </Popup>
                </Marker>
              ))}
            </MapContainer>
          ) : (
            <div style={{ textAlign: "center", color: "#888", padding: "20px" }}>No spots available</div>
          )}
        </div>
      </div>

      <div style={{ background: "#fff", borderRadius: "12px", padding: "16px", boxShadow: "0 4px 10px rgba(0,0,0,0.1)" }}>
        <h2 style={{ fontSize: "20px", fontWeight: "bold", marginBottom: "12px" }}>Top Places</h2>
        <ul style={{ paddingLeft: "20px", color: "#444" }}>
          {(route.length ? route : spots).map((spot, i) => (
            <li key={i} style={{ marginBottom: "8px", cursor: "pointer" }} onClick={() => focusOnSpot(spot)}>
              <strong>{spot.name}</strong> â€” {spot.visitors} visitors
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
}
