import React, { useEffect, useState } from "react";
import Papa from "papaparse";

export default function NatureCulturePage() {
  const [places, setPlaces] = useState([]);
  const [expandedIndex, setExpandedIndex] = useState(null);

  useEffect(() => {
    fetch("/data/spots.csv")
      .then((res) => res.text())
      .then((csv) => {
        Papa.parse(csv, {
          header: true,
          skipEmptyLines: true,
          complete: (res) => {
            const rows = res.data
              .filter((r) => r.name && r.tags)
              .filter((r) =>
                r.tags.toLowerCase().includes("nature") ||
                r.tags.toLowerCase().includes("culture")
              )
              .map((r) => ({ ...r, image: r.image || null }));
            setPlaces(rows);
          },
        });
      });
  }, []);

  const toggleExpand = (index) => {
    setExpandedIndex(expandedIndex === index ? null : index);
  };

  return (
    <div style={{ padding: "24px" }}>
      <div
        style={{
          display: "grid",
          gridTemplateColumns: "repeat(3, 1fr)",
          gap: "16px",
        }}
      >
        {places.map((p, i) => {
          const isExpanded = expandedIndex === i;
          return (
            <div
              key={i}
              onClick={() => toggleExpand(i)}
              style={{
                cursor: "pointer",
                border: "1px solid #ddd",
                borderRadius: "16px",
                overflow: "hidden",
                background: "#fff",
                boxShadow: isExpanded ? "0 8px 20px rgba(0,0,0,0.2)" : "0 2px 6px rgba(0,0,0,0.1)",
                transition: "all 0.3s",
                gridColumn: isExpanded ? "span 3" : "auto",
              }}
            >
              {p.image ? (
                <img
                  src={p.image}
                  alt={p.name}
                  style={{
                    height: isExpanded ? "300px" : "180px",
                    width: "100%",
                    objectFit: "cover",
                    transition: "all 0.3s",
                  }}
                />
              ) : (
                <div
                  style={{
                    height: isExpanded ? "300px" : "180px",
                    display: "flex",
                    alignItems: "center",
                    justifyContent: "center",
                    background: "#eee",
                    color: "#666",
                    transition: "all 0.3s",
                  }}
                >
                  No image
                </div>
              )}
              <div style={{ padding: "12px" }}>
                <h3 style={{ fontWeight: "bold", marginBottom: "4px" }}>{p.name}</h3>
                <p style={{ fontSize: "0.9rem", color: "#888", marginBottom: "8px" }}>{p.tags}</p>
                {isExpanded && (
                  <div style={{ marginTop: "8px", color: "#555" }}>
                    <p>{p.description}</p>
                    <p>Opening Hours: {p.opening || "Not available"}</p>
                    <p>Contact: {p.contact || "Not available"}</p>
                  </div>
                )}
              </div>
            </div>
          );
        })}
      </div>
    </div>
  );
}
