import React, { useEffect, useState, useRef } from "react";
import { MapContainer, TileLayer, Marker, Popup, Polyline } from "react-leaflet";
import L from "leaflet";
import { useNavigate, useLocation } from "react-router-dom";
import "leaflet/dist/leaflet.css";

const ORS_API_KEY = process.env.REACT_APP_ORS_KEY;

// ---------- Marker icon ----------
const createColoredIcon = (color) =>
  new L.Icon({
    iconUrl: `https://raw.githubusercontent.com/pointhi/leaflet-color-markers/master/img/marker-icon-${color}.png`,
    shadowUrl:
      "https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-shadow.png",
    iconSize: [25, 41],
    iconAnchor: [12, 41],
    popupAnchor: [1, -34],
    shadowSize: [41, 41],
  });

// ---------- Map categories to colors ----------
const CATEGORY_COLOR = {
  "Nature & Outdoors": "green",
  "Culture & Heritage": "yellow",
  "Farming & Agriculture": "orange",
  "Food & Drink": "blue",
  "Accommodation & Stay": "red",
};

// ---------- Determine category ----------
const determineCategory = (tags = "") => {
  const t = tags.toLowerCase().split(",").map((x) => x.trim());
  if (t.some((x) => ["history", "culture", "religion", "architecture", "spiritual"].includes(x)))
    return "Culture & Heritage";
  if (t.some((x) => ["farm", "agri", "organic", "animal"].includes(x))) return "Farming & Agriculture";
  if (t.some((x) => ["resort", "motel", "hotel", "lodge"].includes(x))) return "Accommodation & Stay";
  if (t.some((x) => ["restaurant", "coffee", "food"].includes(x))) return "Food & Drink";
  return "Nature & Outdoors";
};

export default function RouteDetails() {
  const location = useLocation();
  const navigate = useNavigate();

  const filteredPois = location.state?.filteredPois || [];
  const recommendedPois = location.state?.recommendedPois || [];
  const startPoint = location.state?.startPoint || null;
  const user = JSON.parse(localStorage.getItem("user"));

  const [userLocation, setUserLocation] = useState(startPoint);
  const [selectedPois, setSelectedPois] = useState(filteredPois);
  const [activeIndex, setActiveIndex] = useState(0);
  const [tracking, setTracking] = useState(false);
  const [likedPois, setLikedPois] = useState({});
  const [routeCoords, setRouteCoords] = useState([]); // ORS polyline coordinates
  const [draggedIndex, setDraggedIndex] = useState(null);

  const watchIdRef = useRef(null);

  // ---------- Get user location ----------
  useEffect(() => {
    if (!startPoint && "geolocation" in navigator) {
      navigator.geolocation.getCurrentPosition(
        (pos) =>
          setUserLocation({
            lat: pos.coords.latitude,
            lng: pos.coords.longitude,
          }),
        () => setUserLocation({ lat: 14.1461, lng: 121.5012 })
      );
    }
  }, [startPoint]);

  // ---------- Fetch ORS Route ----------
  useEffect(() => {
    const fetchRoute = async () => {
      if (!userLocation || selectedPois.length === 0) return;

      const coords = [[userLocation.lng, userLocation.lat], ...selectedPois.map(p => [p.lng, p.lat])];
      try {
        const res = await fetch("https://api.openrouteservice.org/v2/directions/driving-car/geojson", {
          method: "POST",
          headers: {
            "Authorization": ORS_API_KEY,
            "Content-Type": "application/json"
          },
          body: JSON.stringify({ coordinates: coords })
        });
        const data = await res.json();
        if (data.features?.[0]?.geometry?.coordinates) {
          // ORS returns [lng, lat], Leaflet expects [lat, lng]
          const lineCoords = data.features[0].geometry.coordinates.map(c => [c[1], c[0]]);
          setRouteCoords(lineCoords);
        }
      } catch (err) {
        console.error("Failed to fetch ORS route:", err);
      }
    };
    fetchRoute();
  }, [userLocation, selectedPois]);

  // ---------- LIKE / DISLIKE ----------
  const handleLike = async (poiId, liked) => {
    try {
      const token = user?.token;
      if (!token) return;

      const response = await fetch("http://localhost:5000/api/user_feedback", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify({ poi_id: poiId, liked }),
      });

      const data = await response.json();
      if (data.success) setLikedPois((prev) => ({ ...prev, [poiId]: liked }));
    } catch (err) {
      console.error("Error sending feedback:", err);
    }
  };

  // ---------- STOP TOUR ----------
  const stopTour = () => {
    if (watchIdRef.current) navigator.geolocation.clearWatch(watchIdRef.current);
    setTracking(false);
    setActiveIndex(0);
  };

  // ---------- NEXT STOP ----------
  const handleNextStop = () => {
    if (activeIndex + 1 < selectedPois.length) {
      setActiveIndex(prev => prev + 1);
    } else {
      stopTour();
      alert("Tour finished!");
    }
  };

  // ---------- Drag and Drop ----------
  const handleDragStart = (index) => setDraggedIndex(index);
  const handleDrop = (index) => {
    if (draggedIndex === null) return;
    const items = [...selectedPois];
    const [removed] = items.splice(draggedIndex, 1);
    items.splice(index, 0, removed);
    setSelectedPois(items);
    setDraggedIndex(null);
  };

  if (!userLocation) return <p>Loading route...</p>;

  return (
    <div style={{ display: "grid", gridTemplateColumns: "1.3fr 0.7fr", gap: 24, padding: 24 }}>
      {/* ---------- Map ---------- */}
      <div style={{ height: 650, borderRadius: 18, overflow: "hidden" }}>
        <MapContainer center={[userLocation.lat, userLocation.lng]} zoom={13} style={{ height: "100%", width: "100%" }}>
          <TileLayer url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png" />
          {/* User marker */}
          <Marker position={[userLocation.lat, userLocation.lng]}>
            <Popup>Your Location</Popup>
          </Marker>
          {/* POIs */}
          {selectedPois.map((poi, i) => (
            <Marker
              key={poi.id}
              position={[poi.lat, poi.lng]}
              icon={createColoredIcon(CATEGORY_COLOR[determineCategory(poi.tags)] || "blue")}
            >
              <Popup>
                <strong>{poi.name}</strong>
                <p>{poi.description}</p>
              </Popup>
            </Marker>
          ))}
          {/* ORS Route */}
          {routeCoords.length > 0 && <Polyline positions={routeCoords} color="blue" weight={5} />}
        </MapContainer>
      </div>

      {/* ---------- Sidebar ---------- */}
      <div style={{ background: "#fff", borderRadius: 18, padding: 24, boxShadow: "0 8px 24px rgba(0,0,0,0.1)" }}>
        {/* Recommended POIs */}
        {recommendedPois.length > 0 && (
          <div style={{ marginBottom: 24 }}>
            <h3>Recommended POIs</h3>
            {recommendedPois.map((poi, idx) => (
              <div
                key={poi.id}
                style={{
                  border: "1px solid #eee",
                  borderRadius: 6,
                  padding: 8,
                  marginBottom: 6,
                  background: "#f9f9f9",
                  cursor: "pointer",
                }}
                onClick={() => {
                  const index = selectedPois.findIndex((p) => p.id === poi.id);
                  if (index !== -1) setActiveIndex(index);
                }}
              >
                <strong>{poi.name}</strong>
                <p style={{ fontSize: 13, margin: 0 }}>{poi.description}</p>
              </div>
            ))}
          </div>
        )}

        {tracking ? (
          <>
            <h3>Now Visiting:</h3>
            <strong>{activeIndex + 1}. {selectedPois[activeIndex]?.name}</strong>
            <p>{selectedPois[activeIndex]?.description}</p>

            {/* LIKE / DISLIKE */}
            <div style={{ display: "flex", gap: 8, marginTop: 12 }}>
              <button
                onClick={() => handleLike(selectedPois[activeIndex].id, 1)}
                style={{
                  flex: 1,
                  padding: 10,
                  background: likedPois[selectedPois[activeIndex]?.id] === 1 ? "#4caf50" : "#e0e0e0",
                  color: likedPois[selectedPois[activeIndex]?.id] === 1 ? "#fff" : "#000",
                  border: "none",
                  borderRadius: 6,
                  cursor: "pointer",
                  fontWeight: 600,
                }}
              >
                Like
              </button>
              <button
                onClick={() => handleLike(selectedPois[activeIndex].id, 0)}
                style={{
                  flex: 1,
                  padding: 10,
                  background: likedPois[selectedPois[activeIndex]?.id] === 0 ? "#f44336" : "#e0e0e0",
                  color: likedPois[selectedPois[activeIndex]?.id] === 0 ? "#fff" : "#000",
                  border: "none",
                  borderRadius: 6,
                  cursor: "pointer",
                  fontWeight: 600,
                }}
              >
                Dislike
              </button>
            </div>

            <button
              onClick={handleNextStop}
              style={{
                width: "100%",
                padding: 12,
                background: "#1976d2",
                color: "#fff",
                border: "none",
                borderRadius: 8,
                marginTop: 12,
                fontWeight: 600,
                cursor: "pointer",
              }}
            >
              Next Stop
            </button>

            <button
              onClick={stopTour}
              style={{
                width: "100%",
                padding: 12,
                background: "#f44336",
                color: "#fff",
                border: "none",
                borderRadius: 8,
                marginTop: 10,
                fontWeight: 600,
                cursor: "pointer",
              }}
            >
              Cancel Tour
            </button>
          </>
        ) : (
          <>
            <h2>Selected POIs</h2>
            {selectedPois.map((poi, index) => (
              <div
                key={poi.id}
                draggable
                onDragStart={() => handleDragStart(index)}
                onDragOver={(e) => e.preventDefault()}
                onDrop={() => handleDrop(index)}
                style={{
                  marginBottom: 12,
                  padding: 8,
                  border: "1px solid #eee",
                  borderRadius: 6,
                  cursor: "grab",
                  background: "#fafafa",
                }}
              >
                <strong>{index + 1}. {poi.name}</strong>
                <p style={{ margin: 0, fontSize: 13 }}>{poi.description}</p>
              </div>
            ))}
            <button
              onClick={() => setTracking(true)}
              style={{
                width: "100%",
                padding: 12,
                background: "#4caf50",
                color: "#fff",
                border: "none",
                borderRadius: 8,
                fontWeight: 600,
                cursor: "pointer",
              }}
            >
              Start Tour
            </button>
          </>
        )}

        <button
          onClick={() => navigate("/home")}
          style={{
            width: "100%",
            padding: 12,
            background: "#9e9e9e",
            color: "#fff",
            border: "none",
            borderRadius: 8,
            fontWeight: 600,
            cursor: "pointer",
            marginTop: 12,
          }}
        >
          Back to Home
        </button>
      </div>
    </div>
  );
}
