import React, { useEffect, useState } from "react";

/**
 * Simple weather widget (mock/randomized for demo).
 * You can replace with a real API call (e.g., OpenWeatherMap or Open-Meteo) if you want live weather.
 */
export default function WeatherWidget() {
  const [weather, setWeather] = useState("Loading...");

  useEffect(() => {
    // For demo: pick a pseudo-random stable weather label
    const opts = ["Sunny", "Cloudy", "Rainy"];
    const idx = Math.floor((new Date().getDate() + new Date().getMonth()) % opts.length);
    setWeather(opts[idx]);
  }, []);

  return (
    <div style={{ marginTop: 12, padding: 10, borderRadius: 8, background: "#eff6ff", border: "1px solid #e0f2fe" }}>
      <div style={{ fontSize: 13, color: "#0f172a", fontWeight: 600 }}>ðŸŒ¤ Weather (Majayjay)</div>
      <div style={{ marginTop: 6, color: "#475569" }}>{weather}</div>
    </div>
  );
}
