import React, { useEffect, useState } from "react";

export default function ProfilePage() {
  const storedUser = JSON.parse(localStorage.getItem("user"));
  const token = localStorage.getItem("token");

  const [user, setUser] = useState(null);
  const [preferences, setPreferences] = useState([]);
  const [itineraries, setItineraries] = useState([]);
  const [editing, setEditing] = useState(false);
  const [loading, setLoading] = useState(true);

  const preferenceOptions = ["Nature", "Culture", "Food", "History", "Adventure"];

  // Load user data
  useEffect(() => {
    const loadUserData = async () => {
      if (!storedUser?.email || !token) return;

      try {
        const res = await fetch(
          `http://localhost:5000/api/users/email/${encodeURIComponent(storedUser.email)}`,
          { headers: { Authorization: `Bearer ${token}` } }
        );
        const data = await res.json();

        setUser(data);
        setPreferences(Array.isArray(data.preferences) ? data.preferences : []);
        setLoading(false);
      } catch (err) {
        console.error("Error fetching user:", err);
        setLoading(false);
      }
    };

    loadUserData();
  }, [storedUser?.email, token]);

  // Load user itineraries
  useEffect(() => {
    const loadItineraries = async () => {
      if (!storedUser?.id) return;

      try {
        const res = await fetch(`http://localhost:5000/api/itineraries/user/${storedUser.id}`);
        const data = await res.json();
        if (Array.isArray(data)) setItineraries(data);
      } catch (err) {
        console.error("Error fetching itineraries:", err);
      }
    };

    loadItineraries();
  }, [storedUser?.id]);

  // Toggle a preference
  const handleTogglePreference = (option) => {
    setPreferences((prev) =>
      prev.includes(option) ? prev.filter((p) => p !== option) : [...prev, option]
    );
  };

  // Save preferences
  const handleSave = async () => {
    if (!user || !token) {
      alert("No token found. Please login again.");
      return;
    }

    try {
      const res = await fetch(
        `http://localhost:5000/api/users/email/${encodeURIComponent(user.email)}/preferences`,
        {
          method: "PUT",
          headers: {
            "Content-Type": "application/json",
            Authorization: `Bearer ${token}`,
          },
          body: JSON.stringify({ preferences }),
        }
      );

      const data = await res.json();
      if (!res.ok) throw new Error(data.message || "Failed to save preferences");

      setUser({ ...user, preferences });
      setEditing(false);
      alert("Preferences saved successfully!");
    } catch (err) {
      console.error("Error updating preferences:", err);
      alert(`Error saving preferences: ${err.message}`);
    }
  };

  if (loading || !user) return <div style={{ padding: "24px" }}>Loading...</div>;

  return (
    <div style={{ padding: "24px", maxWidth: "800px", margin: "0 auto" }}>
      <h1 style={{ fontSize: "1.8rem", fontWeight: "bold", marginBottom: "16px" }}>Profile</h1>

      {/* User Info */}
      <div
        style={{
          border: "1px solid #ddd",
          borderRadius: "16px",
          padding: "20px",
          background: "#fff",
          boxShadow: "0 2px 6px rgba(0,0,0,0.1)",
          marginBottom: "24px",
        }}
      >
        <p><strong>Username:</strong> {user.username}</p>
        <p><strong>Email:</strong> {user.email}</p>
        <p>
          <strong>Full Name:</strong>{" "}
          {`${user.first_name || ""} ${user.middle_name || ""} ${user.last_name || ""}`}
        </p>
        <p><strong>Age:</strong> {user.age || "Not provided"}</p>

        {/* Preferences Section */}
        <div style={{ marginTop: "16px" }}>
          <strong>Preferences:</strong>

          {editing ? (
            <div>
              {preferenceOptions.map((option) => (
                <label key={option} style={{ display: "block", marginTop: "6px" }}>
                  <input
                    type="checkbox"
                    value={option}
                    checked={preferences.includes(option)}
                    onChange={() => handleTogglePreference(option)}
                  />{" "}
                  {option}
                </label>
              ))}

              <div style={{ marginTop: "12px" }}>
                <button
                  onClick={handleSave}
                  style={{
                    background: "#007bff",
                    color: "#fff",
                    padding: "8px 16px",
                    border: "none",
                    borderRadius: "8px",
                  }}
                >
                  Save
                </button>
                <button
                  onClick={() => {
                    setPreferences(user.preferences || []);
                    setEditing(false);
                  }}
                  style={{
                    background: "#ccc",
                    padding: "8px 16px",
                    border: "none",
                    borderRadius: "8px",
                    marginLeft: "8px",
                  }}
                >
                  Cancel
                </button>
              </div>
            </div>
          ) : (
            <ul style={{ marginTop: "8px" }}>
              {preferences.length > 0 ? (
                preferences.map((p, i) => <li key={i}>{p}</li>)
              ) : (
                <li>No preferences set.</li>
              )}
            </ul>
          )}
        </div>

        {!editing && (
          <button
            onClick={() => setEditing(true)}
            style={{
              marginTop: "16px",
              background: "#28a745",
              color: "#fff",
              padding: "8px 16px",
              border: "none",
              borderRadius: "8px",
            }}
          >
            Edit Preferences
          </button>
        )}
      </div>

      {/* Itineraries Section */}
      <div
        style={{
          border: "1px solid #ddd",
          borderRadius: "16px",
          padding: "20px",
          background: "#fff",
          boxShadow: "0 2px 6px rgba(0,0,0,0.1)",
        }}
      >
        <h2 style={{ fontSize: "1.4rem", marginBottom: "12px" }}>Saved Itineraries</h2>
        {itineraries.length > 0 ? (
          <ul style={{ listStyle: "none", paddingLeft: 0 }}>
            {itineraries.map((itinerary) => (
              <li
                key={itinerary.id}
                style={{
                  background: "#f9f9f9",
                  borderRadius: "10px",
                  padding: "12px",
                  marginBottom: "10px",
                  boxShadow: "0 1px 4px rgba(0,0,0,0.1)",
                }}
              >
                <p style={{ margin: 0 }}>
                  <strong>{itinerary.name}</strong> <br />
                  <span>Status: {itinerary.status || "N/A"}</span>
                  <br />
                  <small>
                    Saved on:{" "}
                    {itinerary.created_at
                      ? new Date(itinerary.created_at).toLocaleString()
                      : "Unknown date"}
                  </small>
                </p>
              </li>
            ))}
          </ul>
        ) : (
          <p>No itineraries found.</p>
        )}
      </div>
    </div>
  );
}
