import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";

const ORS_API_KEY = "eyJvcmciOiI1YjNjZTM1OTc4NTExMTAwMDFjZjYyNDgiLCJpZCI6ImJhNWU3NmIxOTRmMzQyYTg4NzY4MDc2MzVmZDY1ZDRhIiwiaCI6Im11cm11cjY0In0=";

export default function RoutePlanner() {
  const [pois, setPois] = useState([]);
  const [user, setUser] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    const storedUser = JSON.parse(localStorage.getItem("user"));
    setUser(storedUser);

    fetch("http://localhost:5000/api/pois")
      .then(res => res.json())
      .then(data => setPois(data))
      .catch(err => {
        console.error("Error fetching POIs:", err);
        setPois([]);
      });
  }, []);

  const geocodeAddress = async (address) => {
    const res = await fetch(
      `https://api.openrouteservice.org/geocode/search?api_key=${ORS_API_KEY}&text=${encodeURIComponent(address)}`
    );
    if (!res.ok) throw new Error("Geocoding failed");
    const data = await res.json();
    return data.features[0]?.geometry.coordinates; // [lng, lat]
  };

  const getRouteInfo = async (start, end, mode = "driving-car") => {
    const res = await fetch(`https://api.openrouteservice.org/v2/directions/${mode}`, {
      method: "POST",
      headers: {
        "Authorization": ORS_API_KEY,
        "Content-Type": "application/json",
      },
      body: JSON.stringify({
        coordinates: [
          [start[0], start[1]],
          [end[0], end[1]],
        ],
      }),
    });

    if (!res.ok) throw new Error("Routing failed");
    const data = await res.json();
    const summary = data.routes[0].summary;
    return {
      distance: summary.distance / 1000,
      duration: summary.duration / 60,
    };
  };

  const recommendRoute = async () => {
    if (!user || !user.preferences || user.preferences.length === 0) {
      alert("Set your preferences first in profile.");
      return;
    }

    try {
      const prefs = user.preferences.map(p => p.toLowerCase());

      let userCoords = null;
      if (user.address) {
        try {
          userCoords = await geocodeAddress(user.address);
        } catch (err) {
          console.warn("Geocoding failed, falling back to preference only:", err.message);
        }
      }

      const scoredPOIs = await Promise.all(
        pois.map(async (poi) => {
          let score = 0;

          // preference scoring
          prefs.forEach(pref => {
            if (poi.tags?.toLowerCase().includes(pref)) score += 3;
          });

          // optional distance scoring
          if (userCoords) {
            try {
              const route = await getRouteInfo(userCoords, [poi.lng, poi.lat]);
              score += 2 / (route.distance + 0.1);
              return { ...poi, score, distance: route.distance, duration: route.duration };
            } catch {
              return { ...poi, score, distance: Infinity, duration: Infinity };
            }
          }

          // if no address, just return preference score
          return { ...poi, score, distance: null, duration: null };
        })
      );

      const sorted = scoredPOIs.sort((a, b) => b.score - a.score);

      // select top 4, diverse categories
      const selected = [];
      const categoriesUsed = new Set();
      for (let poi of sorted) {
        const cat = poi.category?.toLowerCase() || "other";
        if (!categoriesUsed.has(cat)) {
          selected.push(poi);
          categoriesUsed.add(cat);
        }
        if (selected.length >= 4) break;
      }

      if (selected.length === 0) {
        alert("No POIs match your preferences.");
        return;
      }

      navigate("/routedetails", { state: { itinerary: selected, start: userCoords } });

    } catch (err) {
      console.error(err);
      alert("Error planning route: " + err.message);
    }
  };

  return (
    <div style={{ textAlign: "center", marginTop: 40 }}>
      <button
        onClick={recommendRoute}
        style={{
          padding: "12px 24px",
          background: "#2196f3",
          color: "#fff",
          border: "none",
          borderRadius: 8,
          fontSize: 16,
          cursor: "pointer"
        }}
      >
        Plan My Smart Route
      </button>
    </div>
  );
}
