// src/pages/AdminDashboard.js
import React, { useState, useEffect } from "react";
import { MapContainer, TileLayer, Marker, Popup, useMapEvents } from "react-leaflet";
import "leaflet/dist/leaflet.css";

export default function AdminDashboard({ isAdmin }) {
  const [pois, setPois] = useState([]);
  const [newPoi, setNewPoi] = useState({
    name: "", lat: 0, lng: 0, visitors: "Moderate",
    type: "nature", description: "", image: ""
  });

  useEffect(() => {
    if (!isAdmin) return;
    fetch("/data/spots.csv")
      .then(res => res.json())
      .then(data => setPois(data));
  }, [isAdmin]);

  if (!isAdmin) return <h2>Access Denied</h2>;

  const addPOI = () => {
    fetch("/data/spots.csv", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(newPoi)
    }).then(() => {
      setPois([...pois, newPoi]);
      setNewPoi({ name: "", lat: 0, lng: 0, visitors: "Moderate", type: "nature", description: "", image: "" });
    });
  };

  const deletePOI = (index) => {
    fetch(`/data/spots.csv/${index}`, { method: "DELETE" })
      .then(() => setPois(pois.filter((_, i) => i !== index)));
  };

  const LocationMarker = () => {
    useMapEvents({
      click(e) {
        setNewPoi({ ...newPoi, lat: e.latlng.lat, lng: e.latlng.lng });
      }
    });
    return newPoi.lat && newPoi.lng ? <Marker position={[newPoi.lat, newPoi.lng]} /> : null;
  };

  return (
    <div style={{ padding: 20 }}>
      <h1>Admin Dashboard</h1>
      <div style={{ display: "grid", gridTemplateColumns: "2fr 1fr", gap: 20 }}>
        <div style={{ height: "600px" }}>
          <MapContainer center={[14.1467, 121.4708]} zoom={13} style={{ height: "100%", width: "100%" }}>
            <TileLayer
              attribution='&copy; <a href="https://www.openstreetmap.org/">OSM</a>'
              url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
            />
            {pois.map((p, i) => (
              <Marker key={i} position={[p.lat, p.lng]}>
                <Popup>
                  <strong>{p.name}</strong><br />
                  {p.description}<br />
                  Visitors: {p.visitors}
                  {p.image && <img src={p.image} alt={p.name} style={{ width: 100, borderRadius: 5, marginTop: 4 }} />}
                  <br /><button onClick={() => deletePOI(i)}>Delete</button>
                </Popup>
              </Marker>
            ))}
            <LocationMarker />
          </MapContainer>
        </div>
        <div>
          <h2>Add New POI</h2>
          <input placeholder="Name" value={newPoi.name} onChange={e => setNewPoi({...newPoi, name:e.target.value})} /><br/>
          <input placeholder="Type" value={newPoi.type} onChange={e => setNewPoi({...newPoi, type:e.target.value})} /><br/>
          <input placeholder="Visitors" value={newPoi.visitors} onChange={e => setNewPoi({...newPoi, visitors:e.target.value})} /><br/>
          <textarea placeholder="Description" value={newPoi.description} onChange={e => setNewPoi({...newPoi, description:e.target.value})} /><br/>
          <input placeholder="Image URL" value={newPoi.image} onChange={e => setNewPoi({...newPoi, image:e.target.value})} /><br/>
          <p>Click on the map to select Lat/Lng: {newPoi.lat}, {newPoi.lng}</p>
          <button onClick={addPOI}>Add POI</button>
        </div>
      </div>
    </div>
  );
}
