import React, { useEffect, useState } from "react";
import Papa from "papaparse";

export default function EstablishmentsPage() {
  const [lodgings, setLodgings] = useState([]);
  const [restaurants, setRestaurants] = useState([]);

  useEffect(() => {
    fetch("/data/spots.csv")
      .then((res) => res.text())
      .then((csv) => {
        Papa.parse(csv, { header: true, skipEmptyLines: true, complete: (res) => {
          const rows = res.data
            .filter((r) => r.name && r.type)
            .map((r) => ({
              ...r,
              image: r.image || null,
              description: r.description || "No description available",
              tags: r.tags ? r.tags.split(",").map((t) => t.trim()) : [],
            }));

          setLodgings(rows.filter((r) => ["Lodging", "Resort", "Motel", "Hotel"].includes(r.type)));
          setRestaurants(rows.filter((r) => ["Restaurant", "Coffee"].includes(r.type)));
        }});
      });
  }, []);

  const renderCards = (items) => (
    <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-6">
      {items.map((item, i) => (
        <div
          key={i}
          className="bg-white border rounded-2xl shadow-lg overflow-hidden hover:shadow-2xl cursor-pointer transition"
        >
          {item.image ? (
            <img src={item.image} alt={item.name} className="h-48 w-full object-cover" />
          ) : (
            <div className="h-48 w-full flex items-center justify-center bg-gray-100 text-gray-400">
              No image
            </div>
          )}
          <div className="p-4">
            <h3 className="text-lg font-bold mb-1">{item.name}</h3>
            <p className="text-sm text-gray-600 mb-2">{item.description}</p>
            {item.tags.length > 0 && (
              <div className="flex flex-wrap gap-1">
                {item.tags.map((tag, idx) => (
                  <span
                    key={idx}
                    className="text-xs bg-gray-200 rounded-full px-2 py-1"
                  >
                    {tag}
                  </span>
                ))}
              </div>
            )}
          </div>
        </div>
      ))}
    </div>
  );

  return (
    <div className="p-6">
      <h1 className="text-2xl font-bold mb-6">Establishments</h1>

      <section className="mb-10">
        <h2 className="text-xl font-semibold mb-4">Lodgings</h2>
        {renderCards(lodgings)}
      </section>

      <section>
        <h2 className="text-xl font-semibold mb-4">Restaurants</h2>
        {renderCards(restaurants)}
      </section>
    </div>
  );
}
