import React, { useEffect, useState, useRef } from "react";
import { MapContainer, TileLayer, Marker, Popup, Polyline } from "react-leaflet";
import L from "leaflet";
import { useNavigate, useLocation } from "react-router-dom";
import "leaflet/dist/leaflet.css";

export default function RouteDetails() {
  const location = useLocation();
  const navigate = useNavigate();

  const filteredPois = location.state?.filteredPois || [];
  const startPoint = location.state?.startPoint || null;
  const transportMode = location.state?.transportMode || "driving-car";
  const routeId = location.state?.routeId || null;
  const user = JSON.parse(localStorage.getItem("user"));

  const [selectedPois, setSelectedPois] = useState(filteredPois);
  const [userLocation, setUserLocation] = useState(startPoint);
  const [activeIndex, setActiveIndex] = useState(0);
  const [tracking, setTracking] = useState(false);
  const [nextPreview, setNextPreview] = useState(null);
  const [likedPois, setLikedPois] = useState({});

  const watchIdRef = useRef(null);
  const ORS_API_KEY = "YOUR_ORS_API_KEY_HERE";

  // ---------- LIKE / DISLIKE ----------
  const handleLike = async (poiId, liked) => {
    try {
      const token = user?.token;
      if (!token) {
        console.error("No token found in localStorage");
        return;
      }

      console.log("Sending feedback to backend:", { poi_id: poiId, liked });

      const response = await fetch("http://localhost:5000/api/user_feedback", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify({
          poi_id: poiId,
          liked,
        }),
      });

      const data = await response.json();
      console.log("Server response:", data);

      if (data.success) {
        setLikedPois((prev) => ({ ...prev, [poiId]: liked }));
      } else {
        console.error("Failed to save feedback:", data);
      }
    } catch (err) {
      console.error("Error sending feedback:", err);
    }
  };

  // ---------- STOP TOUR ----------
  const stopTour = async (status = "cancelled") => {
    if (watchIdRef.current) navigator.geolocation.clearWatch(watchIdRef.current);
    setTracking(false);
    setActiveIndex(0);
    setNextPreview(null);
  };

  // ---------- NEXT STOP ----------
  const handleNextStop = () => {
    if (activeIndex + 1 < selectedPois.length) {
      setActiveIndex((prev) => prev + 1);
      setNextPreview(selectedPois[activeIndex + 2] || null);
    } else {
      stopTour("finished");
      alert("Tour finished!");
    }
  };

  if (!userLocation) return <p>Loading route...</p>;

  return (
    <div style={{ display: "grid", gridTemplateColumns: "1.3fr 0.7fr", gap: 24, padding: 24 }}>
      {/* Map */}
      <div style={{ height: 650, borderRadius: 18, overflow: "hidden" }}>
        <MapContainer center={[userLocation.lat, userLocation.lng]} zoom={13} style={{ height: "100%", width: "100%" }}>
          <TileLayer url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png" />
          {selectedPois.map((poi, i) => (
            <Marker key={poi.id} position={[poi.lat, poi.lng]}>
              <Popup>{poi.name}</Popup>
            </Marker>
          ))}
        </MapContainer>
      </div>

      {/* Sidebar */}
      <div style={{ background: "#fff", borderRadius: 18, padding: 24, boxShadow: "0 8px 24px rgba(0,0,0,0.1)" }}>
        {tracking ? (
          <>
            <h3>Now Visiting:</h3>
            <strong>{selectedPois[activeIndex]?.name}</strong>
            <p>{selectedPois[activeIndex]?.description}</p>

            {/* LIKE / DISLIKE BUTTONS */}
            <div style={{ display: "flex", gap: 8, marginTop: 12 }}>
              <button
                onClick={() => handleLike(selectedPois[activeIndex].id, 1)}
                style={{
                  flex: 1,
                  padding: 10,
                  background: likedPois[selectedPois[activeIndex]?.id] === 1 ? "#4caf50" : "#e0e0e0",
                  color: likedPois[selectedPois[activeIndex]?.id] === 1 ? "#fff" : "#000",
                  border: "none",
                  borderRadius: 6,
                  cursor: "pointer",
                  fontWeight: 600,
                }}
              >
                üëç Like
              </button>

              <button
                onClick={() => handleLike(selectedPois[activeIndex].id, 0)}
                style={{
                  flex: 1,
                  padding: 10,
                  background: likedPois[selectedPois[activeIndex]?.id] === 0 ? "#f44336" : "#e0e0e0",
                  color: likedPois[selectedPois[activeIndex]?.id] === 0 ? "#fff" : "#000",
                  border: "none",
                  borderRadius: 6,
                  cursor: "pointer",
                  fontWeight: 600,
                }}
              >
                üëé Dislike
              </button>
            </div>

            <button
              onClick={handleNextStop}
              style={{
                width: "100%",
                padding: 12,
                background: "#1976d2",
                color: "#fff",
                border: "none",
                borderRadius: 8,
                marginTop: 12,
                fontWeight: 600,
                cursor: "pointer",
              }}
            >
              Next Stop
            </button>

            <button
              onClick={() => stopTour("cancelled")}
              style={{
                width: "100%",
                padding: 12,
                background: "#f44336",
                color: "#fff",
                border: "none",
                borderRadius: 8,
                marginTop: 10,
                fontWeight: 600,
                cursor: "pointer",
              }}
            >
              Cancel Tour
            </button>
          </>
        ) : (
          <>
            <h2>Selected POIs</h2>
            {selectedPois.map((poi) => (
              <div key={poi.id} style={{ marginBottom: 16 }}>
                <strong>{poi.name}</strong>
                <p>{poi.description}</p>
              </div>
            ))}
            <button
              onClick={() => setTracking(true)}
              style={{
                width: "100%",
                padding: 12,
                background: "#4caf50",
                color: "#fff",
                border: "none",
                borderRadius: 8,
                fontWeight: 600,
                cursor: "pointer",
              }}
            >
              Start Tour
            </button>
          </>
        )}

        <button
          onClick={() => navigate("/home")}
          style={{
            width: "100%",
            padding: 12,
            background: "#9e9e9e",
            color: "#fff",
            border: "none",
            borderRadius: 8,
            fontWeight: 600,
            cursor: "pointer",
            marginTop: 12,
          }}
        >
          Back to Home
        </button>
      </div>
    </div>
  );
}
