import React, { useState, useEffect } from "react";

export default function AdminUsers() {
  const [users, setUsers] = useState([]);
  const token = localStorage.getItem("token");

  useEffect(() => {
    fetchUsers();
  }, []);

  const fetchUsers = () => {
    fetch("http://localhost:5000/api/users", {
      headers: { Authorization: `Bearer ${token}` },
    })
      .then(res => res.json())
      .then(data => setUsers(Array.isArray(data) ? data : []))
      .catch(err => console.error(err));
  };

  const toggleBan = (userId, currentlyBanned) => {
    fetch(`http://localhost:5000/api/users/${userId}/ban`, {
      method: "POST",
      headers: {
        Authorization: `Bearer ${token}`,
        "Content-Type": "application/json",
      },
      body: JSON.stringify({ ban: !currentlyBanned }),
    })
      .then(res => res.json())
      .then(() => fetchUsers())
      .catch(err => console.error(err));
  };

  return (
    <div style={{ marginLeft: "240px", padding: "20px" }}>
      <h1 style={{ marginBottom: "20px" }}>All Users</h1>
      {users.length === 0 ? (
        <p>No users found</p>
      ) : (
        <table
          style={{
            width: "100%",
            borderCollapse: "collapse",
            background: "#f9f9f9",
          }}
        >
          <thead>
            <tr>
              <th style={thStyle}>ID</th>
              <th style={thStyle}>Username</th>
              <th style={thStyle}>Email</th>
              <th style={thStyle}>Role</th>
              <th style={thStyle}>Status</th>
              <th style={thStyle}>Actions</th>
            </tr>
          </thead>
          <tbody>
            {users.map(user => (
              <tr key={user.id} style={{ textAlign: "center" }}>
                <td style={tdStyle}>{user.id}</td>
                <td style={tdStyle}>{user.username}</td>
                <td style={tdStyle}>{user.email}</td>
                <td style={tdStyle}>{user.role}</td>
                <td style={tdStyle}>{user.banned ? "Banned" : "Active"}</td>
                <td style={tdStyle}>
                  <button
                    onClick={() => toggleBan(user.id, user.banned)}
                    style={{
                      padding: "6px 12px",
                      background: user.banned ? "#4caf50" : "#f44336",
                      color: "#fff",
                      border: "none",
                      borderRadius: "4px",
                      cursor: "pointer",
                    }}
                  >
                    {user.banned ? "Unban" : "Ban"}
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
}

const thStyle = {
  borderBottom: "2px solid #ccc",
  padding: "10px",
  background: "#2563eb",
  color: "#fff",
};

const tdStyle = {
  borderBottom: "1px solid #ccc",
  padding: "10px",
};
