import React, { useState, useRef, useEffect } from "react";
import { Link, useNavigate } from "react-router-dom";

export default function Navbar() {
  const navigate = useNavigate();
  const storedUser = JSON.parse(localStorage.getItem("user"));
  const [menuOpen, setMenuOpen] = useState(false);
  const menuRef = useRef(null);

  const handleLogout = () => {
    localStorage.removeItem("token");
    localStorage.removeItem("user");
    navigate("/login");
  };

  useEffect(() => {
    function handleClickOutside(e) {
      if (menuRef.current && !menuRef.current.contains(e.target)) {
        setMenuOpen(false);
      }
    }
    document.addEventListener("mousedown", handleClickOutside);
    return () => document.removeEventListener("mousedown", handleClickOutside);
  }, []);

  return (
    <nav
      style={{
        padding: "12px 20px",
        background: "#2563eb",
        color: "#fff",
        display: "flex",
        gap: "20px",
        alignItems: "center",
      }}
    >
      <Link to="/home" style={{ color: "#fff", textDecoration: "none" }}>
        Home
      </Link>
      <Link to="/mappage" style={{ color: "#fff", textDecoration: "none" }}>
        Map
      </Link>
      <Link to="/establishments" style={{ color: "#fff", textDecoration: "none" }}>
        Establishments
      </Link>
      <Link to="/natureculture" style={{ color: "#fff", textDecoration: "none" }}>
        Nature & Culture
      </Link>

      <div style={{ marginLeft: "auto", position: "relative" }} ref={menuRef}>
        {storedUser ? (
          <>
            <div
              onClick={() => setMenuOpen((prev) => !prev)}
              style={{
                width: "36px",
                height: "36px",
                borderRadius: "50%",
                background: "#fff",
                color: "#2563eb",
                display: "flex",
                alignItems: "center",
                justifyContent: "center",
                fontWeight: "bold",
                cursor: "pointer",
                userSelect: "none",
              }}
            >
              {storedUser.username[0].toUpperCase()}
            </div>

            {menuOpen && (
              <div
                style={{
                  position: "absolute",
                  right: 0,
                  top: "48px",
                  background: "#fff",
                  color: "#333",
                  borderRadius: "8px",
                  boxShadow: "0 2px 8px rgba(0,0,0,0.15)",
                  overflow: "hidden",
                  zIndex: 100,
                }}
              >
                <button
                  onClick={() => {
                    navigate("/profile");
                    setMenuOpen(false);
                  }}
                  style={{
                    display: "block",
                    width: "100%",
                    padding: "10px 16px",
                    textAlign: "left",
                    background: "none",
                    border: "none",
                    cursor: "pointer",
                  }}
                >
                  Profile
                </button>
                <button
                  onClick={handleLogout}
                  style={{
                    display: "block",
                    width: "100%",
                    padding: "10px 16px",
                    textAlign: "left",
                    background: "none",
                    border: "none",
                    color: "red",
                    cursor: "pointer",
                  }}
                >
                  Logout
                </button>
              </div>
            )}
          </>
        ) : (
          <div style={{ display: "flex", gap: "10px" }}>
            <Link to="/login" style={{ color: "#fff", textDecoration: "none" }}>
              Login
            </Link>
            <Link to="/signup" style={{ color: "#fff", textDecoration: "none" }}>
              Sign Up
            </Link>
          </div>
        )}
      </div>
    </nav>
  );
}
