import React, { useState, useEffect } from "react";
import { MapContainer, TileLayer, Marker, Popup } from "react-leaflet";
import "leaflet/dist/leaflet.css";

export default function AdminDashboard() {
  const [pois, setPois] = useState([]);
  const [usersCount, setUsersCount] = useState(0);

  useEffect(() => {
    const token = localStorage.getItem("token");

    // Fetch POIs
    fetch("http://localhost:5000/api/pois", {
      headers: { Authorization: `Bearer ${token}` },
    })
      .then((res) => res.json())
      .then((data) => setPois(Array.isArray(data) ? data : []))
      .catch((err) => console.error(err));

    // Fetch Users
    fetch("http://localhost:5000/api/users", {
      headers: { Authorization: `Bearer ${token}` },
    })
      .then((res) => res.json())
      .then((data) => setUsersCount(Array.isArray(data) ? data.length : 0))
      .catch((err) => console.error(err));
  }, []);

  // Sort POIs by visitors
  const visitOrder = { "Very High": 4, High: 3, Moderate: 2, Low: 1 };
  const topPOIs = [...pois]
    .sort((a, b) => (visitOrder[b.visitors] || 0) - (visitOrder[a.visitors] || 0))
    .slice(0, 5);

  return (
    <div style={{ marginLeft: "240px", padding: "20px" }}>
      <h1>Admin Dashboard</h1>

      <div style={{ display: "flex", gap: "20px", marginBottom: "20px" }}>
        <div style={{ flex: 1, padding: "20px", background: "#2563eb", color: "#fff", borderRadius: "8px" }}>
          <h3>Total Users</h3>
          <p style={{ fontSize: "24px", fontWeight: "bold" }}>{usersCount}</p>
        </div>
        <div style={{ flex: 1, padding: "20px", background: "#16a34a", color: "#fff", borderRadius: "8px" }}>
          <h3>Total POIs</h3>
          <p style={{ fontSize: "24px", fontWeight: "bold" }}>{pois.length}</p>
        </div>
      </div>

      <h2>Top 5 Most Visited POIs</h2>
      <MapContainer
        center={[14.1467, 121.4708]}
        zoom={13}
        style={{ height: "400px", width: "100%", marginTop: "10px" }}
      >
        <TileLayer
          url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
          attribution='&copy; OpenStreetMap contributors'
        />
        {topPOIs.map((poi) => (
          <Marker key={poi.id} position={[poi.lat, poi.lng]}>
            <Popup>
              <strong>{poi.name}</strong>
              <br />
              {poi.description}
              <br />
              Visitors: {poi.visitors}
              <br />
              Tags: {poi.tags}
            </Popup>
          </Marker>
        ))}
      </MapContainer>
    </div>
  );
}
