// src/pages/RouteDetails.js

import React, { useEffect, useState } from "react";
import { MapContainer, TileLayer, Marker, Popup, Polyline } from "react-leaflet";
import "leaflet/dist/leaflet.css";
import { useNavigate, useLocation } from "react-router-dom";

import { getRouteInfo } from "../services/orsService";
import { getWeather } from "../services/weatherService";
import { scorePOI } from "../services/scoringService";

export default function RouteDetails() {
  const location = useLocation();
  const navigate = useNavigate();
  const { routeSpots, currentLocation, mode, selectedCategories } = location.state || {};

  const [routeIndex, setRouteIndex] = useState(0);
  const [routeInfo, setRouteInfo] = useState(null);
  const [weather, setWeather] = useState(null);
  const [polylineCoords, setPolylineCoords] = useState([]);

  useEffect(() => {
    if (!routeSpots || routeSpots.length === 0) return;

    const fetchData = async () => {
      const nextSpot = routeSpots[routeIndex];
      const info = await getRouteInfo(currentLocation, nextSpot.poi, mode);
      const weatherData = await getWeather(nextSpot.poi.lat, nextSpot.poi.lon);

      if (info) {
        setRouteInfo(info);
        setPolylineCoords(info.geometry ? info.geometry.coordinates.map(c => [c[1], c[0]]) : []);
      }

      if (weatherData) {
        setWeather(weatherData);
      }

      // ✅ Apply scoring here too (optional: for live recalculation)
      if (info && weatherData) {
        const newScore = scorePOI(nextSpot.poi, info, weatherData, selectedCategories, mode);
        console.log("Recalculated Score:", newScore);
      }
    };

    fetchData();
  }, [routeIndex, routeSpots, currentLocation, mode, selectedCategories]);

  if (!routeSpots || routeSpots.length === 0) {
    return <div>No route details available.</div>;
  }

  const currentSpot = routeSpots[routeIndex].poi;

  return (
    <div className="grid grid-cols-1 md:grid-cols-2 h-screen">
      {/* Map Section */}
      <div className="relative h-full">
        <MapContainer
          center={[currentSpot.lat, currentSpot.lon]}
          zoom={14}
          className="w-full h-full"
        >
          <TileLayer
            url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
            attribution="© OpenStreetMap contributors"
          />
          <Marker position={[currentLocation.lat, currentLocation.lon]}>
            <Popup>You are here</Popup>
          </Marker>
          <Marker position={[currentSpot.lat, currentSpot.lon]}>
            <Popup>{currentSpot.name}</Popup>
          </Marker>
          {polylineCoords.length > 0 && <Polyline positions={polylineCoords} color="blue" />}
        </MapContainer>
      </div>

      {/* Info Panel */}
      <div className="p-4 overflow-y-auto bg-white shadow-lg">
        <h2 className="text-xl font-bold mb-2">{currentSpot.name}</h2>
        <p className="text-gray-600 mb-2">Category: {currentSpot.category}</p>

        {routeInfo && (
          <div className="mb-2">
            <p className="text-gray-700">Distance: {(routeInfo.distance / 1000).toFixed(2)} km</p>
            <p className="text-gray-700">Duration: {(routeInfo.duration / 60).toFixed(0)} mins</p>
          </div>
        )}

        {weather && (
          <div className="mb-2">
            <p className="text-gray-700">Weather: {weather.weather[0].main}</p>
            <p className="text-gray-700">Temp: {weather.main.temp}°C</p>
          </div>
        )}

        <div className="flex gap-2 mt-4">
          {routeIndex > 0 && (
            <button
              className="px-4 py-2 bg-gray-300 rounded-lg"
              onClick={() => setRouteIndex(routeIndex - 1)}
            >
              Previous
            </button>
          )}
          {routeIndex < routeSpots.length - 1 ? (
            <button
              className="px-4 py-2 bg-blue-500 text-white rounded-lg"
              onClick={() => setRouteIndex(routeIndex + 1)}
            >
              Next
            </button>
          ) : (
            <button
              className="px-4 py-2 bg-green-500 text-white rounded-lg"
              onClick={() => navigate("/")}
            >
              Finish
            </button>
          )}
        </div>
      </div>
    </div>
  );
}
