import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import PlannerModal from "../components/PlannerModal";

export default function RoutePlanner() {
  const [pois, setPois] = useState([]);
  const [user, setUser] = useState(null);
  const [showPopup, setShowPopup] = useState(false);
  const [startTime, setStartTime] = useState("now");
  const [scheduledDate, setScheduledDate] = useState("");
  const [mode, setMode] = useState("car");
  const [startChoice, setStartChoice] = useState("address");
  const [selectedStartPoi, setSelectedStartPoi] = useState("");
  const navigate = useNavigate();

  useEffect(() => {
    const storedUser = JSON.parse(localStorage.getItem("user"));
    setUser(storedUser);

    fetch("http://localhost:5000/api/pois")
      .then((res) => res.json())
      .then((data) => setPois(data))
      .catch(() => setPois([]));
  }, []);

  const recommendRoute = () => {
    if (!user || !user.preferences || user.preferences.length === 0) {
      alert("Set your preferences first in profile.");
      return;
    }

    let startCoords = null;
    if (startChoice === "address" && user.address_coords) {
      startCoords = user.address_coords;
    } else if (startChoice === "poi" && selectedStartPoi) {
      const poi = pois.find((p) => p.id === parseInt(selectedStartPoi));
      if (poi) startCoords = [parseFloat(poi.lng), parseFloat(poi.lat)];
    }

    if (!startCoords) {
      alert("Please choose a valid starting point.");
      return;
    }

    navigate("/routedetails", {
      state: {
        routeSpots: pois.slice(0, 5),
        currentLocation: {
          lat: startCoords[1],
          lon: startCoords[0]
        },
        mode: mode,
        selectedCategories: user.preferences,
        startTime: startTime === "now" ? new Date().toISOString() : scheduledDate,
      },
    });


  return (
    <div style={{ textAlign: "center", marginTop: 40 }}>
      <button onClick={() => setShowPopup(true)}>Plan My Route</button>

      <PlannerModal
        show={showPopup}
        startTime={startTime}
        setStartTime={setStartTime}
        scheduledDate={scheduledDate}
        setScheduledDate={setScheduledDate}
        mode={mode}
        setMode={setMode}
        startChoice={startChoice}
        setStartChoice={setStartChoice}
        selectedStartPoi={selectedStartPoi}
        setSelectedStartPoi={setSelectedStartPoi}
        pois={pois}
        onConfirm={recommendRoute}
        onCancel={() => setShowPopup(false)}
      />
    </div>
  );
}}
