import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";

export default function RoutePlanner() {
  const navigate = useNavigate();
  const [pois, setPois] = useState([]);
  const [user, setUser] = useState(null);
  const [userLocation, setUserLocation] = useState(null);
  const [showPopup, setShowPopup] = useState(false);
  const [mode, setMode] = useState("car");
  const [recommendedPois, setRecommendedPois] = useState([]);

  // Load user and POIs
  useEffect(() => {
    const storedUser = JSON.parse(localStorage.getItem("user"));
    setUser(storedUser);

    fetch("http://localhost:5000/api/pois")
      .then((res) => res.json())
      .then((data) => setPois(data));

    if ("geolocation" in navigator) {
      navigator.geolocation.getCurrentPosition(
        (pos) => setUserLocation({ lat: pos.coords.latitude, lng: pos.coords.longitude }),
        () => setUserLocation({ lat: 14.1461, lng: 121.5012 }) // fallback
      );
    } else setUserLocation({ lat: 14.1461, lng: 121.5012 });
  }, []);

  // Load recommended POIs (hybrid)
  useEffect(() => {
    if (!user) return;

    fetch("http://127.0.0.1:5000/api/recommend", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ user_id: user.id, top_n: 8 }),
    })
      .then((res) => res.json())
      .then((data) => setRecommendedPois(data));
  }, [user]);

  const planRoute = () => {
    if (!userLocation) return alert("Waiting for location...");
    navigate("/routedetails", {
      state: {
        startPoint: userLocation,
        filteredPois: pois,
        transportMode: mode === "foot" ? "foot-walking" : mode === "bike" ? "cycling-regular" : "driving-car",
      },
    });
    setShowPopup(false);
  };

  return (
    <div style={{ textAlign: "center", marginTop: 40 }}>
      <button
        onClick={() => setShowPopup(true)}
        style={{ padding: "14px 28px", background: "#1976d2", color: "#fff", borderRadius: 10, fontSize: 18 }}
      >
        Plan My Route
      </button>

      {showPopup && (
        <div style={{
          position: "fixed", top: 0, left: 0, width: "100%", height: "100%",
          background: "rgba(0,0,0,0.6)", display: "flex", alignItems: "center",
          justifyContent: "center", zIndex: 9999
        }}>
          <div style={{
            background: "#fff", padding: 30, borderRadius: 14, width: 600, maxHeight: "85vh",
            overflowY: "auto", textAlign: "center"
          }}>
            <h2 style={{ color: "#1976d2", marginBottom: 16 }}>Recommended POIs for You</h2>
            
            <div style={{ display: "grid", gridTemplateColumns: "1fr 1fr", gap: 12, marginBottom: 20 }}>
              {recommendedPois.map((poi) => (
                <div key={poi.id} style={{ border: "1px solid #eee", borderRadius: 8, padding: 10, textAlign: "left" }}>
                  <strong>{poi.name}</strong>
                  <p style={{ fontSize: 12, margin: 2 }}>{poi.description}</p>
                  <p style={{ fontSize: 11, color: "#888" }}>Tags: {poi.tags}</p>
                </div>
              ))}
            </div>

            <div style={{ marginBottom: 16 }}>
              <label>
                Transport Mode:{" "}
                <select value={mode} onChange={(e) => setMode(e.target.value)}>
                  <option value="foot">On Foot</option>
                  <option value="bike">Bike</option>
                  <option value="motor">Motor</option>
                  <option value="car">Car</option>
                </select>
              </label>
            </div>

            <div style={{ display: "flex", justifyContent: "space-between" }}>
              <button onClick={planRoute} style={{
                padding: "10px 24px", background: "#4caf50", color: "#fff",
                borderRadius: 8, fontSize: 16, flex: 1, marginRight: 10
              }}>Confirm</button>

              <button onClick={() => setShowPopup(false)} style={{
                padding: "10px 24px", background: "#f44336", color: "#fff",
                borderRadius: 8, fontSize: 16, flex: 1
              }}>Cancel</button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}
